<script>
document.addEventListener('DOMContentLoaded', () => {

    // --- DOM Elements ---
    const sliderC1 = document.getElementById('slider-c1');
    const c1ValueDisplay = document.getElementById('c1-value-display');
    const resetC1 = document.getElementById('reset-c1');
    const sliderC2 = document.getElementById('slider-c2');
    const c2ValueDisplay = document.getElementById('c2-value-display');
    const resetC2 = document.getElementById('reset-c2');
    const checkboxClipping = document.getElementById('checkbox-clipping');
    const levelControlGroup = document.getElementById('level-control-group');
    const sliderLevel = document.getElementById('slider-level');
    const levelValueDisplay = document.getElementById('level-value-display');
    const btnLinear = document.getElementById('btn-linear');
    const btnDecibel = document.getElementById('btn-decibel');

    // --- Simulation Constants & State ---
    const f = 1000;  // Hz
    const A = 1;
    const N = 2048; // Must be a power of 2 for this FFT
    const fs = 32000; // Hz

    let lastSpectrumMag = [];
    let currentScale = 'linear';

    // --- FFT Implementation (Radix-2 Cooley-Tukey) ---
    function fft(real, imag) {
        const n = real.length;
        if (n === 0) return;

        // Bit-reversal permutation
        for (let i = 1, j = 0; i < n; i++) {
            let bit = n >> 1;
            for (; (j & bit) !== 0; bit >>= 1) {
                j ^= bit;
            }
            j ^= bit;
            if (i < j) {
                [real[i], real[j]] = [real[j], real[i]];
                [imag[i], imag[j]] = [imag[j], imag[i]];
            }
        }

        // Cooley-Tukey FFT
        for (let len = 2; len <= n; len <<= 1) {
            const halfLen = len >> 1;
            const angle = -2 * Math.PI / len;
            const w_real = Math.cos(angle);
            const w_imag = Math.sin(angle);
            for (let i = 0; i < n; i += len) {
                let t_real = 1;
                let t_imag = 0;
                for (let j = 0; j < halfLen; j++) {
                    const u_real = real[i + j];
                    const u_imag = imag[i + j];
                    const v_real = real[i + j + halfLen] * t_real - imag[i + j + halfLen] * t_imag;
                    const v_imag = real[i + j + halfLen] * t_imag + imag[i + j + halfLen] * t_real;
                    real[i + j] = u_real + v_real;
                    imag[i + j] = u_imag + v_imag;
                    real[i + j + halfLen] = u_real - v_real;
                    imag[i + j + halfLen] = u_imag - v_imag;
                    [t_real, t_imag] = [t_real * w_real - t_imag * w_imag, t_real * w_imag + t_imag * w_real];
                }
            }
        }
    }

    // --- Plotly Layouts ---
    const plotMargin = { l: 40, r: 20, b: 40, t: 40, pad: 4 };
    const signalLayout = {
        title: 'signal',
        xaxis: { title: 'time[s]', range: [0, 0.005], autorange: false },
        yaxis: { title: '', range: [-2, 2], autorange: false },
        margin: plotMargin
    };
    const spectrumLayout = {
        title: 'magnitude spectrum (DFT)',
        xaxis: { title: 'frequency[Hz]', range: [0, 8000], autorange: false },
        yaxis: { title: '', autorange: true }, // Range will be set dynamically
        margin: plotMargin
    };

    // --- Core Functions ---
    function updateSimulation() {
        // 1. Read control values
        const c1 = parseFloat(sliderC1.value);
        const c2 = parseFloat(sliderC2.value);
        const clippingEnabled = checkboxClipping.checked;
        const level = parseFloat(sliderLevel.value);

        // 2. Generate time vector and input signal
        const t = Array.from({ length: N }, (_, i) => i / fs);
        const x = t.map(ti => A * Math.sin(2 * Math.PI * f * ti));

        // 3. Calculate output signal y(t)
        let y = x.map(xi => xi + c1 * Math.pow(xi, 2) + c2 * Math.pow(xi, 3));

        // 4. Apply clipping
        if (clippingEnabled) {
            y = y.map(yi => Math.max(-level, Math.min(level, yi)));
        }

        // 5. Update Signal Plot
        Plotly.react('plot-signal', [{ x: t, y: y, type: 'scatter', mode: 'lines' }], signalLayout);

        // 6. Perform DFT
        const fftReal = [...y];
        const fftImag = new Array(N).fill(0);
        fft(fftReal, fftImag);

        const mag = fftReal.map((re, i) => Math.sqrt(re * re + fftImag[i] * fftImag[i]));

        // 7. Normalize spectrum (single-sided)
        const n_half = N / 2;
        const normalizedMag = new Array(n_half).fill(0);
        normalizedMag[0] = mag[0] / N;
        for (let k = 1; k < n_half; k++) {
            normalizedMag[k] = 2 * mag[k] / N;
        }

        // 8. Store spectrum data and update plot
        lastSpectrumMag = normalizedMag;
        updateSpectrumScale();

        // 9. Update value displays
        c1ValueDisplay.textContent = c1.toFixed(1);
        c2ValueDisplay.textContent = c2.toFixed(1);
        levelValueDisplay.textContent = level.toFixed(2);
    }

    function updateSpectrumScale() {
        if (lastSpectrumMag.length === 0) return;

        const freqs = Array.from({ length: N / 2 }, (_, i) => i * fs / N);
        let yData;
        let yAxisLayout;

        if (currentScale === 'linear') {
            yData = lastSpectrumMag;
            yAxisLayout = { title: '', range: [0, 1.1], autorange: false };
        } else { // decibel
            const epsilon = 1e-9; // To avoid log(0)
            yData = lastSpectrumMag.map(v => 20 * Math.log10(v + epsilon));
            yAxisLayout = { title: '', range: [-100, 0], autorange: false };
        }

        const updatedSpectrumLayout = { ...spectrumLayout, yaxis: yAxisLayout };

        Plotly.react('plot-spectrum', [{
            x: freqs,
            y: yData,
            type: 'bar',
            width: 5 // Emulate stem plot
        }], updatedSpectrumLayout);
    }

    // --- Event Listeners ---
    sliderC1.addEventListener('input', updateSimulation);
    sliderC2.addEventListener('input', updateSimulation);
    sliderLevel.addEventListener('input', updateSimulation);

    resetC1.addEventListener('click', () => {
        sliderC1.value = 0;
        updateSimulation();
    });

    resetC2.addEventListener('click', () => {
        sliderC2.value = 0;
        updateSimulation();
    });

    checkboxClipping.addEventListener('change', () => {
        levelControlGroup.style.display = checkboxClipping.checked ? 'flex' : 'none';
        updateSimulation();
    });

    btnLinear.addEventListener('click', () => {
        if (currentScale !== 'linear') {
            currentScale = 'linear';
            btnLinear.classList.add('active');
            btnDecibel.classList.remove('active');
            updateSpectrumScale();
        }
    });

    btnDecibel.addEventListener('click', () => {
        if (currentScale !== 'decibel') {
            currentScale = 'decibel';
            btnDecibel.classList.add('active');
            btnLinear.classList.remove('active');
            updateSpectrumScale();
        }
    });

    // --- Initial Setup ---
    Plotly.newPlot('plot-signal', [], signalLayout, {responsive: true});
    Plotly.newPlot('plot-spectrum', [], spectrumLayout, {responsive: true});
    updateSimulation(); // Initial call to draw plots
});
</script>

</body>
