    <script>
        let controls = {};

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const w = canvasContainer.offsetWidth;
            const h = canvasContainer.offsetHeight;
            const canvas = createCanvas(w, h);
            canvas.parent('p5-canvas');

            angleMode(DEGREES);

            // Sliders
            controls.rayCount = select('#slider-ray-count');
            controls.beamWidth = select('#slider-beam-width');
            controls.incidentAngle = select('#slider-incident-angle');
            controls.refractionRatio = select('#slider-refraction-ratio');

            // Slider value labels
            controls.labelRayCount = select('#label-ray-count');
            controls.labelBeamWidth = select('#label-beam-width');
            controls.labelIncidentAngle = select('#label-incident-angle');
            controls.labelRefractionRatio = select('#label-refraction-ratio');

            // Checkboxes
            controls.showInjected = select('#checkbox-injected');
            controls.showRefracted = select('#checkbox-refracted');
            controls.showReflected = select('#checkbox-reflected');
            controls.showEjected = select('#checkbox-ejected');
            controls.showNormals = select('#checkbox-normals');
            controls.showIntersections = select('#checkbox-intersections');
            controls.showBeamTop = select('#checkbox-beam-top');
            controls.showBeamBottom = select('#checkbox-beam-bottom');

            // Attach listeners
            for (const key in controls) {
                if (controls[key] && typeof controls[key].input === 'function') {
                    controls[key].input(updateValuesAndRedraw);
                }
            }

            // Initial draw
            updateValuesAndRedraw();
            noLoop();
        }

        function windowResized() {
            const canvasContainer = document.getElementById('canvas-container');
            const w = canvasContainer.offsetWidth;
            const h = canvasContainer.offsetHeight;
            resizeCanvas(w, h);
            redraw();
        }

        function updateValuesAndRedraw() {
            controls.labelRayCount.html(controls.rayCount.value());
            controls.labelBeamWidth.html(parseFloat(controls.beamWidth.value()).toFixed(2));
            controls.labelIncidentAngle.html(`${controls.incidentAngle.value()}°`);
            controls.labelRefractionRatio.html(parseFloat(controls.refractionRatio.value()).toFixed(2));
            redraw();
        }

        function draw() {
            const rayCount = controls.rayCount.value();
            const beamWidth = controls.beamWidth.value();
            const incidentAngle = controls.incidentAngle.value();
            const n2 = controls.refractionRatio.value();
            const n1 = 1.0;

            const doShowInjected = controls.showInjected.checked();
            const doShowRefracted = controls.showRefracted.checked();
            const doShowReflected = controls.showReflected.checked();
            const doShowEjected = controls.showEjected.checked();
            const doShowNormals = controls.showNormals.checked();
            const doShowIntersections = controls.showIntersections.checked();
            const doShowBeamTop = controls.showBeamTop.checked();
            const doShowBeamBottom = controls.showBeamBottom.checked();

            // === Coordinate System Setup ===
            background(255);
            translate(width / 2, height / 2);

            const worldExtent = 2.5;
            const scaleFactor = min(width, height) / (worldExtent * 2);
            scale(scaleFactor, -scaleFactor); // Flip Y-axis for standard Cartesian
            strokeWeight(1 / scaleFactor); // Keep stroke weight constant

            // === Draw Static Elements ===
            drawAxes(worldExtent);

            // Draw sphere
            noFill();
            stroke(0);
            strokeWeight(2 / scaleFactor);
            ellipse(0, 0, 2, 2); // Unit sphere, diameter 2
            strokeWeight(1 / scaleFactor);


            // === Ray Tracing ===
            const initialDir = p5.Vector.fromAngle(radians(incidentAngle));
            const perpendicularDir = createVector(initialDir.y, -initialDir.x);

            for (let i = 0; i < rayCount; i++) {

                const isTopHalf = (rayCount > 1) ? (i < rayCount / 2) : true;
                if (isTopHalf && !doShowBeamTop) continue;
                if (!isTopHalf && !doShowBeamBottom) continue;

                const offset = (rayCount > 1) ?
                    map(i, 0, rayCount - 1, -beamWidth / 2, beamWidth / 2) : 0;

                const startPos = perpendicularDir.copy().mult(offset);
                const rayStart = startPos.copy().sub(initialDir.copy().mult(worldExtent));

                // 1. First Intersection (Injected)
                const p1 = rayCircleIntersect(rayStart, initialDir, 1.0);
                if (!p1) continue;

                if (doShowInjected) {
                    stroke(255, 0, 0); // Red
                    line(rayStart.x, rayStart.y, p1.x, p1.y);
                }
                if (doShowIntersections) {
                    fill(255, 0, 0); noStroke();
                    ellipse(p1.x, p1.y, 0.05, 0.05);
                }

                // 2. First Refraction
                let normal1 = p1.copy().normalize();
                let dir2 = refract(initialDir, normal1, n1 / n2);
                if (!dir2) continue; // Should not happen on entry

                if (doShowNormals) drawNormal(p1, normal1);

                // 3. Second Intersection (Refracted)
                const p2 = rayCircleIntersect(p1, dir2, 1.0, true);
                if (!p2) continue;

                if (doShowRefracted) {
                    stroke(0, 255, 0); // Green
                    line(p1.x, p1.y, p2.x, p2.y);
                }
                if (doShowIntersections) {
                    fill(0, 255, 0); noStroke();
                    ellipse(p2.x, p2.y, 0.05, 0.05);
                }

                // 4. Internal Reflection
                let normal2 = p2.copy().normalize();
                let dir3 = dir2.copy().reflect(normal2);

                if (doShowNormals) drawNormal(p2, normal2);

                // 5. Third Intersection (Reflected)
                const p3 = rayCircleIntersect(p2, dir3, 1.0, true);
                if (!p3) continue;

                if (doShowReflected) {
                    stroke(0, 0, 255); // Blue
                    line(p2.x, p2.y, p3.x, p3.y);
                }
                if (doShowIntersections) {
                    fill(0, 0, 255); noStroke();
                    ellipse(p3.x, p3.y, 0.05, 0.05);
                }

                // 6. Second Refraction (Ejected)
                let normal3 = p3.copy().normalize();
                let dir4 = refract(dir3, normal3, n2 / n1);

                if (doShowNormals) drawNormal(p3, normal3);

                if (dir4) { // Check for Total Internal Reflection
                    if(doShowEjected){
                        stroke(0); // Black
                        const endPoint = p3.copy().add(dir4.copy().mult(worldExtent * 2));
                        line(p3.x, p3.y, endPoint.x, endPoint.y);
                    }
                }
            }
        }

        function drawAxes(extent) {
            stroke(180);
            strokeWeight(1 / (min(width, height) / (extent * 2)));
            line(-extent, 0, extent, 0); // X-axis
            line(0, -extent, 0, extent); // Y-axis

            fill(120);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(12 / (min(width, height) / (extent * 2)));

            for (let i = -floor(extent) + 1; i < extent; i++) {
                if (i === 0) continue;
                // X-axis ticks and labels
                stroke(180);
                line(i, -0.05, i, 0.05);
                noStroke();
                push();
                scale(1, -1); // Flip text back
                text(i, i, 0.15);
                pop();

                // Y-axis ticks and labels
                stroke(180);
                line(-0.05, i, 0.05, i);
                noStroke();
                push();
                scale(1, -1); // Flip text back
                text(i, -0.15, -i);
                pop();
            }
        }

        function drawNormal(point, normal) {
            stroke(0, 0, 255, 150);
            push();
            drawingContext.setLineDash([5 / (min(width, height) / 5), 3 / (min(width, height) / 5)]);
            const end = p5.Vector.add(point, normal.copy().mult(0.25));
            line(point.x, point.y, end.x, end.y);
            pop();
            drawingContext.setLineDash([]);
        }

        // Ray-circle intersection. Returns intersection point or null.
        function rayCircleIntersect(origin, dir, r, inside = false) {
            const oc = origin.copy(); // vector from circle center to ray origin
            const a = dir.dot(dir); // should be 1 if dir is normalized
            const b = 2 * oc.dot(dir);
            const c = oc.dot(oc) - r * r;
            const discriminant = b * b - 4 * a * c;

            if (discriminant < 0) {
                return null;
            }

            const sqrtD = sqrt(discriminant);
            const t1 = (-b - sqrtD) / (2 * a);
            const t2 = (-b + sqrtD) / (2 * a);

            let t;
            if (inside) {
                // We're inside, so we need the first positive intersection time
                t = (t1 > 1e-6) ? t1 : t2;
                 if (t <= 1e-6) return null;
            } else {
                // We're outside, looking for the first entry point
                t = (t1 > 1e-6 && t2 > 1e-6) ? min(t1, t2) : max(t1, t2);
                if (t <= 1e-6) return null;
            }

            return origin.copy().add(dir.copy().mult(t));
        }

        // Snell's Law. Returns refracted vector or null for Total Internal Reflection.
        function refract(incident, normal, eta) { // eta = n1/n2
            const I = incident.copy().normalize();
            const N = normal.copy().normalize();
            const dotNI = I.dot(N);
            const k = 1.0 - eta * eta * (1.0 - dotNI * dotNI);

            if (k < 0) {
                return null; // Total Internal Reflection
            }

            const term1 = I.copy().mult(eta);
            const term2 = N.copy().mult(eta * dotNI + sqrt(k));
            const refracted = term1.sub(term2);
            return refracted.normalize();
        }

    </script>
