<script>
document.addEventListener('DOMContentLoaded', () => {

    const sketch = (p) => {
        let graphSize = 18;
        let maxDegree = 3;
        let currentStep = 1;

        let adj, U, V; // Graph structure
        let history = []; // Stores states of the algorithm

        // Colors and styles
        const colors = {
            vertexCover: '#00C000',
            bfsRoot: '#FF0000',
            defaultVertex: '#333333',
            defaultEdge: '#808080',
            matchingEdge: '#A0A0FF',
            pathEdge: '#FF0000',
