            document.getElementById(`degree-${maxDegree}`).classList.add('active');

            // Setup event listeners
            setupControls();

            // Initial generation
            resetAlgorithm();
        };

        p.windowResized = () => {
            const canvasWrapper = document.getElementById('canvas-wrapper');
            p.resizeCanvas(canvasWrapper.clientWidth, canvasWrapper.clientHeight);
            drawCurrentState();
        };

        function setupControls() {
            document.getElementById('size-controls').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    graphSize = parseInt(e.target.id.split('-')[1]);
                    updateActiveButton('size-controls', e.target.id);
                    resetAlgorithm();
                }
            });

            document.getElementById('degree-controls').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    maxDegree = parseInt(e.target.id.split('-')[1]);
                    updateActiveButton('degree-controls', e.target.id);
                    resetAlgorithm();
                }
            });

            document.getElementById('reset-button').addEventListener('click', resetAlgorithm);
        }

        function updateActiveButton(groupId, activeId) {
            const group = document.getElementById(groupId);
            group.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(activeId).classList.add('active');
        }

        function resetAlgorithm() {
            p.randomSeed(Date.now());
            generateGraph();
            computeAlgorithmHistory();
            updateStepControls();
            setStep(1);
        }

        function generateGraph() {
            const numPerSide = graphSize / 2;
            U = Array.from({ length: numPerSide }, (_, i) => i + 1);
            V = Array.from({ length: numPerSide }, (_, i) => i + 1 + numPerSide);
            adj = new Map();
            [...U, ...V].forEach(v => adj.set(v, []));

            V.forEach(v_node => {
                const degree = Math.floor(p.random(1, maxDegree + 1));
                let neighbors = [];
                while(neighbors.length < degree) {
                    const u_node = U[Math.floor(p.random(U.length))];
                    if (!neighbors.includes(u_node)) {
                        neighbors.push(u_node);
                    }
                }
                neighbors.forEach(u_node => {
                    adj.get(u_node).push(v_node);
                    adj.get(v_node).push(u_node);
                });
            });
        }

        function recordState(matching, bfsRoot, bfsTree, highlightedPath, message) {
            const Z = findAlternatingPathReachableSet(U, adj, matching);
            const cover = new Set([...U.filter(u => !Z.has(u)), ...V.filter(v => Z.has(v))]);
            let statusText = message || `${matching.length} edges in matching (blue); ${cover.size} vertices in cover (green)`;

            history.push({
                matching: [...matching],
                cover: cover,
                bfsRoot: bfsRoot,
                bfsTree: JSON.parse(JSON.stringify(bfsTree)),
                highlightedPath: highlightedPath ? [...highlightedPath] : [],
                statusText: statusText
            });
        }

        function computeAlgorithmHistory() {
            history = [];
            let matching = [];

            recordState(matching, null, {}, [], "Initial state: 0 edges in matching");

            const unmatchedU = new Set(U);

            while(true) {
                const startNode = unmatchedU.values().next().value;
                if (!startNode) break;

                // --- BFS to find augmenting path ---
                const queue = [[startNode]];
                const visited = new Set([startNode]);
                const parent = new Map();
                let pathFound = null;

                const bfsTree = { nodes: { [startNode]: { children: [] } }, edges: [] };
                recordState(matching, startNode, bfsTree, [], `Searching for augmenting path from vertex ${startNode}`);

                bfs_loop:
                while(queue.length > 0) {
                    const path = queue.shift();
                    const u = path[path.length - 1];

                    // u is in U (or startNode), find unmatched edge to v
                    if (U.includes(u)) {
                        for (const v of adj.get(u)) {
                            const isMatchedEdge = matching.some(m => (m[0] === u && m[1] === v) || (m[0] === v && m[1] === u));
                            if (!isMatchedEdge && !visited.has(v)) {
                                visited.add(v);
                                parent.set(v, u);
                                const newPath = [...path, v];
                                queue.push(newPath);

                                // Update tree and record state
                                bfsTree.nodes[v] = { children: [] };
                                bfsTree.nodes[u].children.push(v);
                                bfsTree.edges.push([u,v]);
                                recordState(matching, startNode, bfsTree, [], `BFS: Exploring from ${u} to ${v}`);

                                const isVMatched = matching.some(m => m[1] === v);
                                if (!isVMatched) {
                                    pathFound = newPath;
                                    break bfs_loop;
                                }
                            }
                        }
                    } 
                    // u is in V, find matched edge to u'
                    else {
                        const matchedEdge = matching.find(m => m[1] === u);
                        if (matchedEdge) {
                            const u_prime = matchedEdge[0];
                            if (!visited.has(u_prime)) {
                                visited.add(u_prime);
                                parent.set(u_prime, u);
                                const newPath = [...path, u_prime];
                                queue.push(newPath);

                                // Update tree and record state
                                bfsTree.nodes[u_prime] = { children: [] };
                                bfsTree.nodes[u].children.push(u_prime);
                                bfsTree.edges.push([u,u_prime]);
                                recordState(matching, startNode, bfsTree, [], `BFS: Traversing matched edge to ${u_prime}`);
                            }
                        }
                    }
                }

                if (pathFound) {
                    recordState(matching, startNode, bfsTree, pathFound, `Augmenting path found!`);
                    // Augment the matching
                    for (let i = 0; i < pathFound.length - 1; i += 2) {
                        const u_node = pathFound[i];
                        const v_node = pathFound[i+1];
                        const edgeInMatchingIdx = matching.findIndex(m => m[0] === pathFound[i+2] && m[1] === v_node);
                        if(edgeInMatchingIdx > -1) {
                            matching.splice(edgeInMatchingIdx, 1);
                        }
                        matching.push([u_node, v_node]);
                    }
                    unmatchedU.delete(startNode);
                    unmatchedU.delete(pathFound[pathFound.length-2]);
                    recordState(matching, null, {}, [], `Matching augmented. New size: ${matching.length}`);
                } else {
                    unmatchedU.delete(startNode); // No augmenting path from this start node
                }
            }
            const Z = findAlternatingPathReachableSet(U, adj, matching);
            const cover = new Set([...U.filter(u => !Z.has(u)), ...V.filter(v => Z.has(v))]);
            history.push({
                matching: [...matching],
                cover: cover,
                bfsRoot: null,
                bfsTree: {},
                highlightedPath: [],
                statusText: `Final state: ${matching.length} edges in matching (blue); ${cover.size} vertices in cover (green)`
            });
        }

        function findAlternatingPathReachableSet(U_nodes, adj_list, current_matching) {
            const unmatched_U = new Set(U_nodes.filter(u => !current_matching.some(m => m[0] === u)));
            const reachable = new Set();
            const queue = [...unmatched_U];
            queue.forEach(u => reachable.add(u));

            while (queue.length > 0) {
                const u = queue.shift();
                // From U to V via non-matching edge
                if (U_nodes.includes(u)) {
                    for (const v of adj_list.get(u)) {
                        if (!reachable.has(v) && !current_matching.some(m => m[0] === u && m[1] === v)) {
                            reachable.add(v);
                            queue.push(v);
                        }
                    }
                }
                // From V to U via matching edge
                else {
                    const matchedEdge = current_matching.find(m => m[1] === u);
                    if (matchedEdge) {
                        const u_prime = matchedEdge[0];
                        if (!reachable.has(u_prime)) {
                            reachable.add(u_prime);
                            queue.push(u_prime);
                        }
                    }
                }
            }
            return reachable;
        }

        function updateStepControls() {
            const container = document.getElementById('step-controls');
            container.innerHTML = '';
            history.forEach((_, i) => {
                const btn = document.createElement('button');
                btn.id = `step-${i + 1}`;
                btn.textContent = i + 1;
                btn.addEventListener('click', () => setStep(i + 1));
                container.appendChild(btn);
            });
        }

        function setStep(step) {
            currentStep = step;
            updateActiveButton('step-controls', `step-${currentStep}`);
            drawCurrentState();
        }

        function drawCurrentState() {
            if (history.length === 0) return;
            const state = history[currentStep - 1];
            document.getElementById('status-text').textContent = state.statusText;
            p.redraw();
        }

        p.draw = () => {
            p.background(255);
            if (history.length === 0) return;

            const state = history[currentStep - 1];

            // --- Calculate positions ---
            const vertexRadius = p.min(p.width * 0.015, 12);
            const graphArea = { x: p.width * 0.35, y: 0, w: p.width * 0.65, h: p.height };
            const treeArea = { x: 0, y: 0, w: p.width * 0.35, h: p.height };

            const u_y = graphArea.y + graphArea.h * 0.2;
            const v_y = graphArea.y + graphArea.h * 0.8;
            const vertexPositions = new Map();
            U.forEach((u, i) => vertexPositions.set(u, p.createVector(graphArea.x + graphArea.w * (i + 1) / (U.length + 1), u_y)));
            V.forEach((v, i) => vertexPositions.set(v, p.createVector(graphArea.x + graphArea.w * (i + 1) / (V.length + 1), v_y)));

            // --- Draw Bipartite Graph ---
            // Draw all edges first (default)
            p.stroke(colors.defaultEdge);
            p.strokeWeight(1);
            adj.forEach((neighbors, u_node) => {
                if(U.includes(u_node)) {
                    neighbors.forEach(v_node => {
                        const pos1 = vertexPositions.get(u_node);
                        const pos2 = vertexPositions.get(v_node);
                        p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                    });
                }
            });

            // Draw matching edges
            p.stroke(colors.matchingEdge);
            p.strokeWeight(4);
            state.matching.forEach(([u_node, v_node]) => {
                const pos1 = vertexPositions.get(u_node);
                const pos2 = vertexPositions.get(v_node);
                p.line(pos1.x, pos1.y, pos2.x, pos2.y);
            });

            // Draw highlighted path
            if (state.highlightedPath.length > 0) {
                p.stroke(colors.pathEdge);
                p.strokeWeight(2.5);
                for (let i = 0; i < state.highlightedPath.length - 1; i++) {
                    const pos1 = vertexPositions.get(state.highlightedPath[i]);
                    const pos2 = vertexPositions.get(state.highlightedPath[i + 1]);
                    p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                }
            }

            // Draw vertices and labels
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            p.noStroke();
            vertexPositions.forEach((pos, v_id) => {
                if (state.cover.has(v_id)) p.fill(colors.vertexCover);
                else if (state.bfsRoot === v_id) p.fill(colors.bfsRoot);
                else p.fill(colors.defaultVertex);
                p.ellipse(pos.x, pos.y, vertexRadius * 2, vertexRadius * 2);

                p.fill(colors.defaultVertex);
                p.text(v_id, pos.x, U.includes(v_id) ? pos.y - vertexRadius * 2 : pos.y + vertexRadius * 2);
            });

            // --- Draw BFS Tree ---
            if (state.bfsTree && state.bfsTree.nodes) {
                const nodeSize = 25;
                const levelHeight = 60;

                const treePositions = new Map();
                const levels = {};

                // Simple recursive function to calculate positions
                function calculateTreePositions(nodeId, depth, siblingIndex, numSiblings) {
                    if(!levels[depth]) levels[depth] = [];
                    levels[depth].push(nodeId);

                    const x = treeArea.x + treeArea.w * 0.5;
                    const y = treeArea.y + 50 + depth * levelHeight;
                    const pos = p.createVector(x, y);
                    treePositions.set(nodeId, pos);

                    if (state.bfsTree.nodes[nodeId]) {
                        const children = state.bfsTree.nodes[nodeId].children || [];
                        children.forEach((childId, i) => {
                            calculateTreePositions(childId, depth + 1, i, children.length);
                        });
                    }
                }

                if (state.bfsRoot) {
                    calculateTreePositions(state.bfsRoot, 0, 0, 1);
                    // Adjust X positions based on sibling order
                    Object.values(levels).forEach(levelNodes => {
                        levelNodes.forEach((nodeId, i) => {
                            const pos = treePositions.get(nodeId);
                            pos.x = treeArea.x + treeArea.w * (i + 1) / (levelNodes.length + 1);
                        });
                    });

                    // Draw tree edges
                    p.stroke(colors.treeEdge);
                    p.strokeWeight(1);
                    state.bfsTree.edges.forEach(([u_id, v_id]) => {
                        const pos1 = treePositions.get(u_id);
                        const pos2 = treePositions.get(v_id);
                        if (pos1 && pos2) {
                            p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                        }
                    });

                    // Draw tree nodes and labels
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(12);
                    treePositions.forEach((pos, nodeId) => {
                        p.stroke(0);
                        p.strokeWeight(1);
                        p.fill(colors.treeNode);
                        p.rect(pos.x - nodeSize/2, pos.y - nodeSize/2, nodeSize, nodeSize);
                        p.noStroke();
                        p.fill(colors.treeNodeText);
                        p.text(nodeId, pos.x, pos.y);
                        // Also draw label above for context
                        p.fill(150);
                        p.text(U.includes(nodeId) ? 'U' : 'V', pos.x, pos.y - nodeSize);
                    });
                }
            }
        };
    };

    new p5(sketch);
});
</script>

</body>
</html>
