    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element References ---
            const plotContainer = document.getElementById('plot-container');
            const sliderElements = document.getElementById('slider-elements');
            const displayElements = document.getElementById('display-elements');

            const controls = {
                scaleLinear: document.getElementById('radio-scale-linear'),
                scaleLog: document.getElementById('radio-scale-logarithmic'),
                rangeFixed: document.getElementById('radio-range-fixed'),
                rangeAuto: document.getElementById('radio-range-automatic'),
                checkBubble: document.getElementById('check-bubble'),
                checkSelection: document.getElementById('check-selection'),
                checkInsertion: document.getElementById('check-insertion'),
                checkMerge: document.getElementById('check-merge'),
                checkQuick: document.getElementById('check-quick'),
                checkHeap: document.getElementById('check-heap'),
                checkBogo: document.getElementById('check-bogo')
            };

            // --- Helper Functions ---
            const factorial = (n) => {
                if (n < 0) return NaN;
                if (n === 0 || n === 1) return 1;
                let result = 1;
                for (let i = 2; i <= n; i++) {
                    result *= i;
                }
                return result;
            };

            const log2 = Math.log2;

            // --- Data Generation ---
            const getTraces = (n, visibility) => {
                const x = Array.from({ length: n }, (_, i) => i + 1);

                return [
                    { // bubble
                        x: x,
                        y: x.map(val => val * val),
                        name: 'bubble',
                        mode: 'lines',
                        type: 'scatter',
                        visible: visibility.bubble,
                        line: { color: 'green', width: 5 }
                    },
                    { // selection
                        x: x,
                        y: x.map(val => 0.25 * val * val),
                        name: 'selection',
                        mode: 'lines',
                        type: 'scatter',
                        visible: visibility.selection,
                        line: { color: 'orange', width: 3, dash: 'dot' }
                    },
                    { // insertion/bogo
                        x: x,
                        y: x.map(val => val * factorial(val)),
                        name: 'insertion',
                        mode: 'lines',
                        type: 'scatter',
                        visible: visibility.insertion, // This visibility is special
                        line: { color: 'red', width: 2 }
                    },
                    { // merge
                        x: x,
                        y: x.map(val => val > 1 ? val * log2(val) : 0),
                        name: 'merge',
                        mode: 'lines',
                        type: 'scatter',
                        visible: visibility.merge,
                        line: { color: 'blue', width: 2 }
                    },
                    { // quick
                        x: x,
                        y: x.map(val => val > 1 ? 1.2 * val * log2(val) : 0),
                        name: 'quick',
                        mode: 'lines',
                        type: 'scatter',
                        visible: visibility.quick,
                        line: { color: 'purple', width: 2 }
                    },
                    { // heap
                        x: x,
                        y: x.map(val => val > 1 ? 1.5 * val * log2(val) : 0),
                        name: 'heap',
                        mode: 'lines',
                        type: 'scatter',
                        visible: visibility.heap,
                        line: { color: 'cyan', width: 2 }
                    },
                    { // bogo (data is the same as insertion, but it's just for control)
                        // This is a dummy trace, its visibility is handled by the 'insertion' trace
                    }
                ];
            };

            // --- Plot Update Logic ---
            const updatePlot = () => {
                const numElements = parseInt(sliderElements.value, 10);
                const scaleType = controls.scaleLinear.checked ? 'linear' : 'log';
                const rangeType = controls.rangeFixed.checked ? 'fixed' : 'automatic';

                // Update slider display
                displayElements.textContent = `+ ${numElements}`;

                // Get visibility states from checkboxes
                const visibility = {
                    bubble: controls.checkBubble.checked,
                    selection: controls.checkSelection.checked,
                    // Special case: 'insertion' trace is visible if 'insertion' OR 'bogo' is checked
                    insertion: controls.checkInsertion.checked || controls.checkBogo.checked,
                    merge: controls.checkMerge.checked,
                    quick: controls.checkQuick.checked,
                    heap: controls.checkHeap.checked
                };

                // Generate trace data
                const traces = getTraces(numElements, visibility);

                // Define layout
                const layout = {
                    title: 'time complexity',
                    xaxis: {
                        title: 'number of elements',
                        autorange: rangeType === 'automatic',
                        range: rangeType === 'fixed' ? [0, 20] : null
