        #main-container {
            width: 550px;
            padding: 20px;
            background-color: #f0f0f0;
            border: 1px solid #ccc;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0,0,0,0.1);
        }
        #control-panel {
            display: flex;
            align-items: center;
            gap: 15px;
            margin-bottom: 15px;
        }
        label[for="slider-xD"] {
            flex-shrink: 0;
        }
        #slider-xD {
            flex-grow: 1;
            width: 100%;
        }
        #label-xD {
            font-family: 'Courier New', Courier, monospace;
            font-weight: bold;
            min-width: 40px;
            text-align: right;
        }
        #plot {
            border: 1px solid #ccc;
            background-color: white;
        }
    </style>
</head>
<body>

<div id="main-container">
    <div id="control-panel">
        <label for="slider-xD">distillate composition</label>
        <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
        <span id="label-xD">0.84</span>
    </div>
    <div id="plot"></div>
</div>

<script>
    document.addEventListener('DOMContentLoaded', function() {
        const slider = document.getElementById('slider-xD');
        const label = document.getElementById('label-xD');
        const plotDiv = document.getElementById('plot');

        const updatePlot = (xD) => {
            // --- Constants and Functions ---
            const a = 9;
            const b = -0.6;

            // Vapor-Liquid Equilibrium (VLE) function
            const y_vle = (x) => (a * x) / (1 + (a - 1) * x + b * x * (1 - x));

            // Reflux Ratio function
            const r_reflux = (x, xD_val) => {
                const y = y_vle(x);
                // Avoid division by zero or near-zero
                if (Math.abs(y - x) < 1e-6) {
                    return Infinity;
                }
                return (xD_val - y) / (y - x);
            };

            // --- Data Generation for Curve ---
            const x_vals = [];
            const r_vals = [];
            const step = 0.001;
            // Iterate from a small non-zero value up to xD
            for (let x = step; x < xD; x += step) {
                const r = r_reflux(x, xD);
                // Add point if reflux ratio is positive and within a reasonable range
                if (isFinite(r) && r >= 0 && r < 5) {
                    x_vals.push(x);
                    r_vals.push(r);
                }
            }

            // --- Tangent Pinch Point Calculation ---
            const pinch_x = [];
            const pinch_y = [];
            // Search for a local maximum in the generated r values
            for (let i = 1; i < r_vals.length - 1; i++) {
                if (r_vals[i] > r_vals[i - 1] && r_vals[i] > r_vals[i + 1]) {
                    // Check if it's a significant peak, not just noise
                    if (r_vals[i] > 0.1) {
                        pinch_x.push(x_vals[i]);
                        pinch_y.push(r_vals[i]);
                        break; // Found the first significant maximum, stop searching
                    }
                }
            }

            // --- Plotly Data and Layout ---
            const data = [
                {
                    // Bifurcation Curve
                    x: x_vals,
                    y: r_vals,
                    mode: 'lines',
                    type: 'scatter',
                    line: {
                        color: '#8B0000',
                        width: 2.5
                    }
                },
                {
                    // Tangent Pinch Point
                    x: pinch_x,
                    y: pinch_y,
                    mode: 'markers',
                    type: 'scatter',
                    marker: {
                        color: 'royalblue',
                        size: 10
                    }
                }
            ];

            const layout = {
                xaxis: {
                    range: [0, 1.0],
                    dtick: 0.2,
                    gridcolor: '#e0e0e0',
                    zeroline: false
                },
                yaxis: {
                    range: [0, 3.0],
                    dtick: 0.5,
                    gridcolor: '#e0e0e0',
                    zeroline: false
                },
                showlegend: false,
                margin: { l: 40, r: 20, b: 40, t: 20 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            // --- Redraw Plot ---
            Plotly.react(plotDiv, data, layout, {responsive: true});
        };

        // --- Event Listener ---
        slider.addEventListener('input', (event) => {
            const xD_val = parseFloat(event.target.value);
            label.textContent = xD_val.toFixed(2);
            updatePlot(xD_val);
        });

        // --- Initial Plot ---
        const initial_xD = parseFloat(slider.value);
        label.textContent = initial_xD.toFixed(2);
        updatePlot(initial_xD);
    });
</script>

</body>
</html>
