    <script>
        const sketch = function(p) {
            // --- Global State Variables ---
            let sceneObjects = [];
            let selectedTool = null;
            let activeObject = null;
            let interactionMode = null; // 'translate' or 'rotate'
            let dragOffset = { x: 0, y: 0 };
            let initialRotation = { obj: 0, mouse: 0 };
            const ROTATE_RADIUS = 25; // pixels from center to trigger rotation

            // --- p5.js Setup ---
            p.setup = function() {
                const canvasContainer = document.getElementById('canvas-container');
                const w = canvasContainer.offsetWidth;
                const h = canvasContainer.offsetHeight;
                const canvas = p.createCanvas(w, h);
                canvas.parent('canvas-container');
                canvas.id('drawing-canvas');

                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(16);
                p.textFont('Arial');

                setupEventListeners();
            };

            // --- p5.js Draw Loop ---
            p.draw = function() {
                p.background(255);

                // Draw all objects on the canvas
                sceneObjects.forEach(obj => {
                    p.push();
                    p.translate(obj.x, obj.y);
                    p.rotate(obj.rotation);
                    drawObject(obj);
                    p.pop();
                });

                // Draw a preview of the selected tool at the mouse position
                if (selectedTool && isMouseOnCanvas()) {
                    p.push();
                    p.fill(0, 0, 0, 100); // Ghostly appearance
                    p.stroke(0, 0, 0, 100);
                    const previewObj = { ...selectedTool, x: p.mouseX, y: p.mouseY, rotation: 0 };
                    drawObject(previewObj);
                    p.pop();
                }
            };

            // --- Mouse Interaction Handlers ---
            p.mousePressed = function() {
                if (!isMouseOnCanvas()) return;

                // Case 1: A tool is selected from the toolbar -> place a new object
                if (selectedTool) {
                    sceneObjects.push({
                        ...selectedTool,
                        x: p.mouseX,
                        y: p.mouseY,
                        rotation: 0
                    });

                    // Deselect the tool after placing
                    const selectedBtn = document.querySelector('#toolbar button.selected');
                    if(selectedBtn) selectedBtn.classList.remove('selected');
                    selectedTool = null;
                    return;
                }

                // Case 2: No tool selected -> try to interact with existing objects
                for (let i = sceneObjects.length - 1; i >= 0; i--) {
                    const obj = sceneObjects[i];
                    const d = p.dist(p.mouseX, p.mouseY, obj.x, obj.y);

                    // Use a generous hit radius for structures, tighter for atoms
                    const hitRadius = (obj.type === 'atom' || obj.type === 'charge') ? 20 : 80;

                    if (d < hitRadius) {
                        activeObject = obj;

                        const distToCenter = p.dist(p.mouseX, p.mouseY, obj.x, obj.y);

                        // Check if we are close to the center to rotate
                        if (distToCenter < ROTATE_RADIUS) {
                            interactionMode = 'rotate';
                            initialRotation.obj = obj.rotation;
                            initialRotation.mouse = p.atan2(p.mouseY - obj.y, p.mouseX - obj.x);
                        } else {
                            interactionMode = 'translate';
                            dragOffset.x = obj.x - p.mouseX;
                            dragOffset.y = obj.y - p.mouseY;
                        }
                        break; // Stop after finding the topmost object
                    }
                }
            };

            p.mouseDragged = function() {
                if (!activeObject) return;

                if (interactionMode === 'translate') {
                    activeObject.x = p.mouseX + dragOffset.x;
                    activeObject.y = p.mouseY + dragOffset.y;
                } else if (interactionMode === 'rotate') {
                    let currentAngle = p.atan2(p.mouseY - activeObject.y, p.mouseX - activeObject.x);
                    activeObject.rotation = initialRotation.obj + (currentAngle - initialRotation.mouse);
                }
            };

            p.mouseReleased = function() {
                activeObject = null;
                interactionMode = null;
            };

            // --- Drawing Helper Functions ---
            function drawObject(obj) {
                p.fill(0);
                p.stroke(0);
                p.strokeWeight(1.5);
                p.textSize(16);

                if (obj.type === 'atom' || obj.type === 'charge') {
                    p.noStroke();
                    p.text(obj.value, 0, 0);
                } else if (obj.type === 'structure') {
                    switch (obj.value) {
                        case 'bond': drawBond(); break;
                        case 'water': drawWater(); break;
                        case 'cyclopropyl_ethane': drawCyclopropylEthane(); break;
                        case 'borazine': drawBorazine(); break;
                        case 'isopropylamine': drawIsopropylamine(); break;
                    }
                }
            }

            function drawBond() {
                p.line(-20, 0, 20, 0);
            }

            function drawWater() {
                p.noStroke();
                p.text('O', 0, 5);
                p.text('H', -25, -15);
                p.text('H', 25, -15);
                p.stroke(0);
                p.line(0, 5, -20, -10);
                p.line(0, 5, 20, -10);
            }

            function drawCyclopropylEthane() {
                // Centered around the join point
                const c1 = {x: 0, y: 0};
                const c2 = {x: -25, y: 43.3};
                const c3 = {x: 25, y: 43.3};
                p.line(c1.x, c1.y, c2.x, c2.y);
                p.line(c2.x, c2.y, c3.x, c3.y);
                p.line(c3.x, c3.y, c1.x, c1.y);

                const c4 = {x: -30, y: -20};
                const c5 = {x: -60, y: 0};
                p.line(c1.x, c1.y, c4.x, c4.y);
                p.line(c4.x, c4.y, c5.x, c5.y);
            }

            function drawBorazine() {
                const r = 35;
                let points = [];
                for(let i=0; i<6; i++){
                    let angle = p.PI / 3 * i - p.PI / 6;
                    points.push({x: r * p.cos(angle), y: r * p.sin(angle)});
                }

                // Draw hexagon
                for(let i=0; i<6; i++){
                    p.line(points[i].x, points[i].y, points[(i+1)%6].x, points[(i+1)%6].y);
                }

                // Draw atoms (B/N and H)
                p.noStroke();
                for(let i=0; i<6; i++){
                    const label = (i % 2 === 0) ? 'N' : 'B';
                    p.text(label, points[i].x, points[i].y);

                    // Draw attached H
                    const r_h = r + 20;
                    let angle = p.PI / 3 * i - p.PI / 6;
                    const h_pos = {x: r_h * p.cos(angle), y: r_h * p.sin(angle)};
                    p.text('H', h_pos.x, h_pos.y);
                    p.stroke(0);
                    p.line(points[i].x, points[i].y, h_pos.x, h_pos.y);
                    p.noStroke();
                }
            }

            function drawIsopropylamine() {
                // (CH3)2CH-NH2
                // Central carbon (implied) at origin
                const c_center = {x:0, y:0};
                const c_methyl1 = {x: -35, y: -20};
                const c_methyl2 = {x: -35, y: 20};
                const n_pos = {x: 30, y: 0};
                const h1_pos = {x: n_pos.x + 15, y: -15};
                const h2_pos = {x: n_pos.x + 15, y: 15};

                p.stroke(0);
                p.line(c_center.x, c_center.y, c_methyl1.x, c_methyl1.y);
                p.line(c_center.x, c_center.y, c_methyl2.x, c_methyl2.y);
                p.line(c_center.x, c_center.y, n_pos.x, n_pos.y);
                p.line(n_pos.x, n_pos.y, h1_pos.x, h1_pos.y);
                p.line(n_pos.x, n_pos.y, h2_pos.x, h2_pos.y);

                p.noStroke();
                p.text('N', n_pos.x, n_pos.y);
                p.text('H', h1_pos.x, h1_pos.y);
                p.text('H', h2_pos.x, h2_pos.y);
            }

            // --- Utility Functions ---
            function isMouseOnCanvas() {
                return p.mouseX > 0 && p.mouseX < p.width && p.mouseY > 0 && p.mouseY < p.height;
            }

            // --- DOM Event Listeners Setup ---
            function setupEventListeners() {
                // Toolbar buttons
                const toolButtons = document.querySelectorAll('#toolbar button');
                toolButtons.forEach(btn => {
                    btn.addEventListener('click', () => {
                        // Clear any active selection
                        if (activeObject) {
                            activeObject = null;
                            interactionMode = null;
                        }

                        // Remove 'selected' class from any other button
                        toolButtons.forEach(b => b.classList.remove('selected'));

                        // Set the new tool
                        btn.classList.add('selected');
                        selectedTool = {
                            type: btn.dataset.type,
                            value: btn.dataset.value
                        };
                    });
                });

                // Undo button
                document.getElementById('btn-undo').addEventListener('click', () => {
                    sceneObjects.pop();
                });

                // Add structure button
                document.getElementById('btn-add').addEventListener('click', () => {
                    const selector = document.getElementById('select-structure');
                    const structureValue = selector.value;
                    if (structureValue) {
                        sceneObjects.push({
                            type: 'structure',
                            value: structureValue,
                            x: p.width / 2,
                            y: p.height / 2,
                            rotation: 0
                        });
                    }
                });

                // Clicking on canvas should deselect toolbar button
                document.getElementById('drawing-canvas').addEventListener('mousedown', (e) => {
                    // Check if mouse click is on canvas but not on an existing object
                    // This logic is handled inside p.mousePressed to prevent double placement
                });

                // Resize canvas if window is resized
                p.windowResized = function() {
                    const canvasContainer = document.getElementById('canvas-container');
                    const w = canvasContainer.offsetWidth;
                    const h = canvasContainer.offsetHeight;
                    p.resizeCanvas(w, h);
                };
            }
        };

        // Start p5.js
        new p5(sketch);
    </script>
</body>
</html>
