<body>

    <div class="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-amplitude">amplitude \(a\)</label>
                <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
                <span id="value-amplitude">1.50</span>
            </div>
            <div class="control-row">
                <label for="slider-frequency">frequency \(f\)</label>
                <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
                <span id="value-frequency">0.30</span>
            </div>
            <div class="control-row">
                <label for="slider-wavelength">wavelength \(\lambda\)</label>
                <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
                <span id="value-wavelength">4.5</span>
            </div>
            <div class="control-row">
                <label for="slider-time">time \(t\)</label>
                <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
                <span id="value-time">2.50</span>
            </div>
            <div class="control-row">
                <label for="select-phase">initial phase \(\phi_0\)</label>
                <select id="select-phase">
                    <option value="-3.141592653589793">-π</option>
                    <option value="-2.0943951023931953">-2π/3</option>
                    <option value="-1.5707963267948966" selected>-π/2</option>
                    <option value="-1.0471975511965976">-π/3</option>
                    <option value="0">0</option>
                    <option value="1.0471975511965976">π/3</option>
                    <option value="1.5707963267948966">π/2</option>
                    <option value="2.0943951023931953">2π/3</option>
                    <option value="3.141592653589793">π</option>
                </select>
            </div>
        </div>

        <div id="formula-display"></div>

        <div id="canvas-container"></div>
    </div>


    <script>
        // Global p5 instance
        let p5Instance;

        // DOM elements
        const sliderAmplitude = document.getElementById('slider-amplitude');
        const sliderFrequency = document.getElementById('slider-frequency');
        const sliderWavelength = document.getElementById('slider-wavelength');
        const sliderTime = document.getElementById('slider-time');
        const selectPhase = document.getElementById('select-phase');

        const valueAmplitude = document.getElementById('value-amplitude');
        const valueFrequency = document.getElementById('value-frequency');
        const valueWavelength = document.getElementById('value-wavelength');
        const valueTime = document.getElementById('value-time');

        const formulaDisplay = document.getElementById('formula-display');

        // Parameters object
        let params = {};

        function updateParameters() {
            params.A = parseFloat(sliderAmplitude.value);
            params.f = parseFloat(sliderFrequency.value);
            params.lambda = parseFloat(sliderWavelength.value);
            params.t = parseFloat(sliderTime.value);
            params.phi0 = parseFloat(selectPhase.value);

            // Derived parameters
            params.omega = 2 * Math.PI * params.f; // Angular frequency
            params.k = 2 * Math.PI / params.lambda; // Wavenumber

            // Update value displays
            valueAmplitude.textContent = params.A.toFixed(2);
            valueFrequency.textContent = params.f.toFixed(2);
            valueWavelength.textContent = params.lambda.toFixed(1);
            valueTime.textContent = params.t.toFixed(2);
        }

        function updateFormula() {
            const { A, f, lambda, t, phi0, omega, k } = params;
            const omega_t = omega * t;

            let formulaStr = '';
            const tolerance = 1e-4;

            // Simplification Logic
            if (Math.abs(phi0 - Math.PI / 2) < tolerance) {
                // A cos(ωt - kx + π/2) = -A sin(ωt - kx) = A sin(kx - ωt)
                formulaStr = `${A.toFixed(2)}\\sin(${k.toFixed(2)}x - ${omega_t.toFixed(2)})`;
            } else if (Math.abs(phi0 + Math.PI / 2) < tolerance) {
                // A cos(ωt - kx - π/2) = A sin(ωt - kx) = -A sin(kx - ωt)
                formulaStr = `-${A.toFixed(2)}\\sin(${k.toFixed(2)}x - ${omega_t.toFixed(2)})`;
            } else if (Math.abs(phi0 - Math.PI) < tolerance || Math.abs(phi0 + Math.PI) < tolerance) {
                // A cos(ωt - kx + π) = -A cos(ωt - kx) = -A cos(kx - ωt)
                formulaStr = `-${A.toFixed(2)}\\cos(${k.toFixed(2)}x - ${omega_t.toFixed(2)})`;
            } else if (Math.abs(phi0) < tolerance) {
                // A cos(ωt - kx) = A cos(kx - ωt)
                formulaStr = `${A.toFixed(2)}\\cos(${k.toFixed(2)}x - ${omega_t.toFixed(2)})`;
            } else {
                // Full form
                const phaseSign = phi0 > 0 ? '+' : '-';
                const phaseVal = Math.abs(phi0);
                let phaseStr = '';
                // Find a nice π representation if possible
                const option = Array.from(selectPhase.options).find(opt => Math.abs(parseFloat(opt.value) - phi0) < tolerance);
                if (option && !option.text.includes('/')) {
                    phaseStr = option.text;
                } else if(option) {
                    phaseStr = `\\frac{${option.text.replace('/', '}{')}}`
                } else {
                    phaseStr = phaseVal.toFixed(2);
                }

                formulaStr = `${A.toFixed(2)}\\cos(${omega_t.toFixed(2)} - ${k.toFixed(2)}x ${phaseSign} ${phaseStr})`;
            }

            formulaDisplay.innerHTML = `\\(y(x,t) = ${formulaStr}\\)`;

            // Tell MathJax to re-render the div
            if (window.MathJax && window.MathJax.typesetPromise) {
                MathJax.typesetPromise([formulaDisplay]).catch(err => console.error('MathJax error:', err));
            }
        }


        function onControlChange() {
            updateParameters();
            updateFormula();
            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        const p5Sketch = (p) => {
            const scaleFactor = 30;
            const origin = { x: 50, y: 0 };

            p.setup = () => {
                const container = document.getElementById('canvas-container');
                const canvas = p.createCanvas(container.offsetWidth, 400);
                canvas.parent(container);
                origin.y = p.height / 2;
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);
                p.translate(origin.x, origin.y);
                p.scale(1, -1); // Flip y-axis to point upwards

                drawGridAndAxes(p);
                drawWave(p);
                drawPointsAndVectors(p);
            };

            function drawGridAndAxes(p) {
                p.push();

                // Axes
                p.stroke(0);
                p.strokeWeight(1.5);
                // X-axis
                p.line(0, 0, p.width - origin.x - 20, 0);
                p.fill(0);
                p.triangle(p.width - origin.x - 20, 5, p.width - origin.x - 20, -5, p.width - origin.x - 10, 0);
                // Y-axis
                p.line(0, -p.height/2 + 20, 0, p.height/2 - 20);
                p.triangle(-5, p.height/2 - 20, 5, p.height/2 - 20, 0, p.height/2 - 10);

                // Axis labels
                p.push();
                p.scale(1, -1);
                p.noStroke();
                p.fill(0);
                p.textAlign(p.CENTER, p.TOP);
                p.text('x', p.width - origin.x - 20, 5);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text('y', -10, -p.height/2 + 20);
                p.pop();

                // Grid and Ticks
                p.strokeWeight(0.5);
                for (let x = 1; x <= 16; x++) {
                    const px = x * scaleFactor;
                    // Tick mark
                    p.stroke(0);
                    p.line(px, -3, px, 3);
                    // Grid line
                    p.stroke(180, 220, 180);
                    p.drawingContext.setLineDash([5, 5]);
                    p.line(px, 0, px, p.height/2);
                    p.drawingContext.setLineDash([]);

                    // Number label
                    p.push();
                    p.scale(1, -1);
                    p.noStroke();
                    p.fill(0);
                    p.textAlign(p.CENTER, p.BOTTOM);
                    p.text(x, px, -5);
                    p.pop();
                }
                p.pop();
            }

            function drawWave(p) {
                const { A, omega, k, t, phi0 } = params;

                p.push();
                p.noFill();
                p.stroke(0, 102, 204);
                p.strokeWeight(2.5);
                p.beginShape();
                for (let px = 0; px < p.width - origin.x; px += 2) {
                    const x = px / scaleFactor;
                    const y = A * Math.cos(omega * t - k * x + phi0);
                    p.vertex(px, y * scaleFactor);
                }
                p.endShape();
                p.pop();
            }

            function drawPointsAndVectors(p) {
                const { A, omega, k, t, phi0 } = params;

                for (let x = 1; x <= 16; x++) {
                    const y = A * Math.cos(omega * t - k * x + phi0);
                    const v = -A * omega * Math.sin(omega * t - k * x + phi0);
                    const a = -A * omega * omega * Math.cos(omega * t - k * x + phi0);

                    const px = x * scaleFactor;
                    const py = y * scaleFactor;

                    // Draw point
                    p.push();
                    p.fill(0);
                    p.noStroke();
                    p.ellipse(px, py, 8, 8);
                    p.pop();

                    // Draw vectors
                    drawArrow(p, px, py, v * 15, '#d9534f', 'v'); // Velocity vector (red)
                    drawArrow(p, px, py, a * 5, '#000000', 'a');  // Acceleration vector (black)
                }
            }

            function drawArrow(p, baseX, baseY, vecY, color, label) {
                p.push();
                p.stroke(color);
                p.fill(color);
                p.strokeWeight(1.5);

                // Line
                p.line(baseX, baseY, baseX, baseY + vecY);

                // Arrowhead
                p.push();
                p.translate(baseX, baseY + vecY);
                const angle = vecY >= 0 ? p.PI / 2 : -p.PI / 2;
                p.rotate(angle);
                p.triangle(0, 0, -5, -3, -5, 3);
                p.pop();

                // Label
                drawVectorLabel(p, label, baseX + 8, baseY + vecY, color);

                p.pop();
            }

            function drawVectorLabel(p, char, x, y, col) {
                p.push();
                p.translate(x, y);
                p.scale(1, -1); // un-flip for text drawing
                p.noStroke();
                p.fill(col);
                p.textAlign(p.LEFT, p.CENTER);
                p.textSize(14);
                p.textStyle(p.ITALIC);

                // Draw character
                p.text(char, 0, 0);

                // Draw vector symbol above character
                const charWidth = p.textWidth(char);
                p.stroke(col);
                p.strokeWeight(1);
                p.line(0, -10, charWidth, -10);
                p.line(charWidth, -10, charWidth - 4, -12);
                p.line(charWidth, -10, charWidth - 4, -8);

                p.pop();
            }
        };

        // Initialize
        document.addEventListener('DOMContentLoaded', () => {
            sliderAmplitude.addEventListener('input', onControlChange);
            sliderFrequency.addEventListener('input', onControlChange);
            sliderWavelength.addEventListener('input', onControlChange);
            sliderTime.addEventListener('input', onControlChange);
            selectPhase.addEventListener('change', onControlChange);

            // Wait for MathJax to be ready before the first render
            MathJax.startup.promise.then(() => {
                p5Instance = new p5(p5Sketch);
                onControlChange(); // Initial calculation and draw
            });
        });
    </script>

</body>
</html>
