<body>

    <div class="main-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <label for="slider-eccentricity">eccentricity ε</label>
            <input type="range" id="slider-eccentricity" min="0" max="1.5" step="0.01" value="0">
            <span id="value-eccentricity">0.0</span>
        </div>

        <!-- Visualization Area Section -->
        <div id="visualization-area">
            <h3>Kepler orbits</h3>
            <div id="canvas-container"></div>
            <div id="energy-display">
                energy = <span id="energy-value"></span>
            </div>
        </div>
    </div>

    <script>
        // --- Global variables for DOM elements ---
        let slider;
        let valueDisplay;
        let energyValueDisplay;

        // --- p5.js Sketch ---
        function setup() {
            let canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');
            noLoop(); // We will only redraw on slider input

            // Get references to DOM elements using p5's select
            slider = select('#slider-eccentricity');
            valueDisplay = select('#value-eccentricity');
            energyValueDisplay = select('#energy-value');

            // Attach input event listener to the slider
            slider.input(updateVisualization);

            // Initial draw to set up the default state
            updateVisualization();
        }

        function updateVisualization() {
            redraw(); // Triggers the draw() function once
        }

        function draw() {
            // --- 1. Get State & Update UI Text ---
            const epsilon = parseFloat(slider.value());

            // Update eccentricity value display
            valueDisplay.html(epsilon.toFixed(1));

            // Calculate and display energy
            const energy = -0.25 * (1 - epsilon * epsilon);
            if (epsilon === 0) {
                energyValueDisplay.html('\\(-\\frac{1}{4}\\)');
                // Re-render MathJax content if MathJax is loaded
                if (window.MathJax && MathJax.typesetPromise) {
                    MathJax.typesetPromise([energyValueDisplay.elt]).catch(err => console.error(err));
                }
            } else {
                 energyValueDisplay.html(energy.toFixed(2));
            }

            // --- 2. Canvas Drawing ---
            background('#f0f0f0');

            const scaleFactor = 30;
            const originX = width * 0.7;
            const originY = height / 2;

            // Save the original canvas state
            push();

            translate(originX, originY);
            scale(1, -1); // Flip Y-axis for standard math coordinates

            drawAxes(scaleFactor);

            // Draw central attracting body (at the origin of our new coordinate system)
            fill(0);
            noStroke();
            // The circle is drawn in pixel space before scaling to keep its size constant
            circle(0, 0, 6); 

            // Apply scaling for world coordinates
            scale(scaleFactor);

            // Draw orbit path
            stroke('#4a80b5');
            strokeWeight(2 / scaleFactor); // Keep stroke weight constant in pixels
            noFill();

            const p = 2; // Semi-latus rectum

            beginShape();
            if (epsilon === 0) { // Special Case: Circle
                // Per plan: circle of radius 2 centered at (-2, 0) to match screenshot
                for (let theta = 0; theta <= TWO_PI + 0.01; theta += 0.01) {
                    let x = 2 * cos(theta) - 2;
                    let y = 2 * sin(theta);
                    vertex(x, y);
                }
            } else if (epsilon > 0 && epsilon < 1) { // Ellipse
                for (let theta = 0; theta <= TWO_PI + 0.01; theta += 0.01) {
                    let r = p / (1 + epsilon * cos(theta));
                    let x = r * cos(theta);
                    let y = r * sin(theta);
                    vertex(x, y);
                }
            } else if (epsilon === 1) { // Parabola
                // Limit theta to keep the plot within bounds
                for (let theta = -2.2; theta <= 2.2; theta += 0.01) {
                    let r = p / (1 + epsilon * cos(theta));
                    let x = r * cos(theta);
                    let y = r * sin(theta);
                    vertex(x, y);
                }
            } else { // Hyperbola (epsilon > 1)
                const angleLimit = acos(-1 / epsilon);
                // Iterate within the valid range, with a small margin to avoid infinities
                for (let theta = -angleLimit * 0.999; theta <= angleLimit * 0.999; theta += 0.01) {
                    let r = p / (1 + epsilon * cos(theta));
                    // Safety check to prevent drawing extremely large lines
                    if (r > 0 && r < 50) { 
                        let x = r * cos(theta);
                        let y = r * sin(theta);
                        vertex(x, y);
                    }
                }
            }
            endShape();

            // Restore original canvas state
            pop();
        }

        function drawAxes(scaleFactor) {
            const xMin = -15;
            const xMax = 5;
            const yMin = -7;
            const yMax = 7;

            const xMinPx = xMin * scaleFactor;
            const xMaxPx = xMax * scaleFactor;
            const yMinPx = yMin * scaleFactor;
            const yMaxPx = yMax * scaleFactor;

            push(); // Isolate axis drawing state
            stroke(0);
            strokeWeight(1);

            // Draw lines in the transformed (but not scaled by scaleFactor) coordinate system
            // X-axis
            line(xMinPx, 0, xMaxPx, 0);
            // Y-axis
            line(0, yMinPx, 0, yMaxPx);

            // Undo Y-flip for text rendering to keep it upright and un-mirrored
            scale(1, -1);
            fill(0);
            noStroke();
            textSize(12);

            // X-axis ticks and labels
            textAlign(CENTER, TOP);
            for (let i = xMin; i <= xMax; i++) {
                if (i !== 0) {
                    line(i * scaleFactor, -3, i * scaleFactor, 3); // Draw in pre-text-scaling space
                    if (i % 5 === 0) { 
                        text(i, i * scaleFactor, 8);
                    }
                }
            }

            // Y-axis ticks and labels
            textAlign(RIGHT, CENTER);
            for (let i = yMin; i <= yMax; i++) {
                 if (i !== 0) {
                    line(-3, -i * scaleFactor, 3, -i * scaleFactor);
                    if (i % 2 === 0) {
                        text(i, -8, -i * scaleFactor);
                    }
                 }
            }

            // Axis end labels from screenshots
            textAlign(LEFT, CENTER);
            text("Global'x", xMaxPx + 5, 0);
            textAlign(CENTER, BOTTOM);
            text("Global'y", 0, -yMaxPx - 5);

            pop(); // Restore state for main drawing
        }
    </script>

</body>
