<script>
document.addEventListener('DOMContentLoaded', () => {

    // --- DOM Elements ---
    const sliders = {
        poc: document.getElementById('slider-poc'),
        eh: document.getElementById('slider-eh'),
        hg_m: document.getElementById('slider-hg_m'),
        k: document.getElementById('slider-k'),
        a: document.getElementById('slider-a'),
    };

    const values = {
        poc: document.getElementById('value-poc'),
        eh: document.getElementById('value-eh'),
        hg_m: document.getElementById('value-hg_m'),
        k: document.getElementById('value-k'),
        a: document.getElementById('value-a'),
    };

    // --- Configuration and Initial State ---
    const config = {
        poc: { default: 1 },
        eh: { default: 1 },
        hg_m: { default: 1 },
        k: { default: 0.06 },
        a: { default: 0.25 },
    };

    // Set initial slider values
    for (const key in sliders) {
        sliders[key].value = config[key].default;
    }

    const plotConfig = {
        staticPlot: true,
        displayModeBar: false,
    };

    // --- Plotting Logic ---

    function calculateMI(poc, eh, hg_m, srb) {
        // Based on the plan's formula structure, with a derived scaling factor to match visuals.
        // MI = (125 * (-Eh + 200) * POC * [Hg]m) / SRB / K_scaling
        const K_scaling = 1.4e6;
        const C = 125 * (-eh + 200) * poc * hg_m;
        return C / (srb * K_scaling);
    }

    function drawPlot1(poc, eh, hg_m) {
        const srb_x = Array.from({ length: 100 }, (_, i) => i + 1);
        const mi_y = srb_x.map(srb => calculateMI(poc, eh, hg_m, srb));

        const trace = {
            x: srb_x,
            y: mi_y,
            mode: 'lines',
            line: { color: 'orange', dash: 'dash' },
            name: 'Variable POC'
        };

        const layout = {
            title: 'MeHg index: variable POC',
            xaxis: { title: 'SRB (dSrB)', range: [0, 100] },
            yaxis: { title: 'methyl mercury index (MI)', range: [0, 2.0] },
            plot_bgcolor: 'white',
            margin: { t: 40, r: 20, b: 50, l: 60 },
            showlegend: false
        };

        Plotly.react('plot1', [trace], layout, plotConfig);
    }

    function drawPlot2(eh, hg_m) {
        const srb_x = Array.from({ length: 100 }, (_, i) => i + 1);
        const poc_values = {
            River: { poc: 56, color: '#1f77b4' },
            Estuarine: { poc: 6, color: '#aec7e8' },
            Seawater: { poc: 0.3, color: '#d1e0f0' },
        };

        const traces = Object.keys(poc_values).map(name => {
            const { poc, color } = poc_values[name];
            const mi_y = srb_x.map(srb => calculateMI(poc, eh, hg_m, srb));
            return {
                x: srb_x,
                y: mi_y,
                mode: 'lines',
                line: { color: color },
                name: name
            };
        });

        const layout = {
            title: 'MI at river, estuarine and marine POC',
            xaxis: { title: 'SRB (dSrB)', range: [0, 100] },
            yaxis: { title: 'methyl mercury index (MI)', range: [0, 1.0] },
            plot_bgcolor: 'white',
            margin: { t: 40, r: 20, b: 50, l: 60 },
            legend: { x: 1, xanchor: 'right', y: 1 }
        };

        Plotly.react('plot2', traces, layout, plotConfig);
    }

    function drawPlot3(k, a) {
        const hgt_x = Array.from({ length: 201 }, (_, i) => i * 0.01);
        const mehg_y = hgt_x.map(hgt => (a * hgt) / (k + hgt));

        const trace = {
            x: hgt_x,
            y: mehg_y,
            mode: 'lines',
            line: { color: '#1f77b4' },
            fill: 'tozeroy',
            fillcolor: 'rgba(31, 119, 180, 0.2)',
            name: '[MeHg]'
        };

        const layout = {
            title: 'methyl mercury availability',
            xaxis: { title: '(µM/L) HgT', range: [0, 2.0] },
            yaxis: { title: '[MeHg] (µM/L)', range: [0, 0.20] },
            plot_bgcolor: 'white',
            margin: { t: 40, r: 20, b: 50, l: 60 },
            showlegend: false,
            annotations: [{
                x: 1.0,
                y: 0.05,
                text: `[MeHg] = ${a.toFixed(3)}`,
                showarrow: false,
                font: { size: 14 },
                bgcolor: 'rgba(255, 255, 255, 0.8)',
                borderpad: 4
            }]
        };

        Plotly.react('plot3', [trace], layout, plotConfig);
    }

    // --- Update Function and Event Handlers ---

    function update() {
        const currentValues = {
            poc: parseFloat(sliders.poc.value),
            eh: parseFloat(sliders.eh.value),
            hg_m: parseFloat(sliders.hg_m.value),
            k: parseFloat(sliders.k.value),
            a: parseFloat(sliders.a.value),
        };

        // Update text displays
        values.poc.textContent = currentValues.poc.toFixed(1);
        values.eh.textContent = currentValues.eh.toFixed(0);
        values.hg_m.textContent = currentValues.hg_m.toFixed(1);
        values.k.textContent = currentValues.k.toFixed(3);
        values.a.textContent = currentValues.a.toFixed(3);

        // Update plots
        drawPlot1(currentValues.poc, currentValues.eh, currentValues.hg_m);
        drawPlot2(currentValues.eh, currentValues.hg_m);
        drawPlot3(currentValues.k, currentValues.a);
    }

    // Attach event listeners
    for (const key in sliders) {
        sliders[key].addEventListener('input', update);
    }

    // Initial draw
    update();
});
</script>

</body>
