                    <button id="btn-conflicts" class="active">conflicts</button>
                    <button id="btn-eigenvalues">eigenvalues</button>
                </div>
                <div id="plot-probability" style="width: 100%; height: 400px;"></div>
            </div>
        </div>
    </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', () => {
    // --- State & Constants ---
    const N = 64;
    const BITS = 6;
    let state = {
        algorithm: 'heuristic',
        totalSteps: 10,
        currentStep: 0,
        probPlotMode: 'conflicts'
    };

    let problem = {
        solutionStates: [],
        conflictNumbers: [],
    };

    let cache = {};

    // --- DOM Elements ---
    const algorithmButtons = {
        unstructured: document.getElementById('btn-unstructured'),
        adiabatic: document.getElementById('btn-adiabatic'),
        'discrete adiabatic': document.getElementById('btn-discrete-adiabatic'),
        heuristic: document.getElementById('btn-heuristic'),
    };
    const totalStepsSlider = document.getElementById('slider-total-steps');
    const totalStepsDisplay = document.getElementById('display-total-steps');
    const currentStepSlider = document.getElementById('slider-current-step');
    const currentStepDisplay = document.getElementById('display-current-step');
    const currentStepLabel = document.getElementById('label-current-step');
    const probModeButtons = {
        conflicts: document.getElementById('btn-conflicts'),
        eigenvalues: document.getElementById('btn-eigenvalues'),
    };

    // --- Math & Quantum Helpers ---
    const hammingDistance = (a, b) => {
        let x = a ^ b;
        let count = 0;
        while (x > 0) {
            x &= (x - 1);
            count++;
        }
        return count;
    };

    const initializeProblem = () => {
        const s1 = Math.floor(Math.random() * N);
        let s2;
        do {
            s2 = Math.floor(Math.random() * N);
        } while (s1 === s2);
        problem.solutionStates = [s1, s2];

        problem.conflictNumbers = Array.from({ length: N }, (_, i) => {
            if (problem.solutionStates.includes(i)) return 0;
            return Math.min(hammingDistance(i, s1), hammingDistance(i, s2));
        });

        // Clear cache when problem changes
        cache = {};
    };

    const getInitialState = () => {
        if (!cache.psi_0) {
            const val = math.complex(1 / Math.sqrt(N), 0);
            cache.psi_0 = math.matrix(Array(N).fill(val));
        }
        return cache.psi_0;
    };

    const getGroverOperator = () => {
        if (cache.groverOperator) return cache.groverOperator;

        const I = math.identity(N);
        const P0 = math.multiply(1 / N, math.ones(N, N));
        const Us = math.subtract(math.multiply(2, P0), I);

        const Uw_diag = Array(N).fill(1);
        problem.solutionStates.forEach(s => Uw_diag[s] = -1);
        const Uw = math.diag(Uw_diag);

        cache.groverOperator = math.multiply(Us, Uw);
        return cache.groverOperator;
    };

    const getGroverEigenData = () => {
        if(cache.groverEigenData) return cache.groverEigenData;

        const G = getGroverOperator();
        const { values, vectors } = math.eigs(G);

        // Find the two significant eigenvectors/eigenvalues
        const eigenPairs = values.map((val, i) => ({ val, vec: vectors.get([i]) })).toArray();

        // The two important eigenvalues are complex conjugates, not equal to 1 or -1
        const significantPairs = eigenPairs.filter(p => Math.abs(p.val.im) > 1e-9);

        if (significantPairs.length < 2) {
             // Fallback for edge cases (e.g., N=4)
             const nonOnePairs = eigenPairs.filter(p => Math.abs(p.val.re - 1) > 1e-9);
             cache.groverEigenData = {
                eigenvalues: [nonOnePairs[0].val, nonOnePairs[1].val],
                eigenvectors: [nonOnePairs[0].vec, nonOnePairs[1].vec]
            };
             return cache.groverEigenData;
        }

        cache.groverEigenData = {
            eigenvalues: [significantPairs[0].val, significantPairs[1].val],
            eigenvectors: [significantPairs[0].vec, significantPairs[1].vec]
        };
        return cache.groverEigenData;
    }

    const getHamiltonian = (s) => {
        const cacheKey = `H_${s.toFixed(5)}`;
        if (cache[cacheKey]) return cache[cacheKey];

        const I = math.identity(N);
        const P0 = math.multiply(1 / N, math.ones(N, N));
        const H0 = math.multiply(-1, P0);
        const Hp = math.diag(problem.conflictNumbers);

        const H_s = math.add(math.multiply(1 - s, H0), math.multiply(s, Hp));
        cache[cacheKey] = H_s;
        return H_s;
    };

    // --- State Calculation ---
    const calculateStateVector = () => {
        const { algorithm, totalSteps, currentStep } = state;
        const s = totalSteps > 0 ? currentStep / totalSteps : 0;
        const psi_0 = getInitialState();

        if (currentStep === 0) {
            return psi_0;
        }

        switch (algorithm) {
            case 'unstructured': {
                const G = getGroverOperator();
                const G_k = math.pow(G, currentStep);
                return math.multiply(G_k, psi_0);
            }
            case 'adiabatic': {
                const H_s = getHamiltonian(s);
                const { values, vectors } = math.eigs(H_s);
                // Find index of the minimum eigenvalue (ground state)
                const minEigenvalueIndex = values.toArray().reduce((minIndex, val, i, arr) => val < arr[minIndex] ? i : minIndex, 0);
                return vectors.get([minEigenvalueIndex]);
            }
            case 'discrete adiabatic':
            case 'heuristic': {
                let psi = psi_0;
                for (let k = 0; k < currentStep; k++) {
                    const s_k = (k + 0.5) / totalSteps; // Use midpoint for better accuracy
                    const H = getHamiltonian(s_k);
                    // Evolution operator U = exp(-i*H*dt), dt=1 for discrete steps
                    const U = math.expm(math.multiply(H, math.complex(0, -1)));
                    psi = math.multiply(U, psi);
                }
                return psi;
            }
            default:
                return psi_0;
        }
    };

    // --- Plotting ---
    const plotAmplitudes = (psi_vector) => {
        const amplitudes = psi_vector.toArray();
        const nonSolData = { x: [], y: [], text: [], colors: [] };
        const solData = { x: [], y: [], text: [] };

        const colorScale = (c) => `hsl(${240 - c * 30}, 70%, 50%)`; // Blue to Orange/Red

        amplitudes.forEach((amp, i) => {
            if (problem.solutionStates.includes(i)) {
                solData.x.push(amp.re);
                solData.y.push(amp.im);
                solData.text.push(`State ${i} (Solution)`);
            } else {
                nonSolData.x.push(amp.re);
                nonSolData.y.push(amp.im);
                nonSolData.text.push(`State ${i}, Conflicts: ${problem.conflictNumbers[i]}`);
                nonSolData.colors.push(colorScale(problem.conflictNumbers[i]));
            }
        });

        const traces = [
            { // Non-solution states
                x: nonSolData.x,
                y: nonSolData.y,
                mode: 'markers',
                type: 'scattergl',
                text: nonSolData.text,
                hoverinfo: 'text',
                marker: {
                    color: nonSolData.colors,
                    size: 8,
                }
            },
            { // Solution states
                x: solData.x,
                y: solData.y,
                mode: 'markers',
                type: 'scattergl',
                text: solData.text,
                hoverinfo: 'text',
                marker: {
                    color: 'black',
                    size: 12,
                    symbol: 'diamond'
                }
            }
        ];

        if (state.currentStep === 0) {
            // Highlight one initial non-solution state
            const firstNonSolIndex = nonSolData.x.findIndex((_, i) => !problem.solutionStates.includes(i));
            if(firstNonSolIndex !== -1) {
                traces.push({
                    x: [nonSolData.x[firstNonSolIndex]],
                    y: [nonSolData.y[firstNonSolIndex]],
                    mode: 'markers',
                    type: 'scattergl',
                    marker: { color: 'red', size: 10, symbol: 'circle-open', line: {width: 2} }
                });
            }
        }

        const maxAbs = Math.max(...amplitudes.map(a => Math.sqrt(a.re*a.re + a.im*a.im)), 0.15);
        const range = [-maxAbs * 1.1, maxAbs * 1.1];

        const layout = {
            xaxis: { title: 'Re', range: range, zeroline: true, zerolinewidth: 1, zerolinecolor: '#999' },
            yaxis: { title: 'Im', range: range, zeroline: true, zerolinewidth: 1, zerolinecolor: '#999', scaleanchor: 'x', scaleratio: 1 },
            showlegend: false,
            margin: { l: 40, r: 20, b: 40, t: 20 },
            hovermode: 'closest'
        };

        Plotly.react('plot-amplitudes', traces, layout, {responsive: true});
    };

    const plotProbability = (psi_vector) => {
        let data, layout;
        const { probPlotMode, algorithm } = state;

        if (probPlotMode === 'conflicts' || (probPlotMode === 'eigenvalues' && algorithm !== 'unstructured')) {
            const probByConflict = {};
            psi_vector.toArray().forEach((amp, i) => {
                const c = problem.conflictNumbers[i];
                const prob = math.abs(amp) ** 2;
                probByConflict[c] = (probByConflict[c] || 0) + prob;
            });

            const x = Object.keys(probByConflict).map(Number).sort((a,b) => a-b);
            const y = x.map(key => probByConflict[key]);

            data = [{
                x: x,
                y: y,
                type: 'bar',
                width: 0.1,
                marker: { color: '#4682B4' }
            }];
            layout = {
                xaxis: { title: probPlotMode === 'conflicts' ? 'number of conflicts' : 'eigenvalue' },
                yaxis: { title: 'probability', range: [0, 1] },
                margin: { l: 40, r: 20, b: 40, t: 20 },
            };
        } else { // Eigenvalues mode for Unstructured search
            const { eigenvalues, eigenvectors } = getGroverEigenData();

            const projections = eigenvectors.map(vec => math.multiply(math.conj(vec), psi_vector));
            const probabilities = projections.map(p => math.abs(p)**2);
            const args = eigenvalues.map(val => math.arg(val));

            data = [{
                x: args,
                y: probabilities,
                type: 'bar',
                width: 0.1,
                marker: { color: '#4682B4' },
                text: probabilities.map(p => p.toFixed(3)),
                hoverinfo: 'x+text'
            }];

            layout = {
                xaxis: { title: 'arg(eigenvalue)', range: [-Math.PI - 0.2, Math.PI + 0.2] },
                yaxis: { title: 'probability', range: [0, 1] },
                margin: { l: 40, r: 20, b: 40, t: 20 },
            };
        }

        Plotly.react('plot-probability', data, layout, {responsive: true});
    };

    // --- Main Update Function ---
    const update = () => {
        const psi_vector = calculateStateVector();
        plotAmplitudes(psi_vector);
        plotProbability(psi_vector);
    };

    // --- UI Update & Event Handlers ---
    const updateUI = () => {
        // Update sliders and displays
        totalStepsSlider.value = state.totalSteps;
        totalStepsDisplay.textContent = state.totalSteps;

        currentStepSlider.max = state.totalSteps;
        if (state.currentStep > state.totalSteps) state.currentStep = state.totalSteps;
        currentStepSlider.value = state.currentStep;
        currentStepDisplay.textContent = state.currentStep;
        currentStepLabel.textContent = `current step (0 to ${state.totalSteps})`;

        // Update active buttons
        Object.values(algorithmButtons).forEach(btn => btn.classList.remove('active'));
        algorithmButtons[state.algorithm].classList.add('active');

        Object.values(probModeButtons).forEach(btn => btn.classList.remove('active'));
        probModeButtons[state.probPlotMode].classList.add('active');
    };

    Object.entries(algorithmButtons).forEach(([name, button]) => {
        button.addEventListener('click', () => {
            state.algorithm = name;
            const defaults = {
                unstructured: 5,
                adiabatic: 200,
                'discrete adiabatic': 200,
                heuristic: 10,
            };
            state.totalSteps = defaults[name];
            state.currentStep = 0;
            updateUI();
            update();
        });
    });

    totalStepsSlider.addEventListener('input', () => {
        state.totalSteps = parseInt(totalStepsSlider.value);
        updateUI();
        update();
    });

    currentStepSlider.addEventListener('input', () => {
        state.currentStep = parseInt(currentStepSlider.value);
        updateUI();
        update();
    });

    Object.entries(probModeButtons).forEach(([name, button]) => {
        button.addEventListener('click', () => {
            state.probPlotMode = name;
            updateUI();
            update(); // Only need to redraw the probability plot, but full update is simpler
        });
    });

    // --- Initial Load ---
    initializeProblem();
    updateUI();
    update();
});
</script>

</body>
</html>
