<script>
    document.addEventListener('DOMContentLoaded', function() {
        // --- DOM Element References ---
        const sliderPressure = document.getElementById('slider-pressure');
        const valuePressure = document.getElementById('value-pressure');
        const sliderTemperature = document.getElementById('slider-temperature');
        const valueTemperature = document.getElementById('value-temperature');
        const selectPlot = document.getElementById('select-plot');
        const checkboxHighPressure = document.getElementById('checkbox-high-pressure');

        const controlGroupPressure = document.getElementById('control-group-pressure');
        const controlGroupTemperature = document.getElementById('control-group-temperature');
        const checkboxContainer = document.getElementById('checkbox-container');
        const plotContainer = document.getElementById('plot-container');

        // --- Helper Functions ---
        const linspace = (start, end, num) => {
            const step = (end - start) / (num - 1);
            return Array.from({length: num}, (_, i) => start + i * step);
        };

        // --- Plotting Logic ---
        const commonLayout = {
            paper_bgcolor: '#FFFFFF',
            plot_bgcolor: '#FFFFFF',
            font: { family: 'sans-serif', size: 12 },
            xaxis: { gridcolor: '#e0e0e0', zeroline: false, title: { font: { size: 14 } } },
            yaxis: { gridcolor: '#e0e0e0', zeroline: false, title: { font: { size: 14 } } },
            showlegend: false,
            margin: { l: 70, r: 30, b: 60, t: 30 },
        };

        const updatePlot = () => {
            const plotType = selectPlot.value;
            if (plotType === 'vs_temp') {
                drawFugacityVsTempPlot();
            } else {
                drawFugacityVsPressPlot();
            }
        };

        const drawFugacityVsTempPlot = () => {
            const P = parseFloat(sliderPressure.value);

            // Mathematical model
            const f_sat_func = (T) => Math.pow(10, 8.65 - 5818 / (230 + T));
            const T_sat = 5818 / (8.65 - Math.log10(P)) - 230;

            const T_min = 280, T_max = 400;
            const T_values = linspace(T_min, T_max, 500);

            // Data for traces
            const T_liquid_stable = T_values.filter(t => t <= T_sat);
            const f_liquid_stable = T_liquid_stable.map(f_sat_func);

            const T_vapor_stable = T_values.filter(t => t >= T_sat);
            const f_vapor_stable = Array(T_vapor_stable.length).fill(P);

            const T_liquid_metastable = T_values.filter(t => t >= T_sat);
            const f_liquid_metastable = T_liquid_metastable.map(f_sat_func);

            const T_vapor_metastable = T_values.filter(t => t <= T_sat);
            const f_vapor_metastable = Array(T_vapor_metastable.length).fill(P);

            const traces = [
                // Stable Liquid
                { x: T_liquid_stable, y: f_liquid_stable, mode: 'lines', line: { color: '#0000FF', width: 2.5 } },
                // Stable Vapor
                { x: T_vapor_stable, y: f_vapor_stable, mode: 'lines', line: { color: '#0000FF', width: 2.5 } },
                // Metastable Liquid
                { x: T_liquid_metastable, y: f_liquid_metastable, mode: 'lines', line: { color: '#A0A0FF', width: 2, dash: 'dash' } },
                // Metastable Vapor
                { x: T_vapor_metastable, y: f_vapor_metastable, mode: 'lines', line: { color: '#A0A0FF', width: 2, dash: 'dash' } },
                // Saturation Point
                { x: [T_sat], y: [P], mode: 'markers', marker: { color: '#000000', size: 8 } },
                // Indicator Line
                { x: [T_sat, T_sat], y: [0, P], mode: 'lines', line: { color: '#000000', dash: 'dash' } }
            ];

            const layout = {
                ...commonLayout,
                xaxis: { ...commonLayout.xaxis, title: 'temperature (°C)', range: [T_min, T_max] },
                yaxis: { ...commonLayout.yaxis, title: 'fugacity (bar)', range: [0, 0.22] },
                annotations: [
                    { x: 310, y: 0.01, text: 'liquid', showarrow: false, font: { size: 16 } },
                    { x: 390, y: P, text: 'vapor', showarrow: false, font: { size: 16 }, yshift: P > 0.18 ? -15 : 15 },
                    { x: T_sat, y: P, text: '<i>T</i><sup>sat</sup>', showarrow: false, font: { size: 16 }, yshift: 15, xanchor: 'center' }
                ]
            };

            Plotly.react(plotContainer, traces, layout, {responsive: true});
        };

        const drawFugacityVsPressPlot = () => {
            const T = parseFloat(sliderTemperature.value);
            const isHighPressure = checkboxHighPressure.checked;

            // Mathematical model
            const P_sat = Math.pow(10, 10.83 - 5047 / T);
            const f_V_func = isHighPressure ? (p) => p * Math.exp(-25 * p / T) : (p) => p;
            const f_sat = f_V_func(P_sat);

            const P_min = 0, P_max = 3.0;
            const P_values = linspace(P_min, P_max, 500);

            // Data for traces
            const P_vapor_stable = P_values.filter(p => p <= P_sat);
            const f_vapor_stable = P_vapor_stable.map(f_V_func);

            const P_liquid_stable = P_values.filter(p => p >= P_sat);
            const f_liquid_stable = Array(P_liquid_stable.length).fill(f_sat);

            const P_vapor_metastable = P_values.filter(p => p >= P_sat);
            const f_vapor_metastable = P_vapor_metastable.map(f_V_func);

            const P_liquid_metastable = P_values.filter(p => p <= P_sat);
            const f_liquid_metastable = Array(P_liquid_metastable.length).fill(f_sat);

            const traces = [
                // Stable Vapor
                { x: P_vapor_stable, y: f_vapor_stable, mode: 'lines', line: { color: '#0000FF', width: 2.5 } },
                // Stable Liquid
                { x: P_liquid_stable, y: f_liquid_stable, mode: 'lines', line: { color: '#0000FF', width: 2.5 } },
                // Metastable Vapor
                { x: P_vapor_metastable, y: f_vapor_metastable, mode: 'lines', line: { color: '#A0A0FF', width: 2, dash: 'dash' } },
                // Metastable Liquid
                { x: P_liquid_metastable, y: f_liquid_metastable, mode: 'lines', line: { color: '#A0A0FF', width: 2, dash: 'dash' } },
                // Saturation Point
                { x: [P_sat], y: [f_sat], mode: 'markers', marker: { color: '#000000', size: 8 } },
                // Indicator Line
                { x: [P_sat, P_sat], y: [0, f_sat], mode: 'lines', line: { color: '#000000', dash: 'dash' } }
            ];

            const layout = {
                ...commonLayout,
                xaxis: { ...commonLayout.xaxis, title: 'pressure (MPa)', range: [P_min, P_max] },
                yaxis: { ...commonLayout.yaxis, title: 'fugacity (MPa)', range: [0, P_max] },
                annotations: [
                    { x: 0.5, y: 0.25, text: 'vapor', showarrow: false, font: { size: 16 } },
                    { x: 2.7, y: f_sat, text: 'liquid', showarrow: false, font: { size: 16 }, yshift: f_sat > 2.7 ? -15 : 15 },
                    { x: P_sat, y: f_sat, text: '<i>P</i><sup>sat</sup>', showarrow: false, font: { size: 16 }, yshift: 15, xanchor: 'center' }
                ]
            };

            Plotly.react(plotContainer, traces, layout, {responsive: true});
        };

        // --- Event Listeners and Initialization ---
        const handlePlotTypeChange = () => {
            const plotType = selectPlot.value;
            if (plotType === 'vs_temp') {
                controlGroupPressure.style.display = 'flex';
                controlGroupTemperature.style.display = 'none';
                checkboxContainer.style.display = 'none';
            } else { // vs_press
                controlGroupPressure.style.display = 'none';
                controlGroupTemperature.style.display = 'flex';
                checkboxContainer.style.display = 'flex';
            }
            updatePlot();
        };

        const init = () => {
            // Set initial control values and text
            valuePressure.textContent = parseFloat(sliderPressure.value).toFixed(2);
            valueTemperature.textContent = sliderTemperature.value;

            // Attach event listeners
            sliderPressure.addEventListener('input', () => {
                valuePressure.textContent = parseFloat(sliderPressure.value).toFixed(2);
                updatePlot();
            });
            sliderTemperature.addEventListener('input', () => {
                valueTemperature.textContent = sliderTemperature.value;
                updatePlot();
            });
            selectPlot.addEventListener('change', handlePlotTypeChange);
            checkboxHighPressure.addEventListener('change', updatePlot);

            // Initial plot render
            updatePlot();
        };

        init();
    });
</script>

