<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>

<script>
document.addEventListener('DOMContentLoaded', () => {

    const sliderTheta = document.getElementById('slider-theta');
    const spanThetaValue = document.getElementById('span-theta-value');
    const spanSinsqThetaValue = document.getElementById('span-sinsq-theta-value');

    const chkMetricCircle = document.getElementById('checkbox-metric-circle');
    const chkMetricRegion = document.getElementById('checkbox-metric-region');
    const chkFirstOrderVec = document.getElementById('checkbox-first-order-vec');
    const chkFirstOrderRegion = document.getElementById('checkbox-first-order-region');
    const chkOptimalPair = document.getElementById('checkbox-optimal-pair');
    const chkOptimalRegion = document.getElementById('checkbox-optimal-region');

    let p5_sketch;
    const plotErrorDiv = 'plot-error';

    // Global state, managed by p5 sketch
    let state = {
        theta: 0,
        a: null, b: null, c: null, d_opt: null,
        c_opt: null, d_opt_optimal: null
    };

    // --- P5.js Sketch ---
    const sketch = (p) => {
        const canvasSize = 400;
        const radius = 150;
        let origin;
        let isDragging = false;

        p.setup = () => {
            const canvas = p.createCanvas(canvasSize, 400);
            canvas.parent('canvas-bloch');
            origin = p.createVector(p.width / 2, p.height - 50);

            state.c = p.createVector(radius * 0.3, radius * 0.7);

            canvas.mousePressed(startDrag);
            canvas.mouseReleased(stopDrag);
            canvas.mouseOut(stopDrag);

            p.noLoop(); // Redraw manually on updates for efficiency
            p5_sketch = p; // Make p5 instance globally available

            // Add event listeners to controls
            sliderTheta.addEventListener('input', updateAll);
            [chkMetricCircle, chkMetricRegion, chkFirstOrderVec, chkFirstOrderRegion, chkOptimalPair, chkOptimalRegion].forEach(chk => {
                chk.addEventListener('change', () => p.redraw());
            });
            chkOptimalPair.addEventListener('change', updatePlot);

            setupPlot();
            updateAll();
        };

        p.draw = () => {
            handleDrag();
            p.background(255);
            p.translate(origin.x, origin.y);

            drawSemicircle();

            if (chkMetricRegion.checked || chkFirstOrderRegion.checked) {
                drawDashedArc(state.c.mag(), '#2E8B57'); // SeaGreen
                if (state.d_opt) drawDashedArc(state.d_opt.mag(), '#D2691E'); // Chocolate
            }
            if (chkMetricCircle.checked) {
                const errRadius = p5.Vector.dist(state.c, state.a);
                drawDashedCircle(state.a, errRadius, 'blue');
            }

            if (chkOptimalRegion.checked && state.c_opt) {
                drawDashedArc(state.c_opt.mag(), '#006400');
                drawDashedArc(state.d_opt_optimal.mag(), '#8B4513');
            }
            if (chkOptimalPair.checked && state.c_opt) {
                drawVector(state.c_opt, '#006400', '', true); // Dark Green dashed
                drawVector(state.d_opt_optimal, '#8B4513', '', true); // SaddleBrown dashed
            }

            drawVector(state.a, 'blue', 'a');
            drawVector(state.b, 'red', 'b');
            drawVector(state.c, 'green', 'c');

            if (chkFirstOrderVec.checked && state.d_opt) {
                drawDashedVector(state.b, state.d_opt, '#FFA500', 'd_err'); // Orange
            }
        };

        const handleDrag = () => {
            if (isDragging) {
                let mouseVec = p.createVector(p.mouseX - origin.x, -(p.mouseY - origin.y));
                if (mouseVec.mag() > radius) {
                    mouseVec.setMag(radius);
                }
                if(mouseVec.y < 0) mouseVec.y = 0; // Constrain to upper semi-circle

                state.c.set(mouseVec);
                updateCalculations();
                updatePlot();
            }
        };

        const startDrag = () => {
            let mouseVec = p.createVector(p.mouseX - origin.x, -(p.mouseY - origin.y));
            if (p5.Vector.dist(mouseVec, state.c) < 15) { // Grab radius
                isDragging = true;
                p.loop();
            }
        };

        const stopDrag = () => {
            if (isDragging) {
                isDragging = false;
                p.noLoop();
            }
        };

        const find_d_opt = (c_vec, b_vec) => {
            const c_norm = c_vec.mag();
            if (c_norm >= radius) return p.createVector(0, 0);

            const c_norm_abstract = c_norm / radius;
            const alpha = Math.sqrt(1 - c_norm_abstract**2);
            const beta = 1.0;

            const c_hat = c_norm > 0 ? c_vec.copy().normalize() : p.createVector(1, 0);
            const c_perp_hat = p.createVector(-c_hat.y, c_hat.x);

            let best_d = p.createVector(0,0);
            let min_dist_sq = Infinity;

            for (let i = 0; i < 180; i++) {
                const t = p.radians(i * 2);
                const d_parallel_abstract = alpha * Math.cos(t);
                const d_perp_abstract = beta * Math.sin(t);

                const d_parallel_vec = p5.Vector.mult(c_hat, d_parallel_abstract * radius);
                const d_perp_vec = p5.Vector.mult(c_perp_hat, d_perp_abstract * radius);
                const d_candidate = p5.Vector.add(d_parallel_vec, d_perp_vec);

                const dist_sq = (d_candidate.x - b_vec.x)**2 + (d_candidate.y - b_vec.y)**2;

                if (dist_sq < min_dist_sq) {
                    min_dist_sq = dist_sq;
                    best_d = d_candidate;
                }
            }
            return best_d;
        };

        window.updateCalculations = () => {
            state.a = p.createVector(radius * Math.sin(state.theta), radius * Math.cos(state.theta));
            state.b = p.createVector(-radius * Math.sin(state.theta), radius * Math.cos(state.theta));
            state.d_opt = find_d_opt(state.c, state.b);

            // Since the exact formula for optimal vectors is not provided, we use a plausible
            // symmetric case for visualization when the checkbox is ticked.
            const optimal_r = radius * Math.cos(state.theta);
            state.c_opt = p.createVector(0, optimal_r);
            state.d_opt_optimal = find_d_opt(state.c_opt, state.b);
        };

        const drawVector = (v, col, label, dashed = false) => {
            p.push();
            p.stroke(col);
            p.fill(col);
            p.strokeWeight(2);
            if (dashed) p.drawingContext.setLineDash([4, 4]);

            p.line(0, 0, v.x, -v.y);
            if (dashed) p.drawingContext.setLineDash([]);

            p.translate(v.x, -v.y);
            p.rotate(v.heading());
            p.triangle(0, 0, -8, 3, -8, -3);
            p.pop();

            p.push();
            p.noStroke();
            p.fill(col);
            p.textSize(16);
            p.textStyle(p.BOLD);
            const labelOffset = p.createVector(v.x, -v.y).normalize().mult(15);
            p.text(label, v.x + labelOffset.x - 5, -v.y + labelOffset.y + 5);
            p.pop();
        };

        const drawDashedVector = (start, end, col, label) => {
            p.push();
            p.stroke(col);
            p.fill(col);
            p.strokeWeight(1.5);
            p.drawingContext.setLineDash([5, 5]);
            p.line(start.x, -start.y, end.x, -end.y);
            p.drawingContext.setLineDash([]);

            const v = p5.Vector.sub(end, start);
            p.translate(end.x, -end.y);
            p.rotate(v.heading());
            p.triangle(0, 0, -8, 3, -8, -3);
            p.pop();

            p.push();
            p.noStroke();
            p.fill(col);
            p.textSize(14);
            const mid = p5.Vector.add(start, end).mult(0.5);
            p.text(label, mid.x - 10, -mid.y - 10);
            p.pop();
        };

        const drawSemicircle = () => {
            p.push();
            p.noFill();
            p.stroke(0);
            p.strokeWeight(2);
            p.arc(0, 0, radius * 2, radius * 2, p.PI, p.TWO_PI);
            p.line(-radius, 0, radius, 0);
            p.pop();
        };

        const drawDashedCircle = (center, rad, col) => {
            p.push();
            p.noFill();
            p.stroke(col);
            p.strokeWeight(1.5);
            p.drawingContext.setLineDash([5, 5]);
            p.ellipse(center.x, -center.y, rad * 2, rad * 2);
            p.drawingContext.setLineDash([]);
            p.pop();
        };

        const drawDashedArc = (rad, col) => {
            p.push();
            p.noFill();
            p.stroke(col);
            p.strokeWeight(2);
            p.drawingContext.setLineDash([8, 6]);
            p.arc(0, 0, rad * 2, rad * 2, p.PI, p.TWO_PI);
            p.drawingContext.setLineDash([]);
            p.pop();
        };
    };

    // --- Plotly Functions ---
    const setupPlot = () => {
        const layout = {
            xaxis: { title: '<i>D(C, A)</i>', range: [0, 1.5], zeroline: false },
            yaxis: { title: '<i>D(C, B)</i>', range: [0, 1.5], zeroline: false },
            showlegend: false,
            margin: { t: 40, r: 20, b: 50, l: 50 },
            width: 400,
            height: 400,
            plot_bgcolor: '#fff',
            paper_bgcolor: '#fff'
        };
        const data = [
            { x: [], y: [], mode: 'lines', line: { color: 'rgb(31, 119, 180)', width: 3 } },
            { x: [], y: [], mode: 'markers', marker: { color: 'red', size: 10 } },
            { x: [], y: [], mode: 'markers', marker: { color: 'green', size: 10 }, visible: false }
        ];
        Plotly.newPlot(plotErrorDiv, data, layout, {responsive: true});
    };

    const updatePlot = () => {
        const theta = state.theta;
        const s2t = Math.sin(2 * theta);
        const s2t_sq = s2t * s2t;

        const x_boundary = [], y_boundary = [];
        for (let i = 0; i <= 100; i++) {
            const x = (i / 100) * s2t_sq;
            x_boundary.push(x);
            y_boundary.push((s2t - Math.sqrt(x))**2);
        }

        const radius = 150;
        const d_ca = state.a ? 0.5 * (p5.Vector.dist(state.c, state.a) / radius)**2 : 0;
        const d_db = state.b && state.d_opt ? 0.5 * (p5.Vector.dist(state.d_opt, state.b) / radius)**2 : 0;

        const opt_err = (s2t / 2)**2;

        Plotly.react(plotErrorDiv, [
            { x: x_boundary, y: y_boundary, type: 'scatter', mode: 'lines', line: { color: 'rgb(31, 119, 180)', width: 3 } },
            { x: [d_ca], y: [d_db], type: 'scatter', mode: 'markers', marker: { color: 'red', size: 10 } },
            { x: [opt_err], y: [opt_err], type: 'scatter', mode: 'markers', marker: { color: 'green', size: 10 }, visible: chkOptimalPair.checked }
        ]);
    };

    // --- Main Update Trigger ---
    function updateAll() {
        state.theta = parseFloat(sliderTheta.value);

        if (Math.abs(state.theta - Math.PI / 7) < 0.001) {
            spanThetaValue.innerHTML = '&pi;/7';
        } else {
            spanThetaValue.textContent = state.theta.toFixed(5);
        }
        spanSinsqThetaValue.textContent = (Math.sin(state.theta)**2).toFixed(6);

        if (window.updateCalculations) {
            window.updateCalculations();
        }

        if (p5_sketch) {
            p5_sketch.redraw();
        }
        updatePlot();
    }

    // Initialize
    new p5(sketch);
});
</script>

