<body>

    <div id="app-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-points">collocation points</label>
                <input type="range" id="slider-points" min="5" max="30" step="1" value="20">
                <span id="label-points"></span>
            </div>
            <div class="control-row">
                <label for="slider-time">t</label>
                <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05">
                <span id="label-time"></span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Get references to DOM elements
        const sliderPoints = document.getElementById('slider-points');
        const labelPoints = document.getElementById('label-points');
        const sliderTime = document.getElementById('slider-time');
        const labelTime = document.getElementById('label-time');
        const plotContainer = document.getElementById('plot-container');

        // --- Mathematical Functions ---

        /**
         * Calculates the analytical solution for temperature T at position x and time t.
         * T(x, t) = (8/π²) * Σ[n=1 to ∞] (1/n²) * sin(nπ/2) * sin(nπx) * e^(-2n²π²t)
         * @param {number} x - Position along the rod (0 to 1).
         * @param {number} t - Time.
         * @param {number} numTerms - Number of terms for the Fourier series summation.
         * @returns {number} The temperature T.
         */
        function analyticalSolution(x, t, numTerms = 100) {
            let sum = 0;
            for (let n = 1; n <= numTerms; n++) {
                const term = (1 / (n * n)) * 
                             Math.sin(n * Math.PI / 2) * 
                             Math.sin(n * Math.PI * x) * 
                             Math.exp(-2 * n * n * Math.PI * Math.PI * t);
                sum += term;
            }
            return (8 / (Math.PI * Math.PI)) * sum;
        }

        /**
         * Generates Chebyshev-Gauss-Lobatto collocation points in the interval [0, 1].
         * @param {number} numPoints - The total number of points (N+1).
         * @returns {number[]} An array of x coordinates, sorted from 0 to 1.
         */
        function getChebyshevPoints(numPoints) {
            const N = numPoints - 1;
            if (N <= 0) return [0.5]; // Handle edge case of 1 point

            const points = [];
            for (let j = 0; j <= N; j++) {
                // Formula gives points from 1 down to 0
                const point = (Math.cos(j * Math.PI / N) + 1) / 2;
                points.push(point);
            }
            // Reverse to get points sorted from 0 to 1
            return points.reverse();
        }

        // --- Plotting Logic ---

        function updatePlot() {
            // Get current values from sliders
            const numPoints = parseInt(sliderPoints.value);
            const time = parseFloat(sliderTime.value);

            // Update display labels
            labelPoints.textContent = numPoints;
            labelTime.textContent = time.toFixed(3);

            // 1. Generate data for the analytical solution (blue curve)
            const x_analytical = [];
            const y_analytical = [];
            const steps = 200;
            for (let i = 0; i <= steps; i++) {
                const x = i / steps;
                x_analytical.push(x);
                y_analytical.push(analyticalSolution(x, time));
            }

            const traceAnalytical = {
                x: x_analytical,
                y: y_analytical,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'blue',
                    width: 3
                },
                hoverinfo: 'none'
            };

            // 2. Generate data for the numerical solution points (red dots)
            const x_numerical = getChebyshevPoints(numPoints);
            const y_numerical = x_numerical.map(x => analyticalSolution(x, time));

            const traceNumerical = {
                x: x_numerical,
                y: y_numerical,
                type: 'scatter',
                mode: 'markers',
                marker: {
                    color: 'red',
                    size: 8,
                    line: {
                        color: 'darkred',
                        width: 1
                    }
                },
                hoverinfo: 'none'
            };

            // Define plot layout
            const layout = {
                xaxis: {
                    title: 'x',
                    range: [0, 1],
                    tickmode: 'linear',
                    dtick: 0.2,
                    gridcolor: '#e0e0e0',
                },
                yaxis: {
                    title: 'T',
                    range: [-0.1, 1.1],
                    tickmode: 'linear',
                    dtick: 0.2,
                    gridcolor: '#e0e0e0',
                },
                showlegend: false,
                margin: { t: 20, r: 20, b: 50, l: 50 },
                paper_bgcolor: 'white',
                plot_bgcolor: 'white',
            };

            // Update the plot using Plotly.react for performance
            Plotly.react(plotContainer, [traceAnalytical, traceNumerical], layout, {responsive: true});
        }

        // --- Event Listeners ---
        sliderPoints.addEventListener('input', updatePlot);
        sliderTime.addEventListener('input', updatePlot);

        // --- Initial Call ---
        // Run once on page load to draw the initial state
        document.addEventListener('DOMContentLoaded', updatePlot);

    </script>
</body>
</html>
