            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // State
            let numRegularPoints = 4;
            let numSteinerPoints = 2;
            let pointMode = 'steiner';
            let showAngles = true;

            let regularPoints = [];
            let steinerPoints = [];
            let allPoints = [];
            let mstEdges = [];
            let totalLength = 0;

            let draggingPoint = null;
            const pointRadius = 8;
            const canvasSize = 600;

            // DOM Elements
            const textTotalLength = document.getElementById('text-total-length');
            const regularButtons = document.querySelectorAll('#regular-points-selector button');
            const steinerButtons = document.querySelectorAll('#steiner-points-selector button');
            const radioButtons = document.querySelectorAll('input[name="point-mode"]');
            const angleCheckbox = document.getElementById('checkbox-show-angles');

            p.setup = () => {
                const canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.parent('canvas-container');
                p.angleMode(p.DEGREES);

                setupControls();
                updateStateFromControls();
                resetPoints();
                calculateMST();
            };

            p.draw = () => {
                p.background('#EEEEEE');

                drawCompleteGraph();
                drawMST();
                drawPoints();

                if (showAngles) {
                    drawAngles();
                }
            };

            // --- INTERACTION ---
            p.mousePressed = () => {
                const pointsToDrag = (pointMode === 'regular') ? regularPoints : steinerPoints;
                for (let pt of pointsToDrag) {
                    if (p.dist(p.mouseX, p.mouseY, pt.x, pt.y) < pointRadius * 1.5) {
                        draggingPoint = pt;
                        break;
                    }
                }
            };

            p.mouseDragged = () => {
                if (draggingPoint) {
                    draggingPoint.x = p.constrain(p.mouseX, 0, p.width);
                    draggingPoint.y = p.constrain(p.mouseY, 0, p.height);
                    calculateMST();
                }
            };

            p.mouseReleased = () => {
                draggingPoint = null;
            };

            // --- SETUP AND STATE MANAGEMENT ---
            function setupControls() {
                // Regular points buttons
                regularButtons.forEach(btn => {
                    btn.addEventListener('click', () => {
                        numRegularPoints = parseInt(btn.id.split('-')[2]);
                        updateActiveButton(regularButtons, btn);
                        resetPoints();
                        calculateMST();
                    });
                });

                // Steiner points buttons
                steinerButtons.forEach(btn => {
                    btn.addEventListener('click', () => {
                        numSteinerPoints = parseInt(btn.id.split('-')[2]);
                        updateActiveButton(steinerButtons, btn);
                        updateSteinerPoints();
                        calculateMST();
                    });
                });

                // Radio buttons
                radioButtons.forEach(radio => {
                    radio.addEventListener('change', () => {
                        if (radio.checked) {
                            pointMode = radio.value;
                        }
                    });
                });

                // Angle checkbox
                angleCheckbox.addEventListener('change', () => {
                    showAngles = angleCheckbox.checked;
                });
            }

            function updateStateFromControls() {
                // Set initial active buttons and checked states based on default values
                document.getElementById(`btn-regular-${numRegularPoints}`).classList.add('active');
                document.getElementById(`btn-steiner-${numSteinerPoints}`).classList.add('active');
                document.getElementById(`radio-${pointMode}`).checked = true;
                angleCheckbox.checked = showAngles;
            }

            function updateActiveButton(buttons, activeButton) {
                buttons.forEach(btn => btn.classList.remove('active'));
                activeButton.classList.add('active');
            }

            function resetPoints() {
                regularPoints = [];
                const radius = p.width * 0.35;
                const centerX = p.width / 2;
                const centerY = p.height / 2;

                // Special initial configuration for 4 points to match screenshot
                if (numRegularPoints === 4) {
                     const r = p.width * 0.4;
                     regularPoints.push(p.createVector(centerX - r, centerY));
                     regularPoints.push(p.createVector(centerX, centerY - r));
                     regularPoints.push(p.createVector(centerX + r, centerY));
                     regularPoints.push(p.createVector(centerX, centerY + r));
                } else {
                    for (let i = 0; i < numRegularPoints; i++) {
                        const angle = i * 360 / numRegularPoints - 90;
                        const x = centerX + radius * p.cos(angle);
                        const y = centerY + radius * p.sin(angle);
                        regularPoints.push(p.createVector(x, y));
                    }
                }

                updateSteinerPoints(true); // true to force reset
            }

            function updateSteinerPoints(isReset = false) {
                const centerX = p.width / 2;
                const centerY = p.height / 2;

                if (isReset) {
                    steinerPoints = [];
                    // Special initial config for 4 regular + 2 steiner
                    if(numRegularPoints === 4 && numSteinerPoints === 2) {
                        const offset = p.height * 0.15;
                        steinerPoints.push(p.createVector(centerX, centerY - offset));
                        steinerPoints.push(p.createVector(centerX, centerY + offset));
                    } else {
                         for (let i = 0; i < numSteinerPoints; i++) {
                            steinerPoints.push(p.createVector(centerX, centerY));
                        }
                    }
                } else {
                    const diff = numSteinerPoints - steinerPoints.length;
                    if (diff > 0) {
                        for (let i = 0; i < diff; i++) {
                            steinerPoints.push(p.createVector(centerX, centerY));
                        }
                    } else if (diff < 0) {
                        steinerPoints.splice(steinerPoints.length + diff, -diff);
                    }
                }
            }

            // --- DRAWING FUNCTIONS ---
            function drawCompleteGraph() {
                p.stroke(190, 160, 130, 128);
                p.strokeWeight(1);
                for (let i = 0; i < allPoints.length; i++) {
                    for (let j = i + 1; j < allPoints.length; j++) {
                        p.line(allPoints[i].x, allPoints[i].y, allPoints[j].x, allPoints[j].y);
                    }
                }
            }

            function drawMST() {
                p.stroke(51, 51, 51);
                p.strokeWeight(3);
                for (const edge of mstEdges) {
                    const p1 = allPoints[edge.u];
                    const p2 = allPoints[edge.v];
                    p.line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            function drawPoints() {
                // Regular points
                p.strokeWeight(2);
                p.stroke(0, 0, 255);
                p.fill(255);
                for (const pt of regularPoints) {
                    p.ellipse(pt.x, pt.y, pointRadius * 2, pointRadius * 2);
                }

                // Steiner points
                p.strokeWeight(2);
                p.stroke(255, 0, 0);
                p.fill(255);
                for (const pt of steinerPoints) {
                    p.ellipse(pt.x, pt.y, pointRadius * 2, pointRadius * 2);
                    p.ellipse(pt.x, pt.y, pointRadius, pointRadius);
                }
            }

            function drawAngles() {
                p.fill(0);
                p.noStroke();
                p.textSize(14);
                p.textAlign(p.CENTER, p.CENTER);

                for (let i = 0; i < steinerPoints.length; i++) {
                    const steinerIdx = regularPoints.length + i;
                    const steinerPt = steinerPoints[i];

                    const neighbors = [];
                    for(const edge of mstEdges) {
                        if(edge.u === steinerIdx) neighbors.push(allPoints[edge.v]);
                        if(edge.v === steinerIdx) neighbors.push(allPoints[edge.u]);
                    }

                    if (neighbors.length < 2) continue;

                    const neighborAngles = neighbors.map(n => {
                        const vec = p5.Vector.sub(n, steinerPt);
                        return { vec: vec, angle: p.atan2(vec.y, vec.x) };
                    }).sort((a, b) => a.angle - b.angle);

                    for (let j = 0; j < neighborAngles.length; j++) {
                        const current = neighborAngles[j];
                        const next = neighborAngles[(j + 1) % neighborAngles.length];

                        let angleDiff = next.angle - current.angle;
                        if (angleDiff < 0) angleDiff += 360;

                        const midAngle = current.angle + angleDiff / 2;
                        const textRadius = 30;
                        const textX = steinerPt.x + textRadius * p.cos(midAngle);
                        const textY = steinerPt.y + textRadius * p.sin(midAngle);

                        p.text(`${p.round(angleDiff)}°`, textX, textY);
                    }
                }
            }

            // --- CORE LOGIC: KRUSKAL'S ALGORITHM for MST ---
            class DSU {
                constructor(n) {
                    this.parent = Array.from({ length: n }, (_, i) => i);
                }
                find(i) {
                    if (this.parent[i] === i) return i;
                    return this.parent[i] = this.find(this.parent[i]);
                }
                union(i, j) {
                    const rootI = this.find(i);
                    const rootJ = this.find(j);
                    if (rootI !== rootJ) {
                        this.parent[rootI] = rootJ;
                        return true;
                    }
                    return false;
                }
            }

            function calculateMST() {
                allPoints = [...regularPoints, ...steinerPoints];
                const n = allPoints.length;
                if (n < 2) {
                    mstEdges = [];
                    totalLength = 0;
                    updateLengthDisplay();
                    return;
                }

                const edges = [];
                for (let i = 0; i < n; i++) {
                    for (let j = i + 1; j < n; j++) {
                        const weight = p.dist(allPoints[i].x, allPoints[i].y, allPoints[j].x, allPoints[j].y);
                        edges.push({ u: i, v: j, weight: weight });
                    }
                }

                edges.sort((a, b) => a.weight - b.weight);

                mstEdges = [];
                totalLength = 0;
                const dsu = new DSU(n);
                for (const edge of edges) {
                    if (dsu.union(edge.u, edge.v)) {
                        mstEdges.push(edge);
                        totalLength += edge.weight;
                        if (mstEdges.length === n - 1) break;
                    }
                }
                updateLengthDisplay();
            }

            function updateLengthDisplay() {
                 // Normalize the length to make it appear smaller and more like the example values
                const normalizedLength = totalLength / 100;
                textTotalLength.textContent = normalizedLength.toFixed(5);
            }

        };

        new p5(sketch);
    </script>
</body>
</html>
