    <script>
        const sketch = (p) => {
            // --- DOM Elements ---
            let sliders = {};
            let valueSpans = {};
            const groupNames = ['w', 'x', 'y', 'z'];

            // --- ANOVA State ---
            let groupData = {
                w: { mean: 0, sd: 1 },
                x: { mean: 0, sd: 1 },
                y: { mean: 0, sd: 1 },
                z: { mean: 0, sd: 1 },
            };
            let anovaResults = {
                grandMean: 0,
                msBetween: 0,
                msWithin: 0,
                fRatio: 0,
            };

            // --- Visualization Constants ---
            const COLORS = {
                GROUP_BAR: '#F57F29',
                MS_BETWEEN: '#4691A5',
                MS_WITHIN: '#D49D3C',
                F_RATIO: '#8C3A8D',
                AXIS: '#333333',
                TEXT: '#000000'
            };

            p.setup = () => {
                const visPanel = document.getElementById('visualization-panel');
                const canvas = p.createCanvas(visPanel.clientWidth - 40, visPanel.clientHeight - 40);
                canvas.parent('visualization-panel');
                p.noLoop();

                // Get references to all sliders and spans
                groupNames.forEach(group => {
                    sliders[`mean-${group}`] = document.getElementById(`slider-mean-${group}`);
                    sliders[`sd-${group}`] = document.getElementById(`slider-sd-${group}`);
                    valueSpans[`mean-${group}`] = document.getElementById(`value-mean-${group}`);
                    valueSpans[`sd-${group}`] = document.getElementById(`value-sd-${group}`);

                    // Attach event listeners
                    sliders[`mean-${group}`].addEventListener('input', updateAndRedraw);
                    sliders[`sd-${group}`].addEventListener('input', updateAndRedraw);
                });

                // Initial calculation and draw
                updateAndRedraw();
            };

            const updateAndRedraw = () => {
                // 1. Read input values from sliders
                groupNames.forEach(group => {
                    const meanVal = parseFloat(sliders[`mean-${group}`].value);
                    const sdVal = parseFloat(sliders[`sd-${group}`].value);

                    groupData[group].mean = meanVal;
                    groupData[group].sd = sdVal;

                    // 2. Update UI text
                    valueSpans[`mean-${group}`].textContent = meanVal.toFixed(1);
                    valueSpans[`sd-${group}`].textContent = sdVal.toFixed(1);
                });

                // 3. Perform ANOVA Calculation
                const k = 4; // number of groups
                const n = 10; // subjects per group

                const means = Object.values(groupData).map(d => d.mean);
                const sds = Object.values(groupData).map(d => d.sd);

                const grandMean = means.reduce((sum, m) => sum + m, 0) / k;

                const ssBetween = n * means.reduce((sum, m) => sum + Math.pow(m - grandMean, 2), 0);
                const msBetween = ssBetween / (k - 1);

                const ssWithin = sds.reduce((sum, sd) => sum + (Math.pow(sd, 2) * (n - 1)), 0);
                const msWithin = ssWithin / (n * k - k);

                const fRatio = msWithin > 0 ? msBetween / msWithin : Infinity;

                anovaResults = { grandMean, msBetween, msWithin, fRatio };

                // 4. Trigger redraw
                p.redraw();
            };

            p.draw = () => {
                p.background(255);
                drawGroupPlot();
                drawAnovaResultsPlot();
            };

            const drawGroupPlot = () => {
                const plotArea = {
                    x: 60,
                    y: 60,
                    width: p.width - 80,
                    height: p.height / 2 - 80
                };
                const dataRange = { min: -10, max: 10 };

                // --- Draw F-Ratio Text ---
                p.textAlign(p.CENTER, p.CENTER);
                p.noStroke();
                p.fill(COLORS.TEXT);
                p.textSize(16);
                const fRatioText = anovaResults.fRatio < 0.01 && anovaResults.fRatio > 0 ? anovaResults.fRatio.toFixed(4) : anovaResults.fRatio.toFixed(2);
                p.text(`F ratio = \n ${fRatioText}`, plotArea.x + plotArea.width / 2, plotArea.y - 30);


                // --- Draw Axes ---
                p.stroke(COLORS.AXIS);
                p.strokeWeight(1);
                p.line(plotArea.x, plotArea.y, plotArea.x, plotArea.y + plotArea.height); // Y-axis

                p.textAlign(p.RIGHT, p.CENTER);
                p.noStroke();
                p.fill(COLORS.TEXT);
                p.textSize(12);
                for (let v = dataRange.min; v <= dataRange.max; v += 5) {
                    const y = p.map(v, dataRange.min, dataRange.max, plotArea.y + plotArea.height, plotArea.y);
                    p.text(v, plotArea.x - 10, y);
                    p.stroke(COLORS.AXIS);
                    p.line(plotArea.x, y, plotArea.x - 5, y);
                    p.noStroke();
                }

                // --- Draw Grand Mean Line ---
                const gmY = p.map(anovaResults.grandMean, dataRange.min, dataRange.max, plotArea.y + plotArea.height, plotArea.y);
                p.stroke(COLORS.AXIS);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([5, 5]);
                p.line(plotArea.x, gmY, plotArea.x + plotArea.width, gmY);
                p.drawingContext.setLineDash([]); // Reset dash pattern

                // --- Draw Group Bars ---
                const barWidth = plotArea.width / groupNames.length * 0.5;
                groupNames.forEach((name, i) => {
                    const { mean, sd } = groupData[name];
                    const centerX = plotArea.x + (i + 0.5) * (plotArea.width / groupNames.length);

                    const yTop = p.map(p.constrain(mean + sd, dataRange.min, dataRange.max), dataRange.min, dataRange.max, plotArea.y + plotArea.height, plotArea.y);
                    const yBottom = p.map(p.constrain(mean - sd, dataRange.min, dataRange.max), dataRange.min, dataRange.max, plotArea.y + plotArea.height, plotArea.y);
                    const yMean = p.map(p.constrain(mean, dataRange.min, dataRange.max), dataRange.min, dataRange.max, plotArea.y + plotArea.height, plotArea.y);

                    // Bar for SD
                    p.noStroke();
                    p.fill(COLORS.GROUP_BAR);
                    p.rect(centerX - barWidth / 2, yTop, barWidth, yBottom - yTop);

                    // Line for Mean
                    p.stroke(COLORS.TEXT);
                    p.strokeWeight(2);
                    p.line(centerX - barWidth / 2, yMean, centerX + barWidth / 2, yMean);

                    // X-axis label
                    p.noStroke();
                    p.fill(COLORS.TEXT);
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(name.toUpperCase(), centerX, plotArea.y + plotArea.height + 10);
                });
            };

            const drawAnovaResultsPlot = () => {
                const plotArea = {
                    x: 60,
                    y: p.height / 2 + 60,
                    width: p.width - 150,
                    height: p.height / 2 - 80
                };
                const dataRange = { min: 0, max: 200 };

                // --- Draw Title ---
                p.textAlign(p.CENTER, p.CENTER);
                p.noStroke();
                p.fill(COLORS.TEXT);
                p.textSize(16);
                p.text("Analysis of the F-Ratio", plotArea.x + plotArea.width / 2, plotArea.y - 30);

                // --- Draw Axes ---
                p.stroke(COLORS.AXIS);
                p.strokeWeight(1);
                p.line(plotArea.x, plotArea.y + plotArea.height, plotArea.x + plotArea.width, plotArea.y + plotArea.height); // X-axis

                p.textAlign(p.CENTER, p.TOP);
                p.noStroke();
                p.fill(COLORS.TEXT);
                p.textSize(12);
                for (let v = dataRange.min; v <= dataRange.max; v += 50) {
                    const x = p.map(v, dataRange.min, dataRange.max, plotArea.x, plotArea.x + plotArea.width);
                    p.text(v, x, plotArea.y + plotArea.height + 5);
                    p.stroke(COLORS.AXIS);
                    p.line(x, plotArea.y + plotArea.height, x, plotArea.y + plotArea.height + 5);
                    p.noStroke();
                }

                // --- Draw Bars and Labels ---
                const barHeight = 20;
                const barSpacing = (plotArea.height - 3 * barHeight) / 2;

                const plotData = [
                    { label: "MS Between", value: anovaResults.msBetween, color: COLORS.MS_BETWEEN },
                    { label: "MS Within", value: anovaResults.msWithin, color: COLORS.MS_WITHIN },
                    { label: "F-Ratio", value: anovaResults.fRatio, color: COLORS.F_RATIO }
                ];

                plotData.forEach((item, i) => {
                    const barY = plotArea.y + i * (barHeight + barSpacing);

                    // Clamp value for visualization
                    const displayValue = p.constrain(item.value, dataRange.min, dataRange.max);
                    const barWidth = p.map(displayValue, dataRange.min, dataRange.max, 0, plotArea.width);

                    // Draw bar
                    p.noStroke();
                    p.fill(item.color);
                    p.rect(plotArea.x, barY, barWidth, barHeight);

                    // Draw label
                    p.fill(COLORS.TEXT);
                    p.textAlign(p.LEFT, p.CENTER);
                    p.text(item.label, plotArea.x + plotArea.width + 10, barY + barHeight / 2);
                });
            };
        };

        new p5(sketch, 'visualization-panel');
    </script>

</body>
</html>
