    <script>
        window.onload = function() {
            // --- DOM Element References ---
            const controls = {
                mu1: { slider: document.getElementById('slider-mu1'), display: document.getElementById('display-mu1') },
                sigma1: { slider: document.getElementById('slider-sigma1'), display: document.getElementById('display-sigma1') },
                xi1: { slider: document.getElementById('slider-xi1'), display: document.getElementById('display-xi1') },
                mu2: { slider: document.getElementById('slider-mu2'), display: document.getElementById('display-mu2') },
                sigma2: { slider: document.getElementById('slider-sigma2'), display: document.getElementById('display-sigma2') },
                xi2: { slider: document.getElementById('slider-xi2'), display: document.getElementById('display-xi2') },
                pmax: { slider: document.getElementById('slider-pmax'), display: document.getElementById('display-pmax') }
            };

            // --- GEV Formula Implementations ---

            // GEV Probability Density Function (PDF)
            function gevPDF(x, mu, sigma, xi) {
                // Handle the Gumbel case where xi is close to zero
                if (Math.abs(xi) < 1e-6) {
                    const z = (x - mu) / sigma;
                    const t = Math.exp(-z);
                    return (1 / sigma) * t * Math.exp(-t);
                } else {
                    const term = 1 + xi * (x - mu) / sigma;
                    if (term <= 0) return 0; // PDF is zero where this term is non-positive
                    const t = Math.pow(term, -1 / xi);
                    return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
                }
            }

            // GEV Return Level (inverse CDF)
            function gevReturnLevel(p, mu, sigma, xi) {
                if (p <= 1) return NaN; // Return period must be > 1
                const prob = 1 - 1 / p;
                // Handle the Gumbel case where xi is close to zero
                if (Math.abs(xi) < 1e-6) {
                    return mu - sigma * Math.log(-Math.log(prob));
                } else {
                    return mu - (sigma / xi) * (1 - Math.pow(-Math.log(prob), -xi));
                }
            }

            // --- Plotting and Update Logic ---

            function updatePlots() {
                // 1. Read current values from all sliders
                const params = {
                    mu1: parseFloat(controls.mu1.slider.value),
                    sigma1: parseFloat(controls.sigma1.slider.value),
                    xi1: parseFloat(controls.xi1.slider.value),
                    mu2: parseFloat(controls.mu2.slider.value),
                    sigma2: parseFloat(controls.sigma2.slider.value),
                    xi2: parseFloat(controls.xi2.slider.value),
                    pmax: parseInt(controls.pmax.slider.value)
                };

                // 2. Update the readout displays
                controls.mu1.display.value = params.mu1.toFixed(2);
                controls.sigma1.display.value = params.sigma1.toFixed(3);
                controls.xi1.display.value = params.xi1.toFixed(2);
                controls.mu2.display.value = params.mu2.toFixed(2);
                controls.sigma2.display.value = params.sigma2.toFixed(2);
                controls.xi2.display.value = params.xi2.toFixed(2);
                controls.pmax.display.value = params.pmax;

                // 3. Generate data for PDF plot
                const x_pdf = [];
                const y_pdf1 = [];
                const y_pdf2 = [];
                const x_min_pdf = 2.5, x_max_pdf = 7;
                for (let i = 0; i <= 450; i++) {
                    const x = x_min_pdf + i * (x_max_pdf - x_min_pdf) / 450;
                    x_pdf.push(x);
                    y_pdf1.push(gevPDF(x, params.mu1, params.sigma1, params.xi1));
                    y_pdf2.push(gevPDF(x, params.mu2, params.sigma2, params.xi2));
                }

                // 4. Update PDF plot
                const pdfData = [{
                    x: x_pdf,
                    y: y_pdf1,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 },
                    fill: 'tozeroy',
                    fillcolor: 'rgba(0, 0, 255, 0.2)',
                    name: 'Dist 1'
                }, {
                    x: x_pdf,
                    y: y_pdf2,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 },
                    fill: 'tozeroy',
                    fillcolor: 'rgba(255, 0, 0, 0.2)',
                    name: 'Dist 2'
                }];

                const pdfLayout = {
                    title: 'PDFs of GEV Distributions 1 and 2',
                    xaxis: { range: [x_min_pdf, x_max_pdf], title: '' },
                    yaxis: { autorange: true, title: '' },
                    margin: { t: 40, b: 40, l: 40, r: 20 },
                    showlegend: false
                };

                Plotly.react('plot-pdf', pdfData, pdfLayout, {responsive: true});

                // 5. Generate data for Return Level plot
                const x_rl = [];
                const y_rl1 = [];
                const y_rl2 = [];
                // Create more points at the beginning for a smoother curve
                for (let p = 1.01; p < 10 && p <= params.pmax; p += 0.1) {
                    x_rl.push(p);
                }
                const step = Math.max(1, (params.pmax - Math.max(10, x_rl[x_rl.length-1] || 1)) / 190);
                for (let p = Math.max(10, x_rl[x_rl.length-1] || 1); p <= params.pmax; p += step) {
                     x_rl.push(p);
                }
                if (x_rl[x_rl.length - 1] < params.pmax) x_rl.push(params.pmax);

                x_rl.forEach(p => {
                    y_rl1.push(gevReturnLevel(p, params.mu1, params.sigma1, params.xi1));
                    y_rl2.push(gevReturnLevel(p, params.mu2, params.sigma2, params.xi2));
                });

                // 6. Update Return Level plot
                const rlData = [{
                    x: x_rl,
                    y: y_rl1,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2.5 },
                    name: 'Dist 1'
                }, {
                    x: x_rl,
                    y: y_rl2,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2.5 },
                    name: 'Dist 2'
                }];

                const rlLayout = {
                    xaxis: { title: 'Return Period (years)', range: [0, params.pmax] },
                    yaxis: { title: 'Maximum Sea Level (meters)', autorange: true },
                    margin: { t: 20, b: 50, l: 60, r: 20 },
                    showlegend: false
                };

                Plotly.react('plot-return-level', rlData, rlLayout, {responsive: true});
            }

            // --- Initial Setup ---
            // Add event listeners to all sliders
            Object.values(controls).forEach(control => {
                control.slider.addEventListener('input', updatePlots);
            });

            // Initial call to draw the plots with default values
            updatePlots();

            // Ensure plots resize with the window
            window.addEventListener('resize', () => {
                Plotly.Plots.resize(document.getElementById('plot-pdf'));
                Plotly.Plots.resize(document.getElementById('plot-return-level'));
            });
        };
    </script>

