            [[1,0], [1,4], [1.5,4], [1.5,1], [2.5,1], [2.5,4], [3,4], [3,0]],
            [[4,0], [4,4], [4.5,4], [4.5,2], [5.5,2], [5.5,4], [6,4], [6,0]]
        ]}
    };

    // --- Plotting Logic ---
    const plotRange = [-10, 20];
    const plotYRange = [-0.5, 4.5];

    function padPoints(points, range) {
        if (!points || points.length === 0) return { x: [], y: [] };
        const x_coords = [range[0]];
        const y_coords = [0];

        if (points[0][0] > range[0]) {
            x_coords.push(points[0][0] - 1e-9, points[0][0]);
            y_coords.push(0, points[0][1]);
        } else {
            x_coords.push(points[0][0]);
            y_coords.push(points[0][1]);
        }

        for (let i = 1; i < points.length; i++) {
            x_coords.push(points[i][0]);
            y_coords.push(points[i][1]);
        }

        const lastPt = points[points.length - 1];
        if (lastPt[0] < range[1]) {
            x_coords.push(lastPt[0], lastPt[0] + 1e-9);
            y_coords.push(0, 0);
        }
        x_coords.push(range[1]);
        y_coords.push(0);

        return { x: x_coords, y: y_coords };
    }

    function updatePlots() {
        const { a, b, T, activeSignal } = state;
        const basePointSets = signals[activeSignal].points;

        const tracesTop = [];
        const tracesBottom = [];

        const colors = {
            x: 'rgb(70, 130, 180)', // steelblue
            y: 'red',
            z: 'olivedrab',
            w: 'saddlebrown',
        };

        basePointSets.forEach((pointSet, index) => {
            const isFirst = index === 0;

            const x_pts_orig = pointSet.map(([t, v]) => [t, v]);
            const y_pts = pointSet.map(([t, v]) => [(t + b) / a, v]);
            const z_pts = pointSet.map(([t, v]) => [t + b, v]);
            const w_pts = pointSet.map(([t, v]) => [t / a, v]);

            const dataX = padPoints(x_pts_orig, plotRange);
            const dataY = padPoints(y_pts, plotRange);
            const dataZ = padPoints(z_pts, plotRange);
            const dataW = padPoints(w_pts, plotRange);

            // Top plot trace
            tracesTop.push({
                x: dataX.x, y: dataX.y,
                mode: 'lines', name: '\\(x(t)\\)',
                line: { color: colors.x, width: 2.5 },
                showlegend: isFirst
            });

            // Bottom plot traces
            tracesBottom.push({
                x: dataX.x, y: dataX.y,
                mode: 'lines', name: '\\(x(t)\\)',
                line: { color: colors.x, width: 2.5 },
                visible: elements.checkboxes.x.checked,
                showlegend: isFirst
            });
            tracesBottom.push({
                x: dataY.x, y: dataY.y,
                mode: 'lines', name: '\\(y(t) = x(at-b) = x(a(t-T))\\)',
                line: { color: colors.y, width: 2.5 },
                visible: elements.checkboxes.y.checked,
                showlegend: isFirst
            });
            tracesBottom.push({
                x: dataZ.x, y: dataZ.y,
                mode: 'lines', name: '\\(z(t) = x(t-b)\\)',
                line: { color: colors.z, width: 2.5 },
                visible: elements.checkboxes.z.checked,
                showlegend: isFirst
            });
            tracesBottom.push({
                x: dataW.x, y: dataW.y,
                mode: 'lines', name: '\\(w(t) = x(at)\\)',
                line: { color: colors.w, width: 2.5 },
                visible: elements.checkboxes.w.checked,
                showlegend: isFirst
            });
        });

        const commonLayout = {
            xaxis: { title: 'time (s)', range: plotRange, zeroline: true },
            yaxis: { range: plotYRange, zeroline: true },
            margin: { l: 40, r: 20, t: 40, b: 40 },
            shapes: [
                { type: 'circle', xref: 'x', yref: 'y', x0: T - 0.15, y0: -0.15, x1: T + 0.15, y1: 0.15, fillcolor: 'red', line: { color: 'red' } },
                { type: 'circle', xref: 'x', yref: 'y', x0: b - 0.15, y0: -0.15, x1: b + 0.15, y1: 0.15, fillcolor: 'green', line: { color: 'green' } },
            ],
            annotations: [
                { x: T, y: 0, xref: 'x', yref: 'y', text: '<b>T</b>', showarrow: false, yshift: 20, font: { color: 'red', size: 16 } },
                { x: b, y: 0, xref: 'x', yref: 'y', text: '<b>b</b>', showarrow: false, yshift: 20, font: { color: 'green', size: 16 } },
            ]
        };

        const layoutTop = { ...commonLayout, title: 'signal', showlegend: true, legend: { x: 0.01, y: 0.75 } };
        const layoutBottom = { ...commonLayout, showlegend: true, legend: { x: 0.01, y: 0.98 } };

        Plotly.react(elements.plots.top, tracesTop, layoutTop, {responsive: true});
        Plotly.react(elements.plots.bottom, tracesBottom, layoutBottom, {responsive: true}).then(() => {
            if (window.MathJax) {
                MathJax.typesetPromise([elements.plots.top, elements.plots.bottom]);
            }
        });
    }

    // --- Event Listeners ---
    function setupEventListeners() {
        // Signal Selection Buttons
        Object.entries(elements.buttons).forEach(([name, button]) => {
            button.addEventListener('click', () => {
                Object.values(elements.buttons).forEach(b => b.classList.remove('active'));
                button.classList.add('active');
                state.activeSignal = button.textContent;
                updatePlots();
            });
        });

        // Sliders
        elements.sliders.a.addEventListener('input', (e) => {
            state.a = parseFloat(e.target.value);
            state.T = state.b / state.a;
            elements.sliders.t.value = state.T;
            elements.values.a.textContent = state.a.toFixed(1);
            elements.values.t.textContent = state.T.toFixed(1);
            updatePlots();
        });

        elements.sliders.b.addEventListener('input', (e) => {
            state.b = parseFloat(e.target.value);
            state.T = state.b / state.a;
            elements.sliders.t.value = state.T;
            elements.values.b.textContent = state.b.toFixed(1);
            elements.values.t.textContent = state.T.toFixed(1);
            updatePlots();
        });

        elements.sliders.t.addEventListener('input', (e) => {
            state.T = parseFloat(e.target.value);
            state.b = state.a * state.T;
            elements.sliders.b.value = state.b;
            elements.values.t.textContent = state.T.toFixed(1);
            elements.values.b.textContent = state.b.toFixed(1);
            updatePlots();
        });

        // Checkboxes
        Object.values(elements.checkboxes).forEach(checkbox => {
            checkbox.addEventListener('change', (e) => {
                const isChecked = e.target.checked;
                const traceId = e.target.id;
                const numBaseTraces = signals[state.activeSignal].points.length;
                let traceIndices = [];

                const map = { 'check-x': 0, 'check-y': 1, 'check-z': 2, 'check-w': 3 };
                const baseIndex = map[traceId];

                for (let i = 0; i < numBaseTraces; i++) {
                    traceIndices.push(i * 4 + baseIndex);
                }

                Plotly.restyle(elements.plots.bottom, { visible: isChecked }, traceIndices);
            });
        });
    }

    // --- Initialization ---
    function init() {
        // Set initial values from state
        elements.sliders.a.value = state.a;
        elements.sliders.b.value = state.b;
        elements.sliders.t.value = state.T;
        elements.values.a.textContent = state.a.toFixed(1);
        elements.values.b.textContent = state.b.toFixed(1);
        elements.values.t.textContent = state.T.toFixed(1);
        elements.buttons.triangle.classList.add('active');
        state.activeSignal = 'Triangle';

        setupEventListeners();
        updatePlots();
    }

    init();
});
</script>

</body>
</html>
