    <!-- Scripts -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        let scene, camera, renderer, controls, masterGroup;

        // --- DOM Elements ---
        const canvas = document.getElementById('main-canvas');
        const trimSlider = document.getElementById('slider-trim');
        const trimLabel = document.getElementById('label-trim-value');
        const resetButton = document.getElementById('btn-reset-view');

        const controlsList = [
            ...document.querySelectorAll('input[name="unitsX"]'),
            ...document.querySelectorAll('input[name="unitsY"]'),
            trimSlider,
            document.getElementById('checkbox-double'),
            document.getElementById('checkbox-sphere'),
            document.getElementById('checkbox-triangles'),
        ];

        // --- Constants ---
        const SPHERE_RADIUS = 4;
        const SHAPE_SIDE_LENGTH = 2.5;
        const SEGMENTS_PER_EDGE = 30;

        // --- Core Functions ---

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            const container = document.getElementById('canvas-container');
            camera = new THREE.PerspectiveCamera(50, container.clientWidth / container.clientHeight, 0.1, 100);
            camera.position.set(0, 0, 15);
            camera.lookAt(scene.position);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Lights
            const hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444, 1.0);
            scene.add(hemiLight);
            const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight.position.set(5, 5, 5);
            scene.add(dirLight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Master group for generated objects
            masterGroup = new THREE.Group();
            scene.add(masterGroup);

            // Event Listeners
            trimSlider.addEventListener('input', () => {
                trimLabel.textContent = parseFloat(trimSlider.value).toFixed(3);
                updateScene();
            });

            controlsList.forEach(control => {
                if(control.id !== 'slider-trim') {
                    control.addEventListener('change', updateScene);
                }
            });

            resetButton.addEventListener('click', () => controls.reset());

            window.addEventListener('resize', onWindowResize, false);

            // Initial call
            trimLabel.textContent = parseFloat(trimSlider.value).toFixed(3);
            updateScene();
            animate();
        }

        function updateScene() {
            // Clear previous objects
            while(masterGroup.children.length > 0) {
                const child = masterGroup.children[0];
                if (child.geometry) child.geometry.dispose();
                if (child.material) {
                     if (Array.isArray(child.material)) {
                        child.material.forEach(m => m.dispose());
                    } else {
                        child.material.dispose();
                    }
                }
                masterGroup.remove(child);
            }

            // Get control values
            const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
            const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
            const trim = parseFloat(trimSlider.value);
            const showDouble = document.getElementById('checkbox-double').checked;
            const showSphere = document.getElementById('checkbox-sphere').checked;
            const showTriangles = document.getElementById('checkbox-triangles').checked;

            const shapeCache = {};

            const createAndAddShapes = (isDouble) => {
                const color1 = isDouble ? new THREE.Color(0x80ffff) : new THREE.Color(0xffc0cb); // cyan / pink
                const color2 = isDouble ? new THREE.Color(0x008080) : new THREE.Color(0x904090); // teal / magenta

                const cacheKey = `${isDouble}`;
                if (!shapeCache[cacheKey]) {
                    shapeCache[cacheKey] = createCurvedTriangle(trim, color1, color2);
                }
                const protoShape = shapeCache[cacheKey];

                const s = SHAPE_SIDE_LENGTH;
                const h = s * Math.sqrt(3) / 2;
                const offsetX = isDouble ? s / 2 : 0;
                const offsetY = isDouble ? h / 3 : 0;

                for (let j = -unitsY; j <= unitsY; j++) {
                    for (let i = -unitsX; i <= unitsX; i++) {
                        const shapeGroup = new THREE.Group();

                        const currentShape = protoShape.shape.clone();
                        const currentOutline = protoShape.outline.clone();
                        shapeGroup.add(currentShape, currentOutline);

                        const pos = new THREE.Vector3(
                            i * s + (j % 2 !== 0 ? s / 2 : 0) + offsetX,
                            j * h + offsetY,
                            0
                        );
                        shapeGroup.position.copy(pos);

                        let rotationParity = (i + j) % 2 !== 0;
                        if (isDouble) rotationParity = !rotationParity;
                        if (rotationParity) {
                           shapeGroup.rotation.z = Math.PI;
                        }

                        masterGroup.add(shapeGroup);

                        if(showTriangles) {
                            const flatGeom = protoShape.flatTriangle.clone();
                            flatGeom.applyMatrix4(shapeGroup.matrix);
                            masterGroup.add(flatGeom);

                            const pointsGeom = protoShape.points.clone();
                            pointsGeom.applyMatrix4(shapeGroup.matrix);
                            masterGroup.add(pointsGeom);
                        }
                    }
                }
            };

            // Create main pattern
            createAndAddShapes(false);

            // Create double pattern if enabled
            if (showDouble) {
                createAndAddShapes(true);
            }

            // Add reference sphere if enabled
            if (showSphere) {
                const sphereGeom = new THREE.SphereGeometry(SPHERE_RADIUS, 64, 32);
                const sphereMat = new THREE.MeshPhongMaterial({
                    color: 0xdddddd,
                    shininess: 80,
                    specular: 0x222222
                });
                const sphereMesh = new THREE.Mesh(sphereGeom, sphereMat);
                masterGroup.add(sphereMesh);
            }
        }

        function createCurvedTriangle(trim, color1, color2) {
            // This function creates one procedural shape and returns it.

            const shapeGroup = new THREE.Group();

            // Define 3 vertices of a flat equilateral triangle
            const distToVertex = SHAPE_SIDE_LENGTH / Math.sqrt(3);
            const flatVertices = [
                new THREE.Vector2(distToVertex, 0).rotateAround(new THREE.Vector2(), -Math.PI/6),
                new THREE.Vector2(distToVertex, 0).rotateAround(new THREE.Vector2(), Math.PI*3/6),
                new THREE.Vector2(distToVertex, 0).rotateAround(new THREE.Vector2(), Math.PI*7/6),
            ];

            const perimeterPoints = [];
            const flatPerimeterPoints = [];

            // Create curved edges
            for (let i = 0; i < 3; i++) {
                const p1 = flatVertices[i];
                const p2 = flatVertices[(i + 1) % 3];

                const midpoint = new THREE.Vector2().addVectors(p1, p2).multiplyScalar(0.5);
                const toCenterDir = midpoint.clone().normalize().multiplyScalar(-1);

                // Map trim value to arc "bulge"
                // (0.9 - t) / (0.9 - 0.65) -> [0, 1] range for concavity
                const concavity = (0.9 - trim) / 0.25;
                const bulge = concavity * distToVertex * 0.35;

                const arcCenter = midpoint.clone().add(toCenterDir.multiplyScalar(bulge));
                const arcRadius = arcCenter.distanceTo(p1);

                const angle1 = Math.atan2(p1.y - arcCenter.y, p1.x - arcCenter.x);
                const angle2 = Math.atan2(p2.y - arcCenter.y, p2.x - arcCenter.x);

                // Ensure angles are in correct order for interpolation
                let angleDist = angle2 - angle1;
                if (angleDist > Math.PI) angleDist -= 2 * Math.PI;
                if (angleDist < -Math.PI) angleDist += 2 * Math.PI;

                for (let j = 0; j < SEGMENTS_PER_EDGE; j++) {
                    const t = j / (SEGMENTS_PER_EDGE -1);
                    const angle = angle1 + t * angleDist;
                    const x = arcCenter.x + arcRadius * Math.cos(angle);
                    const y = arcCenter.y + arcRadius * Math.sin(angle);
                    flatPerimeterPoints.push(new THREE.Vector2(x, y));
                }
            }

            // Project points onto sphere and create geometry
            const geometry = new THREE.BufferGeometry();
            const vertices = [];
            const colors = [];
            const center = new THREE.Vector3(0, 0, SPHERE_RADIUS);

            vertices.push(center.x, center.y, center.z);
            colors.push(color1.r, color1.g, color1.b);

            flatPerimeterPoints.forEach(p => {
                const pSq = p.x * p.x + p.y * p.y;
                if (pSq < SPHERE_RADIUS * SPHERE_RADIUS) {
                    const z = Math.sqrt(SPHERE_RADIUS * SPHERE_RADIUS - pSq);
                    vertices.push(p.x, p.y, z);
                    colors.push(color2.r, color2.g, color2.b);
                } else { // Fallback for points outside sphere radius
                    const p3 = new THREE.Vector3(p.x, p.y, 0).normalize().multiplyScalar(SPHERE_RADIUS);
                    vertices.push(p3.x, p3.y, p3.z);
                    colors.push(color2.r, color2.g, color2.b);
                }
            });

            const indices = [];
            const numPerimeterPoints = flatPerimeterPoints.length;
            for (let i = 1; i <= numPerimeterPoints; i++) {
                indices.push(0, i, (i % numPerimeterPoints) + 1);
            }

            geometry.setIndex(indices);
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            const material = new THREE.MeshStandardMaterial({
                vertexColors: true,
                side: THREE.DoubleSide,
                metalness: 0.1,
                roughness: 0.6
            });
            const mesh = new THREE.Mesh(geometry, material);

            // Create outline
            const outlineGeom = new THREE.BufferGeometry();
            const outlineVertices = vertices.slice(3); // Exclude center point
            outlineGeom.setAttribute('position', new THREE.Float32BufferAttribute(outlineVertices, 3));
            const outlineMat = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 2 });
            const outline = new THREE.LineLoop(outlineGeom, outlineMat);

            // Create helper geometries for 'triangles' option
            const flatTriangleGeom = new THREE.BufferGeometry();
            const flatTriangleVertices = [];
            const flatMainVertices = [];
            flatVertices.forEach(v => flatMainVertices.push(v.x, v.y, 0.01)); // slight z-offset
            flatTriangleVertices.push(...flatMainVertices);
            flatTriangleGeom.setAttribute('position', new THREE.Float32BufferAttribute(flatTriangleVertices, 3));
            const flatTriangleMat = new THREE.MeshBasicMaterial({color: 0x800080, transparent: true, opacity: 0.4, side: THREE.DoubleSide});
            const flatTriangleMesh = new THREE.Mesh(flatTriangleGeom, flatTriangleMat);

            const pointsGeom = new THREE.BufferGeometry();
            pointsGeom.setAttribute('position', new THREE.Float32BufferAttribute(flatMainVertices, 3));
            const pointsMat = new THREE.PointsMaterial({color: 0x000000, size: 3, sizeAttenuation: false});
            const points = new THREE.Points(pointsGeom, pointsMat);

            return {
                shape: mesh,
                outline: outline,
                flatTriangle: flatTriangleMesh,
                points: points
            };
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        // --- Start ---
        init();

    </script>
</body>
