<script>
const sketch = (p) => {
    // --- State Variables ---
    let startTime = -1.0;
    let totalTime = 2.2;
    let selectedFunction = 'g';
    let selectedEncoding = 'asdm';

    // --- DOM Elements ---
    let startTimeSlider, totalTimeSlider;
    let functionButtons, encodingButtons;

    // --- Plotting Constants ---
    const margin = { top: 40, right: 40, bottom: 40, left: 40 };
    let plotWidth, plotHeight;
    const yRange = { min: -1.2, max: 1.2 };

    // --- Analog Function Definitions ---
    const functions = {
        'f': t => 0.5 * t,
        'g': t => p.sin(2 * p.PI * t) + 0.3 * p.cos(3 * 2 * p.PI * t),
        'sin': t => p.sin(2 * p.PI * t),
        'cos': t => p.cos(2 * p.PI * t),
        'tan': t => p.tan(p.PI * t),
    };

    p.setup = () => {
        const container = document.getElementById('canvas-container');
        const canvas = p.createCanvas(container.clientWidth, 500);
        canvas.parent(container);

        plotWidth = p.width - margin.left - margin.right;
        plotHeight = p.height - margin.top - margin.bottom;

        p.noLoop();

        setupControls();
        updateAndRedraw();
    };

    // --- Control Setup and Event Handling ---
    function setupControls() {
        // Info button
        document.getElementById('info-button').addEventListener('click', () => {
            const panel = document.getElementById('description-panel');
            panel.style.display = panel.style.display === 'none' ? 'block' : 'none';
        });

        // Sliders
        startTimeSlider = document.getElementById('slider-start-time');
        totalTimeSlider = document.getElementById('slider-total-time');
        startTimeSlider.addEventListener('input', () => {
            startTime = parseFloat(startTimeSlider.value);
            updateAndRedraw();
        });
        totalTimeSlider.addEventListener('input', () => {
            totalTime = parseFloat(totalTimeSlider.value);
            updateAndRedraw();
        });

        // Button Groups
        functionButtons = document.getElementById('function-buttons');
        encodingButtons = document.getElementById('encoding-buttons');

        functionButtons.addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                selectedFunction = e.target.id.replace('btn-', '');
                updateActiveButton(functionButtons, e.target);
                updateAndRedraw();
            }
        });

        encodingButtons.addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                selectedEncoding = e.target.id.replace('btn-', '');
                updateActiveButton(encodingButtons, e.target);
                updateAndRedraw();
            }
        });

        // Set initial active buttons
        updateActiveButton(functionButtons, document.getElementById('btn-' + selectedFunction));
        updateActiveButton(encodingButtons, document.getElementById('btn-' + selectedEncoding));
    }

    function updateActiveButton(group, activeButton) {
        Array.from(group.children).forEach(btn => btn.classList.remove('active'));
        activeButton.classList.add('active');
    }

    function updateAndRedraw() {
        p.redraw();
    }

    p.draw = () => {
        p.background(255);
        p.translate(margin.left, margin.top);

        const xRange = { min: startTime, max: startTime + totalTime };

        drawAxes(xRange, yRange);
        drawAnalogSignal(xRange, yRange);
        drawEncodedSignal(xRange, yRange);
    };

    // --- Drawing Functions ---
    function drawAxes(xRange, yRange) {
        p.stroke(200);
        p.strokeWeight(1);
        p.noFill();

        // Axes lines
        p.line(0, 0, 0, plotHeight);
        p.line(0, plotHeight, plotWidth, plotHeight);

        // Map y=0 to screen coordinates
        const yZero = p.map(0, yRange.min, yRange.max, plotHeight, 0);
        p.stroke(150);
        p.line(0, yZero, plotWidth, yZero); // X-axis at y=0

        // Map x=0 to screen coordinates
        const xZero = p.map(0, xRange.min, xRange.max, 0, plotWidth);
        if (xZero >= 0 && xZero <= plotWidth) {
           p.line(xZero, 0, xZero, plotHeight); // Y-axis at x=0
        }

        // Ticks and labels
        p.fill(100);
        p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        const xTickStep = 0.5;
        for (let x = Math.ceil(xRange.min / xTickStep) * xTickStep; x <= xRange.max; x += xTickStep) {
            const screenX = p.map(x, xRange.min, xRange.max, 0, plotWidth);
            if (Math.abs(x) > 1e-9) { // Don't label 0 on top of y-axis
                p.text(x.toFixed(1), screenX, yZero + 5);
            }
             p.stroke(200);
             p.line(screenX, yZero - 3, screenX, yZero + 3);
             p.noStroke();
        }

        p.textAlign(p.RIGHT, p.CENTER);
        const yTickStep = 0.5;
        for (let y = Math.ceil(yRange.min / yTickStep) * yTickStep; y <= yRange.max; y += yTickStep) {
            const screenY = p.map(y, yRange.min, yRange.max, plotHeight, 0);
             if (Math.abs(y) > 1e-9) {
                 p.text(y.toFixed(1), xZero - 5, screenY);
             }
             p.stroke(200);
             p.line(xZero - 3, screenY, xZero + 3, screenY);
             p.noStroke();
        }

        if (Math.abs(0) < 1e-9 && 0 >= xRange.min && 0 <= xRange.max && 0 >= yRange.min && 0 <= yRange.max) {
           p.text("0", xZero - 5, yZero + 10);
        }
    }

    function drawAnalogSignal(xRange, yRange) {
        const analogFunc = functions[selectedFunction];
        p.stroke(0, 0, 255);
        p.strokeWeight(2);
        p.noFill();

        p.beginShape();
        let lastScreenY = null;
        for (let i = 0; i <= plotWidth; i++) {
            const t = p.map(i, 0, plotWidth, xRange.min, xRange.max);
            const y = analogFunc(t);

            if (isFinite(y)) {
                const screenY = p.map(y, yRange.min, yRange.max, plotHeight, 0);
                // Handle discontinuities in tan function
                if (lastScreenY !== null && p.abs(screenY - lastScreenY) > plotHeight / 2) {
                    p.endShape();
                    p.beginShape();
                }
                p.vertex(i, screenY);
                lastScreenY = screenY;
            } else {
                 p.endShape();
                 p.beginShape();
                 lastScreenY = null;
            }
        }
        p.endShape();
    }

    function drawEncodedSignal(xRange, yRange) {
        if (selectedEncoding === 'asdm') {
            drawASDM(xRange, yRange);
        } else if (selectedEncoding === 'iaf') {
            drawIAF(xRange, yRange);
        }
    }

    function drawASDM(xRange, yRange) {
        // --- Simulation ---
        const B = 0.25;
        const delta = 0.05;
        const dt = (xRange.max - xRange.min) / 5000;
        const analogFunc = functions[selectedFunction];

        let integral = 0;
        let outputLevel = B;
        const flips = [{ t: xRange.min, level: outputLevel }];

        for (let t = xRange.min; t < xRange.max; t += dt) {
            integral += (analogFunc(t) - outputLevel) * dt;

            if ((outputLevel === B && integral < -delta) || (outputLevel === -B && integral > delta)) {
                outputLevel *= -1;
                integral = 0;
                flips.push({ t: t, level: outputLevel });
            }
        }
        flips.push({ t: xRange.max, level: flips[flips.length-1].level });

        // --- Drawing ---
        p.stroke(255, 0, 0);
        p.strokeWeight(2);
        p.noFill();

        for (let i = 0; i < flips.length - 1; i++) {
            const startT = flips[i].t;
            const endT = flips[i + 1].t;
            const level = (i === 0) ? flips[i+1].level * -1 : flips[i].level * -1; // Level before the flip

            const screenX1 = p.map(startT, xRange.min, xRange.max, 0, plotWidth);
            const screenX2 = p.map(endT, xRange.min, xRange.max, 0, plotWidth);
            const screenY1 = p.map(level, yRange.min, yRange.max, plotHeight, 0);
            const screenY2 = p.map(flips[i+1].level, yRange.min, yRange.max, plotHeight, 0);

            // Horizontal line
            p.line(screenX1, screenY1, screenX2, screenY1);
            // Vertical line at flip time
            p.line(screenX2, screenY1, screenX2, screenY2);
        }
    }

    function drawIAF(xRange, yRange) {
        // --- Simulation ---
        const theta = 0.05;
        const dt = (xRange.max - xRange.min) / 5000;
        const analogFunc = functions[selectedFunction];
        const spikeY = 0.3;

        let integral = 0;
        const spikeTimes = [];

        for (let t = xRange.min; t < xRange.max; t += dt) {
            integral += analogFunc(t) * dt;
            if (integral >= theta) {
                spikeTimes.push(t);
                integral = 0;
            }
        }

        // --- Drawing ---
        const yZero = p.map(0, yRange.min, yRange.max, plotHeight, 0);
        const ySpike = p.map(spikeY, yRange.min, yRange.max, plotHeight, 0);

        p.stroke(255, 0, 0);
        p.strokeWeight(1);

        for (const t of spikeTimes) {
            const screenX = p.map(t, xRange.min, xRange.max, 0, plotWidth);
            // Draw vertical line
            p.line(screenX, yZero, screenX, ySpike);
            // Draw dot
            p.fill(255, 0, 0);
            p.noStroke();
            p.circle(screenX, ySpike, 6);
            p.stroke(255, 0, 0); // Reset stroke for next line
            p.strokeWeight(1);
        }
    }
};

new p5(sketch, 'main-container');
</script>

</body>
</html>
