    <script src="https://unpkg.com/three@0.128.0/build/three.min.js"></script>
    <script src="https://unpkg.com/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // --- State Variables ---
        let simState = {
            time: 0,
            isPlaying: false,
            plotPoints: 40,
            pWave: {
                type: 'sine',
                amp: 0.1,
                wl: 2.0,
                speed: 1.5,
            },
            sWave: {
                type: 'sine',
                amp: 0.1,
                wl: 2.5,
                dir: 'xy',
                speed: 1.0,
            },
            view: 'default',
        };

        // --- three.js setup ---
        let scene, renderer, perspectiveCamera, orthographicCamera, activeCamera;
        let gridMesh, wireframe, controls;
        let originalPositions;

        const vizContainer = document.getElementById('visualization-container');

        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color('#E0E0E0');

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(vizContainer.clientWidth, vizContainer.clientHeight);
            vizContainer.appendChild(renderer.domElement);

            // Cameras
            const aspect = vizContainer.clientWidth / vizContainer.clientHeight;
            perspectiveCamera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
            perspectiveCamera.position.set(10, 10, 10);
            perspectiveCamera.lookAt(0, 0, 0);

            const orthoSize = 7;
            orthographicCamera = new THREE.OrthographicCamera(-orthoSize * aspect, orthoSize * aspect, orthoSize, -orthoSize, 0.1, 100);
            orthographicCamera.position.set(0, 0, 10);
            orthographicCamera.lookAt(0, 0, 0);

            activeCamera = perspectiveCamera;

            // Lights
            scene.add(new THREE.AmbientLight(0xffffff, 0.6));
            const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight.position.set(5, 10, 7.5);
            scene.add(dirLight);

            // Controls
            controls = new THREE.OrbitControls(perspectiveCamera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.1;

            // Initial Grid
            createGrid();

            // Event Listeners
            setupEventListeners();

            // Start animation loop
            animate();
        }

        function createGrid() {
            if (gridMesh) scene.remove(gridMesh);
            if (wireframe) scene.remove(wireframe);

            const size = 10;
            const segments = simState.plotPoints;

            const geometry = new THREE.PlaneBufferGeometry(size, size, segments, segments);

            // Store original positions
            originalPositions = new Float32Array(geometry.attributes.position.array);

            // Enable vertex colors
            const material = new THREE.MeshLambertMaterial({ vertexColors: true });
            gridMesh = new THREE.Mesh(geometry, material);
            scene.add(gridMesh);

            // Wireframe
            const wireframeGeom = new THREE.WireframeGeometry(geometry);
            const wireframeMat = new THREE.LineBasicMaterial({ color: 0x556B2F, linewidth: 1, transparent: true, opacity: 0.75 });
            wireframe = new THREE.LineSegments(wireframeGeom, wireframeMat);
            scene.add(wireframe);

            updateGrid();
        }

        function waveFunction(type, u) {
            if (type === 'sine') {
                return Math.sin(2 * Math.PI * u);
            } else if (type === 'pulse') {
                const width = 2.0;
                return Math.exp(-Math.pow(u * width, 2));
            }
            return 0;
        }

        function updateGrid() {
            if (!gridMesh) return;

            const positions = gridMesh.geometry.attributes.position.array;
            const colors = new Float32Array(positions.length);

            const maxAmplitude = parseFloat(document.getElementById('p-wave-amplitude-slider').max) + parseFloat(document.getElementById('s-wave-amplitude-slider').max);

            for (let i = 0; i < positions.length; i += 3) {
                const ox = originalPositions[i];
                const oy = originalPositions[i + 1];

                const r = Math.sqrt(ox * ox + oy * oy);
                if (r === 0) {
                     positions[i] = ox;
                     positions[i + 1] = oy;
                     positions[i + 2] = 0;
                     continue;
                }

                // P-wave displacement (interpreted as vertical pressure wave)
                const p_u = (r / simState.pWave.wl) - simState.time * simState.pWave.speed;
                const p_disp_z = simState.pWave.amp * waveFunction(simState.pWave.type, p_u);

                // S-wave displacement
                let s_disp_x = 0;
                let s_disp_y = 0;
                let s_disp_z = 0;
                const s_u = (r / simState.sWave.wl) - simState.time * simState.sWave.speed;
                const s_mag = simState.sWave.amp * waveFunction(simState.sWave.type, s_u);

                if (simState.sWave.dir === 'z') {
                    s_disp_z = s_mag;
                } else if (simState.sWave.dir === 'xy') {
                    s_disp_x = s_mag * (-oy / r);
                    s_disp_y = s_mag * (ox / r);
                }

                // Total displacement
                positions[i] = ox + s_disp_x;
                positions[i + 1] = oy + s_disp_y;
                const final_z = p_disp_z + s_disp_z;
                positions[i + 2] = final_z;

                // Color mapping based on z-displacement
                const color_intensity = Math.max(-1, Math.min(1, final_z / (maxAmplitude * 0.75)));
                const color = new THREE.Color();
                if (color_intensity > 0) {
                    color.setRGB(1, 1 - color_intensity, 1 - color_intensity); // Red for positive
                } else {
                    color.setRGB(1 + color_intensity, 1 + color_intensity, 1); // Light blue for negative
                }
                colors[i] = color.r;
                colors[i+1] = color.g;
                colors[i+2] = color.b;
            }

            gridMesh.geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));
            gridMesh.geometry.attributes.position.needsUpdate = true;
            gridMesh.geometry.attributes.color.needsUpdate = true;
            gridMesh.geometry.computeVertexNormals();
        }

        function animate() {
            requestAnimationFrame(animate);

            if (simState.isPlaying) {
                simState.time += 0.01;
                const timeSlider = document.getElementById('time-slider');
                if (simState.time > timeSlider.max) {
                    simState.time = parseFloat(timeSlider.min);
                }
                timeSlider.value = simState.time;
            }

            updateGrid();

            if (simState.view === 'default') {
                controls.update();
            }

            renderer.render(scene, activeCamera);
        }

        function handleResize() {
            const width = vizContainer.clientWidth;
            const height = vizContainer.clientHeight;

            renderer.setSize(width, height);

            const aspect = width / height;
            perspectiveCamera.aspect = aspect;
            perspectiveCamera.updateProjectionMatrix();

            const orthoSize = 7;
            orthographicCamera.left = -orthoSize * aspect;
            orthographicCamera.right = orthoSize * aspect;
            orthographicCamera.top = orthoSize;
            orthographicCamera.bottom = -orthoSize;
            orthographicCamera.updateProjectionMatrix();
        }

        function setupEventListeners() {
            // Time controls
            const timeSlider = document.getElementById('time-slider');
            const playPauseButton = document.getElementById('play-pause-button');
            const resetButton = document.getElementById('reset-button');
            const stepButton = document.getElementById('step-button');

            timeSlider.addEventListener('input', (e) => {
                simState.time = parseFloat(e.target.value);
            });

            playPauseButton.addEventListener('click', () => {
                simState.isPlaying = !simState.isPlaying;
                playPauseButton.textContent = simState.isPlaying ? '❚❚' : '►';
            });

            resetButton.addEventListener('click', () => {
                simState.time = 0;
                timeSlider.value = 0;
                simState.isPlaying = false;
                playPauseButton.textContent = '►';
            });

            stepButton.addEventListener('click', () => {
                if (!simState.isPlaying) {
                    simState.time += parseFloat(timeSlider.step);
                    if (simState.time > timeSlider.max) simState.time = parseFloat(timeSlider.max);
                    timeSlider.value = simState.time;
                }
            });

            // Plot points
            document.getElementById('plot-points-slider').addEventListener('input', (e) => {
                simState.plotPoints = parseInt(e.target.value);
                createGrid();
            });

            // Wave controls
            const pWaveAmp = document.getElementById('p-wave-amplitude-slider');
            const pWaveWl = document.getElementById('p-wave-wavelength-slider');
            const sWaveAmp = document.getElementById('s-wave-amplitude-slider');
            const sWaveWl = document.getElementById('s-wave-wavelength-slider');

            pWaveAmp.addEventListener('input', (e) => simState.pWave.amp = parseFloat(e.target.value));
            pWaveWl.addEventListener('input', (e) => simState.pWave.wl = parseFloat(e.target.value));
            sWaveAmp.addEventListener('input', (e) => simState.sWave.amp = parseFloat(e.target.value));
            sWaveWl.addEventListener('input', (e) => simState.sWave.wl = parseFloat(e.target.value));

            // Wave type buttons
            const pWaveSine = document.getElementById('p-wave-sine-button');
            const pWavePulse = document.getElementById('p-wave-pulse-button');
            const sWaveSine = document.getElementById('s-wave-sine-button');
            const sWavePulse = document.getElementById('s-wave-pulse-button');

            pWaveSine.addEventListener('click', () => {
                simState.pWave.type = 'sine';
                pWaveSine.classList.add('active');
                pWavePulse.classList.remove('active');
            });
            pWavePulse.addEventListener('click', () => {
                simState.pWave.type = 'pulse';
                pWavePulse.classList.add('active');
                pWaveSine.classList.remove('active');
            });
            sWaveSine.addEventListener('click', () => {
                simState.sWave.type = 'sine';
                sWaveSine.classList.add('active');
                sWavePulse.classList.remove('active');
            });
            sWavePulse.addEventListener('click', () => {
                simState.sWave.type = 'pulse';
                sWavePulse.classList.add('active');
                sWaveSine.classList.remove('active');
            });

            // S-wave direction
            const sWaveDirXY = document.getElementById('s-wave-dir-xy-button');
            const sWaveDirZ = document.getElementById('s-wave-dir-z-button');

            sWaveDirXY.addEventListener('click', () => {
                simState.sWave.dir = 'xy';
                sWaveDirXY.classList.add('active');
                sWaveDirZ.classList.remove('active');
            });
            sWaveDirZ.addEventListener('click', () => {
                simState.sWave.dir = 'z';
                sWaveDirZ.classList.add('active');
                sWaveDirXY.classList.remove('active');
            });

            // Viewpoint
            const viewTop = document.getElementById('viewpoint-top-button');
            const viewDefault = document.getElementById('viewpoint-default-button');

            viewTop.addEventListener('click', () => {
                simState.view = 'top';
                activeCamera = orthographicCamera;
                controls.enabled = false;
                viewTop.classList.add('active');
                viewDefault.classList.remove('active');
            });
            viewDefault.addEventListener('click', () => {
                simState.view = 'default';
                activeCamera = perspectiveCamera;
                controls.enabled = true;
                viewDefault.classList.add('active');
                viewTop.classList.remove('active');
            });

            // Window resize
            window.addEventListener('resize', handleResize);
        }

        // --- Start everything ---
        init();

    </script>
</body>
</html>
