<!-- p5.js CDN script -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
    // State variables
    let a, b, c;

    // Geometric points
    let O, L1, L2, L3, L1_prime, L2_prime;

    // DOM Elements
    let sliderZ, labelZ;
    let sliderPlotRange, labelPlotRange;
    let checkShowLabels, checkShowCircle, checkShowAxes, checkShowGridLines;
    let btnNewExample, btnGoldenRatio;
    let formulaText, valuePz, valueL3L2, valueZ;
    let canvasContainer;

    // Control values
    let zValue, plotRange;
    let showLabels, showCircle, showAxes, showGridLines;

    const sketch = (p) => {
        p.setup = () => {
            canvasContainer = document.getElementById('canvas-container');
            const canvas = p.createCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvas.parent('canvas-container');
            p.noLoop();

            // Get DOM elements
            sliderZ = document.getElementById('slider-z');
            labelZ = document.getElementById('label-z');
            sliderPlotRange = document.getElementById('slider-plot-range');
            labelPlotRange = document.getElementById('label-plot-range');
            checkShowLabels = document.getElementById('check-show-labels');
            checkShowCircle = document.getElementById('check-show-circle');
            checkShowAxes = document.getElementById('check-show-axes');
            checkShowGridLines = document.getElementById('check-show-grid-lines');
            btnNewExample = document.getElementById('btn-new-example');
            btnGoldenRatio = document.getElementById('btn-golden-ratio');
            formulaText = document.getElementById('formula-text');
            valuePz = document.getElementById('value-pz');
            valueL3L2 = document.getElementById('value-l3l2');
            valueZ = document.getElementById('value-z');

            // Set up event listeners
            sliderZ.addEventListener('input', updateAndRedraw);
            sliderPlotRange.addEventListener('input', updateAndRedraw);
            checkShowLabels.addEventListener('change', updateAndRedraw);
            checkShowCircle.addEventListener('change', updateAndRedraw);
            checkShowAxes.addEventListener('change', updateAndRedraw);
            checkShowGridLines.addEventListener('change', updateAndRedraw);
            btnNewExample.addEventListener('click', loadNewExample);
            btnGoldenRatio.addEventListener('click', loadGoldenRatioExample);

            window.addEventListener('resize', () => {
                p.resizeCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
                updateAndRedraw();
            });

            // Initial setup
            loadGoldenRatioExample();
        };

        p.draw = () => {
            p.background(255);

            // Calculate view transform
            const allPoints = [O, L1, L2, L3];
            let minX = Infinity, maxX = -Infinity, minY = Infinity, maxY = -Infinity;
            allPoints.forEach(pt => {
                if (pt.x < minX) minX = pt.x;
                if (pt.x > maxX) maxX = pt.x;
                if (pt.y < minY) minY = pt.y;
                if (pt.y > maxY) maxY = pt.y;
            });

            const centerX = (minX + maxX) / 2;
            const centerY = (minY + maxY) / 2;

            p.translate(p.width / 2, p.height / 2);
            const scaleFactor = p.min(p.width, p.height) / (2 * plotRange);
            p.scale(scaleFactor, -scaleFactor); // Zoom and flip Y-axis
            p.translate(-centerX, -centerY); // Pan to center geometry

            const strokeW = 1 / scaleFactor;

            if (showGridLines) drawGrid(strokeW);
            if (showAxes) drawAxes(strokeW);

            drawCoefficientPath(strokeW);
            drawTestPath(strokeW);

            if (showCircle) drawCircle(strokeW);
            if (showLabels) drawLabels(strokeW * 16);
        };

        function drawGrid(strokeW) {
            p.stroke(220);
            p.strokeWeight(strokeW);
            const step = Math.pow(10, Math.floor(Math.log10(plotRange)));

            const viewBounds = {
                left: -plotRange + p.width / (2 * p.getURL())[0],
                right: plotRange + p.width / (2 * p.getURL())[0],
                bottom: -plotRange - p.height / (2 * p.getURL())[0],
                top: plotRange - p.height / (2 * p.getURL())[0]
            };
            const transform = p.drawingContext.getTransform();
            const inv = transform.inverse();
            const topLeft = inv.transformPoint({x: 0, y: 0});
            const bottomRight = inv.transformPoint({x: p.width, y: p.height});

            for (let x = Math.floor(topLeft.x / step) * step; x < bottomRight.x; x += step) {
                p.line(x, topLeft.y, x, bottomRight.y);
            }
            for (let y = Math.floor(topLeft.y / step) * step; y < bottomRight.y; y += step) {
                p.line(topLeft.x, y, bottomRight.x, y);
            }
        }

        function drawAxes(strokeW) {
            p.stroke(0);
            p.strokeWeight(2 * strokeW);

            const transform = p.drawingContext.getTransform();
            const inv = transform.inverse();
            const topLeft = inv.transformPoint({x: 0, y: 0});
            const bottomRight = inv.transformPoint({x: p.width, y: p.height});

            // Y-axis
            p.line(0, topLeft.y, 0, bottomRight.y);
            // X-axis
            p.line(topLeft.x, 0, bottomRight.x, 0);

            // Ticks and labels
            p.noStroke();
            p.fill(0);
            p.textAlign(p.CENTER, p.TOP);
            p.scale(1, -1); // Flip text back
            const textSize = 12 * strokeW;
            p.textSize(textSize);

            const step = Math.max(1, Math.round(plotRange / 4));
            for (let i = Math.floor(topLeft.x / step) * step; i < bottomRight.x; i += step) {
                if (i !== 0) {
                   p.text(i, i * scaleFactor, -5 * strokeW * scaleFactor);
                   p.stroke(0); p.strokeWeight(2*strokeW);
                   p.line(i, 0.1, i, -0.1);
                   p.noStroke();
                }
            }
            p.textAlign(p.RIGHT, p.CENTER);
            for (let i = Math.floor(topLeft.y / step) * step; i < bottomRight.y; i += step) {
                 if (i !== 0) {
                    p.text(i, -5 * strokeW * scaleFactor, -i * scaleFactor);
                    p.stroke(0); p.strokeWeight(2*strokeW);
                    p.line(0.1, i, -0.1, i);
                    p.noStroke();
                 }
            }
            p.scale(1, -1); // Flip back
        }

        function drawCoefficientPath(strokeW) {
            // Path O -> L1 -> L2 -> L3
            p.stroke(0);
            p.strokeWeight(2 * strokeW);
            p.noFill();
            p.beginShape();
            p.vertex(O.x, O.y);
            p.vertex(L1.x, L1.y);
            p.vertex(L2.x, L2.y);
            p.vertex(L3.x, L3.y);
            p.endShape();

            // Points
            p.fill(0);
            p.noStroke();
            const pointSize = 8 * strokeW;
            p.ellipse(O.x, O.y, pointSize, pointSize);
            p.ellipse(L1.x, L1.y, pointSize, pointSize);
            p.ellipse(L2.x, L2.y, pointSize, pointSize);
            p.ellipse(L3.x, L3.y, pointSize, pointSize);
        }

        function drawTestPath(strokeW) {
            // Path O -> L1' -> L2'
            p.strokeWeight(2 * strokeW);
            p.noFill();

            // O -> L1'
            p.stroke(0);
            p.line(O.x, O.y, L1_prime.x, L1_prime.y);

            // L1' -> L2'
            p.stroke(255, 0, 255); // Magenta
            p.line(L1_prime.x, L1_prime.y, L2_prime.x, L2_prime.y);

            // Error segment L2' -> L3
            p.stroke(255, 0, 0); // Red
            p.line(L2_prime.x, L2_prime.y, L3.x, L3.y);

            // Points
            p.fill(0);
            p.noStroke();
            const pointSize = 8 * strokeW;
            p.ellipse(L1_prime.x, L1_prime.y, pointSize, pointSize);
            p.ellipse(L2_prime.x, L2_prime.y, pointSize, pointSize);
        }

        function drawCircle(strokeW) {
            const centerX = (L1.x - c) / 2;
            const centerY = L2.y / 2;
            const diameter = p.dist(L1.x - c, L2.y, 0, 0);

            p.noFill();
            p.stroke(0);
            p.strokeWeight(1.5 * strokeW);
            p.ellipse(centerX, centerY, diameter, diameter);
        }

        function drawLabels(textSize) {
            p.noStroke();
            p.fill(0);
            p.textSize(textSize);
            p.textAlign(p.CENTER, p.BOTTOM);

            p.scale(1, -1); // Flip text back
            const offset = textSize / scaleFactor;

            p.text("O", O.x, -O.y - offset);
            p.text("L₁", L1.x, -L1.y - offset);
            p.text("L₂", L2.x, -L2.y + offset*2.5);
            p.text("L₃", L3.x, -L3.y + offset*2.5);
            p.text("L'₁", L1_prime.x, -L1_prime.y - offset);
            p.text("L'₂", L2_prime.x, -L2_prime.y + offset*2.5);

            // z label
            if (zValue !== 0) {
                const midZ = { x: (L1.x + L1_prime.x) / 2, y: (L1.y + L1_prime.y) / 2 };
                p.push();
                p.translate(midZ.x, -midZ.y);
                p.rotate(p.PI/2);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text("z", 0, offset*2);
                p.pop();
            }

            p.scale(1, -1); // Flip back
        }
    };

    function updateAndRedraw() {
        // Read values from controls
        zValue = parseFloat(sliderZ.value);
        plotRange = parseFloat(sliderPlotRange.value);
        showLabels = checkShowLabels.checked;
        showCircle = checkShowCircle.checked;
        showAxes = checkShowAxes.checked;
        showGridLines = checkShowGridLines.checked;

        // Update labels
        labelZ.textContent = zValue.toFixed(2);
        labelPlotRange.textContent = plotRange.toFixed(1);

        // Recalculate geometry and values
        calculateGeometry();
        updateTable();

        // Redraw canvas
        if(window.p5Instance) {
            window.p5Instance.redraw();
        }
    }

    function calculateGeometry() {
        O = { x: 0, y: 0 };
        L1 = { x: a, y: 0 };
        L2 = { x: a, y: b };
        L3 = { x: a - c, y: b };

        L1_prime = { x: a, y: -zValue * a };
        L2_prime = { x: a + b * zValue + a * zValue * zValue, y: b };
    }

    function updateTable() {
        const pz = a * zValue * zValue + b * zValue + c;
        const l3l2_dist = Math.abs(L3.x - L2_prime.x);

        valuePz.textContent = pz.toFixed(4);
        valueL3L2.textContent = l3l2_dist.toFixed(4);
        valueZ.textContent = zValue.toFixed(4);
    }

    function updateFormulaText() {
        let formula = '';

        // a term
        if (a === 1) formula += 'z^2';
        else if (a === -1) formula += '-z^2';
        else if (a !== 0) formula += `${a}z^2`;

        // b term
        if (b !== 0) {
            if (b > 0 && formula) formula += ' + ';
            else if (b < 0) formula += ' - ';

            if (Math.abs(b) === 1) formula += 'z';
            else formula += `${Math.abs(b)}z`;
        }

        // c term
        if (c !== 0) {
            if (c > 0 && formula) formula += ' + ';
            else if (c < 0) formula += ' - ';

            formula += `${Math.abs(c)}`;
        }

        formulaText.innerHTML = formula; // use innerHTML because MathJax will render it
        if (window.MathJax && MathJax.typesetPromise) {
            MathJax.typesetPromise(['#formula-display']);
        }
    }

    function loadNewExample() {
        a = Math.floor(Math.random() * 3) + 1; // 1, 2, 3
        if (Math.random() < 0.5) a = -a;
        b = Math.floor(Math.random() * 11) - 5; // -5 to 5
        c = Math.floor(Math.random() * 11) - 5; // -5 to 5

        updateFormulaText();
        updateAndRedraw();
    }

    function loadGoldenRatioExample() {
        a = 1;
        b = -1;
        c = -1;

        updateFormulaText();
        updateAndRedraw();
    }

    // Initialize p5 sketch after the DOM is loaded
    document.addEventListener('DOMContentLoaded', (event) => {
        window.p5Instance = new p5(sketch);
    });
</script>

</body>
</html>
