document.addEventListener('DOMContentLoaded', () => {

    // --- STATE ---
    let currentView = 'plot_solutions'; // 'plot_solutions', 'table', 'plot_turning_points'
    let alphaValue = 0.039;
    let p5_instance;

    // --- DATA ---
    // Pre-calculated data for the solution curve
    const solutionCurveData = [
        {alpha: 0.0000, x1: 0.999, x2: 0.998, t1: 8.00, t2: 0.000},
        {alpha: 0.0100, x1: 0.995, x2: 0.991, t1: 7.80, t2: 0.150},
