        {alpha: 0.0300, x1: 0.985, x2: 0.980, t1: 7.20, t2: 0.650},
        {alpha: 0.0400, x1: 0.980, x2: 0.975, t1: 6.90, t2: 1.050},
        {alpha: 0.0480, x1: 0.970, x2: 0.965, t1: 6.50, t2: 1.450},
        {alpha: 0.0503, x1: 0.965, x2: 0.960, t1: 6.30, t2: 1.620},
        {alpha: 0.0485, x1: 0.950, x2: 0.940, t1: 5.90, t2: 1.800},
        {alpha: 0.0350, x1: 0.900, x2: 0.880, t1: 5.00, t2: 2.000},
        {alpha: 0.0200, x1: 0.850, x2: 0.830, t1: 4.50, t2: 2.150},
        {alpha: 0.0125, x1: 0.800, x2: 0.800, t1: 4.00, t2: 2.220},
        {alpha: 0.0250, x1: 0.750, x2: 0.780, t1: 3.50, t2: 2.300},
        {alpha: 0.0350, x1: 0.650, x2: 0.800, t1: 3.00, t2: 3.000},
        {alpha: 0.0402, x1: 0.500, x2: 0.850, t1: 2.80, t2: 4.000},
        {alpha: 0.0380, x1: 0.350, x2: 0.870, t1: 2.65, t2: 4.700},
