        {alpha: 0.0120, x1: 0.100, x2: 0.950, t1: 0.80, t2: 6.000},
        {alpha: 0.0250, x1: 0.080, x2: 0.960, t1: 0.50, t2: 6.300},
        {alpha: 0.0400, x1: 0.070, x2: 0.970, t1: 0.45, t2: 6.750},
        {alpha: 0.0500, x1: 0.065, x2: 0.972, t1: 0.43, t2: 6.900},
        {alpha: 0.0583, x1: 0.060, x2: 0.974, t1: 0.42, t2: 6.400},
        {alpha: 0.0550, x1: 0.058, x2: 0.975, t1: 0.41, t2: 5.500},
        {alpha: 0.0450, x1: 0.057, x2: 0.975, t1: 0.40, t2: 4.500},
        {alpha: 0.0410, x1: 0.360, x2: 0.870, t1: 2.66, t2: 4.400},
        {alpha: 0.0425, x1: 0.362, x2: 0.865, t1: 2.67, t2: 3.500},
        {alpha: 0.0435, x1: 0.363, x2: 0.680, t1: 2.68, t2: 2.800},
        {alpha: 0.0422, x1: 0.364, x2: 0.480, t1: 2.70, t2: 2.200},
        {alpha: 0.0550, x1: 0.365, x2: 0.350, t1: 2.72, t2: 2.400},
        {alpha: 0.0700, x1: 0.366, x2: 0.340, t1: 2.73, t2: 2.450}
    ];

    const turningPointsData = [
        {alpha: 0.0503, t2: 1.62},
        {alpha: 0.0125, t2: 2.22},
        {alpha: 0.0402, t2: 4.00},
        {alpha: 0.0120, t2: 6.00},
        {alpha: 0.0583, t2: 6.40},
        {alpha: 0.0435, t2: 2.80}
    ];

    // --- DOM ELEMENTS ---
    const btnPlot = document.getElementById('btn-plot');
    const btnTable = document.getElementById('btn-table');
    const btnTurningPoints = document.getElementById('btn-turning-points');
    const viewButtons = [btnPlot, btnTable, btnTurningPoints];

    const sliderAlpha = document.getElementById('slider-alpha');
    const alphaValueDisplay = document.getElementById('alpha-value-display');

    const parameterControls = document.getElementById('parameter-controls');
    const plotView = document.getElementById('plot-view');
    const tableView = document.getElementById('table-view');
    const solutionsTableBody = document.getElementById('solutions-table-body');

    // --- UTILITY FUNCTIONS ---

    function findIntersections(targetAlpha) {
        const intersections = [];
        for (let i = 0; i < solutionCurveData.length - 1; i++) {
            const p1 = solutionCurveData[i];
            const p2 = solutionCurveData[i + 1];

            if ((p1.alpha <= targetAlpha && p2.alpha >= targetAlpha) || (p1.alpha >= targetAlpha && p2.alpha <= targetAlpha)) {
                if (p1.alpha === p2.alpha) { // Vertical segment, should not happen with this data
                    if (p1.alpha === targetAlpha) intersections.push(p1);
                    continue;
                }

                const t = (targetAlpha - p1.alpha) / (p2.alpha - p1.alpha);

                const intersection = {
                    alpha: targetAlpha,
                    x1: p1.x1 + t * (p2.x1 - p1.x1),
                    x2: p1.x2 + t * (p2.x2 - p1.x2),
                    t1: p1.t1 + t * (p2.t1 - p1.t1),
                    t2: p1.t2 + t * (p2.t2 - p1.t2),
                };
                intersections.push(intersection);
            }
        }
        return intersections.sort((a,b) => a.t2 - b.t2); // Sort by T2 value
    }

    function updateTable() {
        const solutions = findIntersections(alphaValue);
        solutionsTableBody.innerHTML = ''; // Clear previous results

        solutions.forEach((sol, index) => {
            const row = document.createElement('tr');
            row.innerHTML = `
                <td>${index + 1}</td>
                <td>${sol.x1.toFixed(5)}</td>
                <td>${sol.x2.toFixed(5)}</td>
                <td>${sol.t1.toFixed(5)}</td>
                <td>${sol.t2.toFixed(5)}</td>
            `;
            solutionsTableBody.appendChild(row);
        });
    }

    function updateDisplay() {
        if (currentView === 'table') {
            updateTable();
        } else {
             if (p5_instance) {
                p5_instance.redraw();
            }
        }
    }


    // --- EVENT HANDLERS ---

    function switchView(viewName, clickedButton) {
        currentView = viewName;

        viewButtons.forEach(btn => btn.classList.remove('active'));
        clickedButton.classList.add('active');

        if (currentView === 'plot_solutions') {
            plotView.style.display = 'block';
            tableView.style.display = 'none';
            parameterControls.style.display = 'flex';
        } else if (currentView === 'table') {
            plotView.style.display = 'none';
            tableView.style.display = 'block';
            parameterControls.style.display = 'flex';
        } else if (currentView === 'plot_turning_points') {
            plotView.style.display = 'block';
            tableView.style.display = 'none';
            parameterControls.style.display = 'none';
        }
        updateDisplay();
    }

    btnPlot.addEventListener('click', () => switchView('plot_solutions', btnPlot));
    btnTable.addEventListener('click', () => switchView('table', btnTable));
    btnTurningPoints.addEventListener('click', () => switchView('plot_turning_points', btnTurningPoints));

    sliderAlpha.addEventListener('input', (e) => {
        alphaValue = parseFloat(e.target.value);
        alphaValueDisplay.textContent = alphaValue.toFixed(4);
        updateDisplay();
    });

    // --- P5.JS SKETCH ---

    const sketch = (p) => {
        const canvasWidth = 600;
        const canvasHeight = 600;
        const margin = { top: 40, right: 40, bottom: 60, left: 60 };
        const plotWidth = canvasWidth - margin.left - margin.right;
        const plotHeight = canvasHeight - margin.top - margin.bottom;

        const alphaMin = 0.0, alphaMax = 0.07;
        const t2Min = 0.0, t2Max = 8.0;

        p.setup = () => {
            const canvas = p.createCanvas(canvasWidth, canvasHeight);
            canvas.parent('plot-view');
            p.noLoop();
        };

        p.draw = () => {
            p.background(255);

            drawGrid();
            drawAxes();
            drawCurve();

            if (currentView === 'plot_solutions') {
                drawVerticalLineAndPoints();
            } else if (currentView === 'plot_turning_points') {
                drawTurningPoints();
            }
        };

        function mapAlphaToX(a) {
            return p.map(a, alphaMin, alphaMax, margin.left, canvasWidth - margin.right);
        }

        function mapT2ToY(t) {
            return p.map(t, t2Min, t2Max, canvasHeight - margin.bottom, margin.top);
        }

        function drawDashedLine(x1, y1, x2, y2, dashLen) {
            p.push();
            const d = p.dist(x1, y1, x2, y2);
            const dashCount = Math.floor(d / dashLen);
            const newDashLen = d / dashCount;
            for (let i = 0; i < dashCount; i += 2) {
                let start = i * newDashLen;
                let end = start + newDashLen;
                p.line(p.lerp(x1, x2, start / d), p.lerp(y1, y2, start / d), p.lerp(x1, x2, end / d), p.lerp(y1, y2, end / d));
            }
            p.pop();
        }

        function drawGrid() {
            p.stroke(220);
            p.strokeWeight(1);
            // Vertical grid lines
            for (let a = alphaMin; a <= alphaMax; a += 0.01) {
                let x = mapAlphaToX(a);
                p.line(x, margin.top, x, canvasHeight - margin.bottom);
            }
            // Horizontal grid lines
            for (let t = t2Min; t <= t2Max; t += 1) {
                let y = mapT2ToY(t);
                p.line(margin.left, y, canvasWidth - margin.right, y);
            }
        }

        function drawAxes() {
            p.stroke(0);
            p.strokeWeight(1.5);
            p.fill(0);
            p.textAlign(p.CENTER, p.CENTER);

            // X-axis
            p.line(margin.left, canvasHeight - margin.bottom, canvasWidth - margin.right, canvasHeight - margin.bottom);
            for (let a = alphaMin; a <= alphaMax; a += 0.01) {
                let x = mapAlphaToX(a);
                p.line(x, canvasHeight - margin.bottom, x, canvasHeight - margin.bottom + 5);
                p.noStroke();
                p.text(a.toFixed(2), x, canvasHeight - margin.bottom + 20);
                p.stroke(0);
            }
            p.noStroke();
            p.text('α', canvasWidth / 2, canvasHeight - margin.bottom + 40);

            // Y-axis
            p.line(margin.left, margin.top, margin.left, canvasHeight - margin.bottom);
            for (let t = t2Min; t <= t2Max; t += 1) {
                let y = mapT2ToY(t);
                p.line(margin.left, y, margin.left - 5, y);
                p.noStroke();
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(t, margin.left - 10, y);
                p.stroke(0);
            }
            p.noStroke();
            p.push();
            p.translate(margin.left - 40, canvasHeight / 2);
            p.rotate(-p.HALF_PI);
            p.textAlign(p.CENTER, p.CENTER);
            p.text('T₂(α)', 0, 0);
            p.pop();
        }

        function drawCurve() {
            p.stroke(255, 0, 0); // Red
            p.strokeWeight(2);
            p.noFill();
            p.beginShape();
            solutionCurveData.forEach(point => {
                p.vertex(mapAlphaToX(point.alpha), mapT2ToY(point.t2));
            });
            p.endShape();
        }

        function drawVerticalLineAndPoints() {
            const x = mapAlphaToX(alphaValue);

            // Dashed green line
            p.stroke(0, 180, 0);
            p.strokeWeight(2);
            drawDashedLine(x, margin.top, x, canvasHeight - margin.bottom, 8);

            // Blue intersection points
            const solutions = findIntersections(alphaValue);
            p.noStroke();
            p.fill(0, 0, 255); // Blue
            solutions.forEach(sol => {
                const y = mapT2ToY(sol.t2);
                p.ellipse(x, y, 10, 10);
            });
        }

        function drawTurningPoints() {
            p.noStroke();
            p.fill(0, 255, 0); // Green
            turningPointsData.forEach(point => {
                const x = mapAlphaToX(point.alpha);
                const y = mapT2ToY(point.t2);
                p.ellipse(x, y, 12, 12);
            });
        }
    };

    // --- INITIALIZATION ---
    alphaValueDisplay.textContent = parseFloat(sliderAlpha.value).toFixed(4);
    p5_instance = new p5(sketch);
    updateDisplay(); // Initial draw/table population
});
</script>

</body>
