        ];

        let sliderGrowth, displayGrowth;

        class Complex {
            constructor(re = 0, im = 0) { this.re = re; this.im = im; }
            static fromP5Vector(v) { return new Complex(v.x, v.y); }
            static add(c1, c2) { return new Complex(c1.re + c2.re, c1.im + c2.im); }
            static sub(c1, c2) { return new Complex(c1.re - c2.re, c1.im - c2.im); }
            static mult(c1, c2) { return new Complex(c1.re * c2.re - c1.im * c2.im, c1.re * c2.im + c1.im * c2.re); }
            static div(c1, c2) {
                const den = c2.re * c2.re + c2.im * c2.im;
                if (den === 0) return new Complex(Infinity, Infinity);
                const re = (c1.re * c2.re + c1.im * c2.im) / den;
                const im = (c1.im * c2.re - c1.re * c2.im) / den;
                return new Complex(re, im);
            }
            static exp(c) {
                const expRe = Math.exp(c.re);
                return new Complex(expRe * Math.cos(c.im), expRe * Math.sin(c.im));
