                btn.addEventListener('click', () => {
                    itemType = btn.id.split('-').slice(2).join('-');
                    updateActiveButtons();
                });
            });

            updateActiveButtons();
        };

        function updateActiveButtons() {
            document.querySelectorAll('#type-controls button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-type-${spiralType}`).classList.add('active');

            document.querySelectorAll('#item-controls button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-item-${itemType}`).classList.add('active');
        }

        p.draw = function() {
            p.background(255);
            p.translate(p.width / 2, p.height / 2);

            if (spiralType === 'single') {
                drawSingleSpiral();
                if(itemType !== 'filled-fox' && itemType !== 'fox') {
                   drawLocator(z0_loc, p.color(255, 255, 0, 200));
                }
                const z0LabelPos = Complex.fromP5Vector(z0_loc);
                p.fill(0); p.noStroke(); p.textAlign(p.CENTER, p.CENTER); p.textSize(16);
                p.text("z0Loc", z0LabelPos.re, z0LabelPos.im - 20);

            } else {
                drawDoubleSpiral();
                drawLocator(z_plus_loc, p.color(255, 255, 0, 200));
                drawLocator(z_minus_loc, p.color(0, 255, 0, 200));
            }
        };

        function drawSingleSpiral() {
            const k = spiralGrowth;
            const z0 = Complex.fromP5Vector(z0_loc);
            const curvePoints = [];

            p.noFill(); p.stroke('#4A6DA8'); p.strokeWeight(2);
            p.beginShape();
            const tMax = 4 * p.PI;
            const tStep = 0.05;
            for (let t = -tMax; t <= tMax; t += tStep) {
                const exp_term = Complex.exp(new Complex(k * t, t));
                const z = Complex.add(z0, exp_term);
                p.curveVertex(z.re, z.im);
                curvePoints.push({ pos: z, t: t });
            }
            p.endShape();
            drawItemsOnCurve(curvePoints, false);
        }

        function drawDoubleSpiral() {
            const k = spiralGrowth;
            const P = Complex.fromP5Vector(z_plus_loc);
            const Q = Complex.fromP5Vector(z_minus_loc);

            const z0LocLabelPos = Complex.div(Complex.add(P, Q), new Complex(2, 0));
            p.fill(0); p.noStroke(); p.textAlign(p.CENTER, p.TOP); p.textSize(16);
            p.text("z0Loc", z0LocLabelPos.re, z0LocLabelPos.im + 15);

            const drawBranch = (startT, endT) => {
                const curvePoints = [];
                p.noFill(); p.stroke('#4A6DA8'); p.strokeWeight(2);
                p.beginShape();
                const tStep = 0.05;
                for (let t = startT; t <= endT; t += tStep) {
                     const w = Complex.exp(new Complex(k * t, t));
                     const num = Complex.sub(Complex.mult(P, w), Q);
                     const den = Complex.sub(w, new Complex(1, 0));
                     if (den.magSq() < 1e-6) continue;
                     const z = Complex.div(num, den);
                     if (z.magSq() > 4 * p.width * p.height) continue;
                     p.curveVertex(z.re, z.im);
                     curvePoints.push({pos: z, t: t});
                }
                p.endShape();
                return curvePoints;
            };

            const tMax = 8 * p.PI;
            const epsilon = 0.01;
            const points1 = drawBranch(epsilon, tMax);
            const points2 = drawBranch(-tMax, -epsilon);

            drawItemsOnCurve(points1, true);
            drawItemsOnCurve(points2, true);
        }

        function drawItemsOnCurve(points, isDouble) {
            if (itemType === 'none' || points.length < 2) return;

            const k = spiralGrowth;
            const P = Complex.fromP5Vector(z_plus_loc);
            const Q = Complex.fromP5Vector(z_minus_loc);
            const itemStep = p.map(k, 0.01, 0.5, 12, 4);

            for (let i = 0; i < points.length; i += Math.max(1, Math.floor(itemStep))) {
                const pt = points[i];

                if (itemType === 'point') {
                    p.fill('#CB3335'); p.noStroke();
                    p.circle(pt.pos.re, pt.pos.im, 5);
                } else if (itemType === 'fox' || itemType === 'filled-fox') {
                    let tangent;
                    if (!isDouble) {
                        tangent = Complex.mult(new Complex(k, 1), Complex.exp(new Complex(k * pt.t, pt.t)));
                    } else {
                        const w = Complex.exp(new Complex(k * pt.t, pt.t));
                        const w_minus_1 = Complex.sub(w, new Complex(1, 0));
                        if(w_minus_1.magSq() < 1e-6) continue;
                        const num = Complex.mult(Complex.sub(P, Q), Complex.mult(new Complex(k, 1), w));
                        const den = Complex.mult(w_minus_1, w_minus_1);
                        tangent = Complex.div(num, den);
                    }

                    const angle = tangent.angle();
                    p.push();
                    p.translate(pt.pos.re, pt.pos.im);
                    p.rotate(angle);

                    let scaleFactor = isDouble ? 0.35 : 0.6;
                    scaleFactor *= p.map(k, 0.01, 0.5, 1.2, 0.6);
                    p.scale(scaleFactor);

                    if (itemType === 'fox') {
                        p.noFill(); p.stroke('#D6292D'); p.strokeWeight(1.5 / scaleFactor);
                    } else {
                        p.fill('#D6292D'); p.noStroke();
                    }
                    drawFox();
                    p.pop();
                }
            }
        }

        function drawFox() {
            p.beginShape();
            for (const v of fox_vertices) { p.vertex(v.x, v.y); }
            p.endShape(p.CLOSE);
        }

        function drawLocator(pos, col) {
            p.push();
            p.stroke(0); p.strokeWeight(1.5); p.fill(col);
            p.circle(pos.x, pos.y, locatorRadius * 2);
            p.line(pos.x - locatorRadius, pos.y, pos.x + locatorRadius, pos.y);
            p.line(pos.x, pos.y - locatorRadius, pos.x, pos.y + locatorRadius);
            p.pop();
        }

        function getMouseWorldCoords() {
            return p.createVector(p.mouseX - p.width / 2, p.mouseY - p.height / 2);
        }

        p.mousePressed = function() {
            if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;

            const mousePos = getMouseWorldCoords();

            if (spiralType === 'single') {
                if (p.dist(mousePos.x, mousePos.y, z0_loc.x, z0_loc.y) < locatorRadius) {
                    isDraggingZ0 = true;
                }
            } else {
                if (p.dist(mousePos.x, mousePos.y, z_plus_loc.x, z_plus_loc.y) < locatorRadius) {
                    isDraggingZPlus = true;
                }
                if (p.dist(mousePos.x, mousePos.y, z_minus_loc.x, z_minus_loc.y) < locatorRadius) {
                    isDraggingZMinus = true;
                }
            }
        };

        p.mouseDragged = function() {
            const mousePos = getMouseWorldCoords();
            if (isDraggingZ0) z0_loc.set(mousePos);
            if (isDraggingZPlus) z_plus_loc.set(mousePos);
            if (isDraggingZMinus) z_minus_loc.set(mousePos);
        };

        p.mouseReleased = function() {
            isDraggingZ0 = false;
            isDraggingZPlus = false;
            isDraggingZMinus = false;
        };
    };

    new p5(p5_sketch);
});
</script>

</body>
</html>
