                <button id="btn-m">m</button><button id="btn-n">n</button><button id="btn-o">o</button><button id="btn-p">p</button>
                <button id="btn-i">i</button><button id="btn-j">j</button><button id="btn-k">k</button><button id="btn-l">l</button>
                <button id="btn-e">e</button><button id="btn-f">f</button><button id="btn-g">g</button><button id="btn-h">h</button>
                <button id="btn-a">a</button><button id="btn-b">b</button><button id="btn-c">c</button><button id="btn-d">d</button>
            </div>

            <label class="control-section-label">example type</label>
            <button id="fixed-example-btn">fixed example</button>
            <button id="random-example-btn">random example</button>

            <label class="control-section-label">implement algorithm</label>
            <button id="next-step-btn">next step</button>
        </div>
        <div id="viz-container">
            <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
            <div id="canvas-container"></div>
        </div>
    </div>

<script>
document.addEventListener('DOMContentLoaded', () => {

    // --- DOM Elements ---
    const startOverBtn = document.getElementById('start-over-btn');
    const titleDisplay = document.getElementById('title-display');
    const fixedExampleBtn = document.getElementById('fixed-example-btn');
    const randomExampleBtn = document.getElementById('random-example-btn');
    const nextStepBtn = document.getElementById('next-step-btn');
    const vertexGrid = document.getElementById('vertex-grid');
    const vertexButtons = {};
    const vertexChars = ['m','n','o','p','i','j','k','l','e','f','g','h','a','b','c','d'];
    vertexChars.forEach(char => {
        vertexButtons[char] = document.getElementById(`btn-${char}`);
    });

    // --- Graph Data ---
    const vertices = {};
    const edges = [];

    // Setup vertices in a grid
    const cols = ['m', 'n', 'o', 'p'];
    const rows = ['m', 'i', 'e', 'a'];
    const charGrid = [
        ['m','n','o','p'],
        ['i','j','k','l'],
        ['e','f','g','h'],
        ['a','b','c','d']
    ];
    for (let r = 0; r < 4; r++) {
        for (let c = 0; c < 4; c++) {
            const name = charGrid[r][c];
            vertices[name] = {
                name,
                x: 100 + c * 133,
                y: 100 + r * 133
            };
        }
    }

    // Setup edges
    for (let r = 0; r < 4; r++) {
        for (let c = 0; c < 4; c++) {
            const current = charGrid[r][c];
            // Horizontal edge
            if (c < 3) {
                const right = charGrid[r][c+1];
                edges.push({ v1: current, v2: right });
            }
            // Vertical edge
            if (r < 3) {
                const down = charGrid[r+1][c];
                edges.push({ v1: current, v2: down });
            }
        }
    }

    const fixedWeights = {
        'm-n': 8, 'n-o': 7, 'o-p': 7,
        'i-j': 9, 'j-k': 7, 'k-l': 1,
        'e-f': 1, 'f-g': 7, 'g-h': 6,
        'a-b': 10, 'b-c': 4, 'c-d': 8,
        'i-m': 6, 'e-i': 9, 'a-e': 9,
        'j-n': 7, 'f-j': 6, 'b-f': 5,
        'k-o': 1, 'g-k': 8, 'c-g': 8,
        'l-p': 2, 'h-l': 1, 'd-h': 7
    };

    // --- Application State ---
    let appState = {
        startVertex: 'm',
        weights: {},
        distances: {},
        predecessors: {},
        visited: new Set(),
        isFinished: false
    };

    // --- Helper Functions ---
    function getEdgeKey(v1, v2) {
        return [v1, v2].sort().join('-');
    }

    function getWeight(v1, v2) {
        return appState.weights[getEdgeKey(v1, v2)];
    }

    function generateRandomWeights() {
        const randomW = {};
        edges.forEach(edge => {
            randomW[getEdgeKey(edge.v1, edge.v2)] = Math.floor(Math.random() * 10) + 1;
        });
        return randomW;
    }

    function updateVertexButtonUI() {
        Object.values(vertexButtons).forEach(btn => btn.classList.remove('active'));
        if (vertexButtons[appState.startVertex]) {
            vertexButtons[appState.startVertex].classList.add('active');
        }
    }

    // --- Algorithm & State Management ---
    function resetAlgorithm() {
        appState.distances = {};
        appState.predecessors = {};
        appState.visited = new Set();
        appState.isFinished = false;

        for (const name in vertices) {
            appState.distances[name] = Infinity;
        }
        appState.distances[appState.startVertex] = 0;

        titleDisplay.textContent = `Dijkstra's algorithm starting at vertex ${appState.startVertex}`;
        nextStepBtn.disabled = false;

        if (sketchInstance) sketchInstance.redraw();
    }

    function stepAlgorithm() {
        if (appState.isFinished) return;

        // Find unvisited vertex with smallest distance
        let minDistance = Infinity;
        let u = null;

        for (const name in vertices) {
            if (!appState.visited.has(name) && appState.distances[name] < minDistance) {
                minDistance = appState.distances[name];
                u = name;
            }
        }

        if (u === null) {
            appState.isFinished = true;
            nextStepBtn.disabled = true;
            if (sketchInstance) sketchInstance.redraw();
            return;
        }

        appState.visited.add(u);

        // Get neighbors of u
        const neighbors = edges
            .filter(e => e.v1 === u || e.v2 === u)
            .map(e => e.v1 === u ? e.v2 : e.v1);

        neighbors.forEach(v => {
            if (!appState.visited.has(v)) {
                const weight = getWeight(u, v);
                const newDist = appState.distances[u] + weight;
                if (newDist < appState.distances[v]) {
                    appState.distances[v] = newDist;
                    appState.predecessors[v] = u;
                }
            }
        });

        if (appState.visited.size === Object.keys(vertices).length) {
            appState.isFinished = true;
            nextStepBtn.disabled = true;
        }

        if (sketchInstance) sketchInstance.redraw();
    }


    // --- Event Handlers ---
    function handleStartOver() {
        appState.startVertex = 'm';
        appState.weights = { ...fixedWeights };
        updateVertexButtonUI();
        resetAlgorithm();
    }

    function handleVertexSelect(e) {
        const btn = e.target.closest('button');
        if (!btn) return;
        const newStartVertex = btn.id.split('-')[1];
        if (newStartVertex && vertexChars.includes(newStartVertex)) {
            appState.startVertex = newStartVertex;
            updateVertexButtonUI();
            resetAlgorithm();
        }
    }

    function handleFixedExample() {
        appState.weights = { ...fixedWeights };
        resetAlgorithm();
    }

    function handleRandomExample() {
        appState.weights = generateRandomWeights();
        resetAlgorithm();
    }

    // --- p5.js Sketch ---
    let sketchInstance;

    const sketch = (p) => {
        p.setup = () => {
            const canvas = p.createCanvas(600, 600);
            canvas.parent('canvas-container');
            p.noLoop();
        };

        p.draw = () => {
            p.background(255);
            drawGraph(p);
        };

        function drawGraph(p) {
            // Draw edges first
            edges.forEach(edge => {
                const v1 = vertices[edge.v1];
                const v2 = vertices[edge.v2];
                const weight = getWeight(edge.v1, edge.v2);

                const predV1 = appState.predecessors[edge.v1];
                const predV2 = appState.predecessors[edge.v2];

                const isTreeEdge = (predV1 === edge.v2 && appState.visited.has(edge.v1)) || (predV2 === edge.v1 && appState.visited.has(edge.v2));

                // Style and draw edge line
                if (isTreeEdge) {
                    p.stroke(0, 0, 255); // Blue
                    p.strokeWeight(4);
                } else {
                    p.stroke(0); // Black
                    p.strokeWeight(1);
                }
                p.line(v1.x, v1.y, v2.x, v2.y);

                // Draw edge weight
                const midX = (v1.x + v2.x) / 2;
                const midY = (v1.y + v2.y) / 2;
                p.noStroke();
                p.fill(255);
                p.rectMode(p.CENTER);
                p.rect(midX, midY, 20, 20); // Clear background for text

                p.fill(0);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(16);
                p.textStyle(p.NORMAL);
                p.text(weight, midX, midY);
            });

            // Draw vertices and labels
            for (const name in vertices) {
                const v = vertices[name];
                const isVisited = appState.visited.has(name);
                const distance = appState.distances[name];

                // Draw vertex circle
                p.stroke(0);
                p.strokeWeight(1);
                p.fill(isVisited ? p.color(255, 0, 0) : p.color(0, 0, 0)); // Red if visited, else black
                p.ellipse(v.x, v.y, 10, 10);

                // Draw vertex name label
                p.noStroke();
                p.fill(0);
                p.textSize(16);
                p.textAlign(p.LEFT, p.CENTER);
                p.textStyle(p.BOLD);
                p.text(name, v.x + 10, v.y);

                // Draw distance label
                p.fill(isVisited ? p.color(255, 0, 0) : p.color(0, 0, 0));
                p.textAlign(p.CENTER, p.BOTTOM);
                p.textStyle(p.NORMAL);
                const distText = (distance === Infinity) ? '(\u221E)' : `(${distance})`;
                p.text(distText, v.x, v.y - 10);
            }
        }
    };

    // --- Initialization ---
    function initialize() {
        startOverBtn.addEventListener('click', handleStartOver);
        vertexGrid.addEventListener('click', handleVertexSelect);
        fixedExampleBtn.addEventListener('click', handleFixedExample);
        randomExampleBtn.addEventListener('click', handleRandomExample);
        nextStepBtn.addEventListener('click', stepAlgorithm);

        sketchInstance = new p5(sketch);
        handleStartOver(); // Set initial state
    }

    initialize();
});
</script>
</body>
