        const polyhedronButtonsContainer = document.getElementById('polyhedron-buttons');
        const polyhedronButtons = Array.from(polyhedronButtonsContainer.querySelectorAll('button'));
        const sliderConeHeight = document.getElementById('slider-cone-height');
        const sliderConeBase = document.getElementById('slider-cone-base');

        function init() {
            // Scene
            scene = new THREE.Scene();

            // Camera
            const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera = new THREE.PerspectiveCamera(45, aspect, 0.1, 100);
            camera.position.set(0, 0, 8);
            camera.lookAt(scene.position);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setClearColor(0xf0f0f0);
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvasContainer.appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 1.2);
            directionalLight1.position.set(1, 1, 1);
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.6);
            directionalLight2.position.set(-1, -1, -1);
            scene.add(directionalLight2);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Main objects
            conesGroup = new THREE.Group();
            scene.add(conesGroup);

            const sphereGeometry = new THREE.SphereGeometry(1, 64, 64); // Base radius of 1
            const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50, specular: 0x111111 });
            sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(sphere);

            // Event Listeners
            window.addEventListener('resize', onWindowResize);
            sliderSphereDiameter.addEventListener('input', updateSphereSize);
            sliderConeHeight.addEventListener('input', updateConesGeometry);
            sliderConeBase.addEventListener('input', updateConesGeometry);
            polyhedronButtons.forEach(button => {
                button.addEventListener('click', (e) => handlePolyhedronChange(e.target));
            });

            // Initial setup
            document.getElementById('btn-octahedron').classList.add('active');
            updateScene();

            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        function getPolyhedronVertices(name) {
            let geometry;
            // Radius of 1 for consistent vertex positions
            switch (name) {
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(1);
                    break;
                case 'cube':
                    geometry = new THREE.BoxGeometry(1.2, 1.2, 1.2); // Sized to have vertices roughly at radius 1
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(1);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(1);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(1);
                    break;
                default:
                    return [];
            }

            const positions = geometry.attributes.position;
            const uniqueVertices = new Set();
            const vertices = [];

            for (let i = 0; i < positions.count; i++) {
                const x = positions.getX(i);
                const y = positions.getY(i);
                const z = positions.getZ(i);
                const key = `${x.toFixed(5)},${y.toFixed(5)},${z.toFixed(5)}`;
                if (!uniqueVertices.has(key)) {
                    uniqueVertices.add(key);
                    vertices.push(new THREE.Vector3(x, y, z).normalize());
                }
            }
            return vertices;
        }

        function handlePolyhedronChange(clickedButton) {
            polyhedronButtons.forEach(btn => btn.classList.remove('active'));
            clickedButton.classList.add('active');
            updateScene();
        }

        function updateScene() {
            // Clear existing cones
            while (conesGroup.children.length > 0) {
                const cone = conesGroup.children[0];
                cone.geometry.dispose();
                cone.material.dispose();
                conesGroup.remove(cone);
            }

            // Get new vertices
            const activePolyhedron = polyhedronButtonsContainer.querySelector('.active').id.replace('btn-', '');
            vertexDirections = getPolyhedronVertices(activePolyhedron);

            // Get current parameters
            const coneHeight = parseFloat(sliderConeHeight.value);
            const coneBase = parseFloat(sliderConeBase.value);

            const coneGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);
            const coneMaterial = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30, specular: 0x222222 });

            vertexDirections.forEach(direction => {
                const cone = new THREE.Mesh(coneGeometry.clone(), coneMaterial);
                cone.quaternion.setFromUnitVectors(UP_VECTOR, direction);
                conesGroup.add(cone);
            });

            updateSphereSize();
        }

        function updateSphereSize() {
            const sphereDiameter = parseFloat(sliderSphereDiameter.value);
            const sphereRadius = sphereDiameter / 2.0;

            sphere.scale.set(sphereRadius, sphereRadius, sphereRadius);

            updateConePositions();
        }

        function updateConesGeometry() {
            const coneHeight = parseFloat(sliderConeHeight.value);
            const coneBase = parseFloat(sliderConeBase.value);
            const newGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);

            conesGroup.children.forEach(cone => {
                cone.geometry.dispose();
                cone.geometry = newGeometry;
            });

            newGeometry.dispose(); // Dispose the template geometry after use

            updateConePositions();
        }

        function updateConePositions() {
            const sphereRadius = parseFloat(sliderSphereDiameter.value) / 2.0;
            const coneHeight = parseFloat(sliderConeHeight.value);

            conesGroup.children.forEach((cone, i) => {
                if(vertexDirections[i]) {
                    const direction = vertexDirections[i];
                    const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2.0);
                    cone.position.copy(position);
                }
            });
        }

        init();
    </script>
</body>
</html>
