    <script>
        const planetData = {
          "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
          "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        // --- P5.js Sketch ---
        const sketch = (p) => {
            // HTML element references
            let selectPlanet, checkboxManual, sliderTilt, sliderEquinox, sliderEccentricity;
            let checkboxSnapshots, checkboxScales;
            let labelTilt, labelEquinox, labelEccentricity;

            let analemmaPoints = [];

            p.setup = () => {
                const visArea = document.getElementById('visualization-area');
                const canvas = p.createCanvas(visArea.clientWidth - 40, 600);
                canvas.id('analemma-canvas');
                canvas.parent(visArea);

                // Get control elements
                selectPlanet = p.select('#select-planet');
                checkboxManual = p.select('#checkbox-manual');
                sliderTilt = p.select('#slider-tilt');
                sliderEquinox = p.select('#slider-equinox');
                sliderEccentricity = p.select('#slider-eccentricity');
                checkboxSnapshots = p.select('#checkbox-snapshots');
                checkboxScales = p.select('#checkbox-scales');

                labelTilt = p.select('#label-tilt');
                labelEquinox = p.select('#label-equinox');
                labelEccentricity = p.select('#label-eccentricity');

                // Attach event listeners
                selectPlanet.changed(handlePlanetSelect);
                checkboxManual.changed(handleManualCheckbox);

                sliderTilt.input(() => p.redraw());
                sliderEquinox.input(() => p.redraw());
                sliderEccentricity.input(() => p.redraw());
                checkboxSnapshots.changed(() => p.redraw());
                checkboxScales.changed(() => p.redraw());

                p.noLoop(); // We will redraw manually on control changes
                updateAndRedraw(); // Initial draw
            };

            p.draw = () => {
                updateLabels();
                calculateAnalemma();

                p.background(255);

                // --- 1. Draw Orbit Ellipse ---
                drawOrbit();

                // --- 2. Main Plot Area ---
                p.push();
                p.translate(p.width / 2, p.height / 2 + 30);

                // --- 3. Draw Scales ---
                if (checkboxScales.checked()) {
                    drawScales();
                }

                // --- 4. Draw Analemma Curve ---
                p.stroke('orange');
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (const pt of analemmaPoints) {
                    p.vertex(pt.x, pt.y);
                }
                p.endShape();

                // --- 5. Draw Sun Snapshots ---
                if (checkboxSnapshots.checked()) {
                    p.noStroke();
                    p.fill('orange');
                    const snapshotInterval = Math.round(365 / 20);
                    for (let i = 0; i < analemmaPoints.length; i += snapshotInterval) {
                        p.circle(analemmaPoints[i].x, analemmaPoints[i].y, 8);
                    }
                }
                p.pop();
            };

            function updateAndRedraw() {
                p.redraw();
            }

            function handlePlanetSelect() {
                const planet = selectPlanet.value();
                if (planet === 'manual') {
                    checkboxManual.checked(true);
                    enableManualControls(true);
                } else {
                    const data = planetData[planet];
                    sliderTilt.value(data.tilt);
                    sliderEccentricity.value(data.eccentricity);
                    sliderEquinox.value(data.equinox);
                    checkboxManual.checked(false);
                    enableManualControls(false);
                }
                updateAndRedraw();
            }

            function handleManualCheckbox() {
                if (checkboxManual.checked()) {
                    selectPlanet.value('manual');
                    enableManualControls(true);
                } else {
                    enableManualControls(false);
                }
                updateAndRedraw();
            }

            function enableManualControls(enable) {
                 sliderTilt.elt.disabled = !enable;
                 sliderEquinox.elt.disabled = !enable;
                 sliderEccentricity.elt.disabled = !enable;
                 checkboxManual.elt.disabled = !enable;
                 if(selectPlanet.value() !== 'manual') {
                     checkboxManual.elt.disabled = true;
                 }
            }

            function updateLabels() {
                labelTilt.html(`axis angle relative to ecliptic (${parseFloat(sliderTilt.value()).toFixed(1)})`);
                labelEquinox.html(`spring equinox point (${sliderEquinox.value()})`);
                labelEccentricity.html(`orbit eccentricity (${parseFloat(sliderEccentricity.value()).toFixed(3)})`);
            }

            // --- Calculation Functions ---

            function solveKepler(M, ecc) {
                let E = M; // Initial guess
                for (let i = 0; i < 7; i++) { // Newton-Raphson iterations
                    E = E - (E - ecc * p.sin(E) - M) / (1 - ecc * p.cos(E));
                }
                return E;
            }

            function calculateAnalemma() {
                analemmaPoints = [];
                const tilt = p.radians(sliderTilt.value());
                const ecc = sliderEccentricity.value();
                const equinoxAngle = p.radians(sliderEquinox.value());

                const DAYS_IN_YEAR = 365.25;

                for (let d = 0; d <= 365; d++) {
                    const M = (2 * p.PI * d) / DAYS_IN_YEAR; // Mean anomaly
                    const E = solveKepler(M, ecc); // Eccentric anomaly

                    const v_x = p.sqrt(1 + ecc) * p.sin(E / 2);
                    const v_y = p.sqrt(1 - ecc) * p.cos(E / 2);
                    const v = 2 * p.atan2(v_x, v_y); // True anomaly

                    const lambda = v + equinoxAngle; // Ecliptic longitude

                    // Y-coordinate (Declination)
                    const declination = p.asin(p.sin(tilt) * p.sin(lambda));

                    // X-coordinate (Equation of Time)
                    const alpha_y = p.cos(tilt) * p.sin(lambda);
                    const alpha_x = p.cos(lambda);
                    const alpha = p.atan2(alpha_y, alpha_x); // Right ascension

                    let EoT = M - alpha;
                    // Normalize EoT to [-PI, PI]
                    if (EoT > p.PI) EoT -= 2 * p.PI;
                    if (EoT < -p.PI) EoT += 2 * p.PI;

                    // Scale to pixel coordinates
                    const scaleFactor = 400; // Converts radians to pixels for a nice size
                    const x = EoT * scaleFactor;
                    const y = -declination * scaleFactor; // Negative because canvas Y is inverted

                    analemmaPoints.push({ x, y });
                }
            }

            // --- Drawing Helper Functions ---

            function drawOrbit() {
                const ecc = sliderEccentricity.value();
                const orbitWidth = 80;
                const orbitHeight = orbitWidth * p.sqrt(1 - ecc * ecc);

                p.push();
                p.translate(p.width / 2, 50);
                p.noFill();
                p.stroke(139, 69, 19); // Brown color
                p.strokeWeight(2);
                p.ellipse(0, 0, orbitWidth, orbitHeight);
                p.pop();
            }

            function drawScales() {
                const pixel_per_unit = 5;
                const max_unit = 40;
                const max_pixel_x = p.width / 2 - 20;
                const max_pixel_y = (p.height / 2) - 80;

                p.stroke(200);
                p.strokeWeight(1);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(10);
                p.fill(100);

                // Axes
                p.line(-max_pixel_x, 0, max_pixel_x, 0); // X-axis
                p.line(0, -max_pixel_y, 0, max_pixel_y); // Y-axis

                // X-axis ticks and labels
                for (let i = -max_unit; i <= max_unit; i += 20) {
                    const x = i * pixel_per_unit;
                    if (p.abs(x) > max_pixel_x) continue;
                    p.line(x, -5, x, 5);
                    if (i !== 0) {
                         p.text(i, x, 15);
                    }
                }

                // Y-axis ticks and labels
                for (let i = -max_unit; i <= max_unit; i += 20) {
                    const y = -i * pixel_per_unit; // Negative for inverted canvas y
                     if (p.abs(y) > max_pixel_y) continue;
                    p.line(-5, y, 5, y);
                    p.text(i, -15, y);
                }
                p.text(0, -15, 0); // Origin label
            }
        };

        // Initialize the p5 sketch
        new p5(sketch);
    </script>
</body>
</html>
