        canvasAlgoWrapper: document.getElementById('canvas-algo-wrapper'),
        exampleTypeAlgo: document.getElementById('text-example-type-algo'),
        tableTitle: document.getElementById('table-title'),
        edgeListTable: document.getElementById('table-edge-list'),
        totalWeightText: document.getElementById('text-total-weight'),
        optimalContainer: document.getElementById('optimal-result-container'),
        canvasOptimalWrapper: document.getElementById('canvas-optimal-wrapper'),
        exampleTypeOptimal: document.getElementById('text-example-type-optimal'),
        optimalWeightText: document.getElementById('text-optimal-weight'),
    };

    // --- GRAPH DATA ---
    const fixedGraphData = {
        vertices: {
            A: { x: 200, y: 60 }, B: { x: 80, y: 120 }, C: { x: 60, y: 240 },
            D: { x: 150, y: 340 }, E: { x: 250, y: 340 }, F: { x: 340, y: 240 },
            G: { x: 320, y: 120 }
        },
        weights: [
            [-1, 11, 16, 44, 30, 44, 23], // A
            [11, -1, 12, 12, 10, 30, 25], // B
            [16, 12, -1, 43, 32, 36, 44], // C
            [44, 12, 43, -1, 41, 30, 35], // D
            [30, 10, 32, 41, -1, 31, 20], // E
            [44, 30, 36, 30, 31, -1, 18], // F
            [23, 25, 44, 35, 20, 18, -1]  // G
        ],
        optimal: {
            path: ['A', 'C', 'B', 'D', 'F', 'G', 'E', 'A'],
            weight: 138
        }
    };

    // --- P5.JS SKETCH LOGIC ---
    const sketch = (p) => {
        let localGraph = {};
        let highlightedPath = [];
        let highlightColor = [0, 0, 0];
        let vertexRadius = 20;

        p.setup = () => {
            const wrapper = p.canvas.parentElement;
            const canvas = p.createCanvas(wrapper.clientWidth, wrapper.clientHeight);
            canvas.style('display', 'block');
            p.textAlign(p.CENTER, p.CENTER);
            p.noLoop();
        };

        p.windowResized = () => {
             const wrapper = p.canvas.parentElement;
             p.resizeCanvas(wrapper.clientWidth, wrapper.clientHeight);
             p.redraw();
        };

        p.update = (graphData, path, color) => {
            localGraph = graphData;
            highlightedPath = path;
            highlightColor = color;
            p.redraw();
        };

        p.draw = () => {
            if (!localGraph.vertices) return;
            p.background(255);
            p.push();
            p.scale(p.width / 400, p.height / 400); // Scale to fit canvas

            drawAllEdges(p, localGraph);
            drawHighlightedPath(p, localGraph, highlightedPath, highlightColor);
            drawVertices(p, localGraph);
            drawEdgeWeights(p, localGraph);

            p.pop();
        };

        function drawAllEdges(p, g) {
            p.stroke(200);
            p.strokeWeight(1);
            for (let i = 0; i < vertexLabels.length; i++) {
                for (let j = i + 1; j < vertexLabels.length; j++) {
                    const v1 = g.vertices[vertexLabels[i]];
                    const v2 = g.vertices[vertexLabels[j]];
                    p.line(v1.x, v1.y, v2.x, v2.y);
                }
            }
        }

        function drawHighlightedPath(p, g, path, color) {
            if (path.length < 2) return;
            p.stroke(color);
            p.strokeWeight(3);
            for (let i = 0; i < path.length - 1; i++) {
                const u = path[i];
                const v = path[i+1];
                const v1 = g.vertices[u];
                const v2 = g.vertices[v];
                p.line(v1.x, v1.y, v2.x, v2.y);
            }
        }

        function drawVertices(p, g) {
            p.stroke(0);
            p.strokeWeight(1);
            p.textSize(14);
            Object.keys(g.vertices).forEach(label => {
                const v = g.vertices[label];
                p.fill(255);
                p.ellipse(v.x, v.y, vertexRadius, vertexRadius);
                p.fill(0);
                p.noStroke();
                p.text(label, v.x, v.y);
            });
        }

        function drawEdgeWeights(p, g) {
            p.strokeWeight(0);
            p.fill(0);
            p.textSize(12);
            for (let i = 0; i < vertexLabels.length; i++) {
                for (let j = i + 1; j < vertexLabels.length; j++) {
                    const v1 = g.vertices[vertexLabels[i]];
                    const v2 = g.vertices[vertexLabels[j]];
                    const weight = g.weights[i][j];
                    const midX = (v1.x + v2.x) / 2;
                    const midY = (v1.y + v2.y) / 2;
                    p.fill(255);
                    p.rectMode(p.CENTER);
                    p.rect(midX, midY, 18, 14);
                    p.fill(0);
                    p.text(weight, midX, midY);
                }
            }
        }
    };

    // --- ALGORITHM LOGIC ---
    function executeNextStep() {
        if (currentAlgorithm === 'nearest-neighbor') {
            stepNearestNeighbor();
        } else if (currentAlgorithm === 'cheapest-link') {
            stepCheapestLink();
        }
        updateUI();
    }

    function stepNearestNeighbor() {
        if (algoState.path.length === vertexLabels.length) { // Final step: close the circuit
            const startNode = algoState.startVertex;
            const lastNode = algoState.currentVertex;
            const weight = getWeight(startNode, lastNode);
            algoState.path.push(startNode);
            algoState.totalWeight += weight;
            algoState.edges.push({ u: lastNode, v: startNode, weight: weight });
            algoState.isComplete = true;
            controls.nextStepBtn.disabled = true;
        } else {
            let nearest = null;
            let minWeight = Infinity;
            const currentIdx = vertexLabels.indexOf(algoState.currentVertex);

            for (let i = 0; i < vertexLabels.length; i++) {
                const neighborLabel = vertexLabels[i];
                if (!algoState.visited.has(neighborLabel)) {
                    const weight = graph.weights[currentIdx][i];
                    if (weight < minWeight) {
                        minWeight = weight;
                        nearest = neighborLabel;
                    }
                }
            }

            if (nearest) {
                algoState.edges.push({ u: algoState.currentVertex, v: nearest, weight: minWeight });
                algoState.visited.add(nearest);
                algoState.path.push(nearest);
                algoState.currentVertex = nearest;
                algoState.totalWeight += minWeight;
            }
        }
    }

    function stepCheapestLink() {
        while (algoState.sortedEdges.length > 0) {
            const edge = algoState.sortedEdges.shift(); // Get cheapest remaining edge
            const { u, v } = edge;

            // Check if adding this edge creates a premature cycle or a vertex with degree 3
            if (algoState.degree[u] < 2 && algoState.degree[v] < 2 && !createsCycle(u, v)) {
                algoState.edges.push(edge);
                algoState.path.push([u,v]); // Path for CL is just a list of edges
                algoState.totalWeight += edge.weight;
                algoState.degree[u]++;
                algoState.degree[v]++;

                // Union the sets for cycle detection
                const rootU = findSet(u);
                const rootV = findSet(v);
                if (rootU !== rootV) {
                    algoState.subsets[rootU] = rootV;
                }

                if (algoState.edges.length === vertexLabels.length) {
                    algoState.isComplete = true;
                    controls.nextStepBtn.disabled = true;
                }
                return; // Exit after adding one valid edge
            }
        }
        // If we exit the loop, something went wrong or we are done
        algoState.isComplete = true;
        controls.nextStepBtn.disabled = true;
    }

    function createsCycle(u, v) {
        // If we have selected n-1 edges and u and v are not connected, adding this edge is fine (it will complete the circuit)
        if(algoState.edges.length === vertexLabels.length - 1) return false;
        // If u and v are already in the same subset, adding an edge between them creates a cycle
        return findSet(u) === findSet(v);
    }

    function findSet(vertex) {
        if (algoState.subsets[vertex] === vertex) {
            return vertex;
        }
        // Path compression
        algoState.subsets[vertex] = findSet(algoState.subsets[vertex]);
        return algoState.subsets[vertex];
    }

    // --- UI & STATE UPDATE FUNCTIONS ---
    function resetSimulation() {
        controls.nextStepBtn.disabled = true;
        controls.vertexBtns.forEach(btn => {
            btn.disabled = false;
            btn.classList.remove('selected');
        });

        algoState = {
            isComplete: false,
            totalWeight: 0,
            edges: []
        };

        if (currentAlgorithm === 'nearest-neighbor') {
            display.titleAlgo.textContent = 'Select a starting vertex';
            controls.startingVertexSelector.style.display = 'block';
            algoState.path = [];
            algoState.visited = new Set();
        } else { // cheapest-link
            display.titleAlgo.textContent = 'The cheapest link algorithm';
            controls.startingVertexSelector.style.display = 'none';
            controls.nextStepBtn.disabled = false; // CL can start immediately

            // Prepare for CL
            const allEdges = [];
            for (let i = 0; i < vertexLabels.length; i++) {
                for (let j = i + 1; j < vertexLabels.length; j++) {
                    allEdges.push({ u: vertexLabels[i], v: vertexLabels[j], weight: graph.weights[i][j] });
                }
            }
            algoState.sortedEdges = allEdges.sort((a, b) => a.weight - b.weight);
            algoState.degree = vertexLabels.reduce((acc, v) => ({ ...acc, [v]: 0 }), {});
            // For cycle detection (Disjoint Set Union)
            algoState.subsets = vertexLabels.reduce((acc, v) => ({ ...acc, [v]: v }), {});
            algoState.path = []; // Will store edge pairs
        }
        updateUI();
    }

    function updateUI() {
        // Update main canvas
        let algoPath = [];
        if (currentAlgorithm === 'nearest-neighbor') {
            algoPath = algoState.path;
        } else { // Reconstruct path for drawing cheapest link
            const adj = {};
            algoState.edges.forEach(({u, v}) => {
                if(!adj[u]) adj[u] = [];
                if(!adj[v]) adj[v] = [];
                adj[u].push(v);
                adj[v].push(u);
            });
            if(algoState.edges.length > 0) {
                const startNode = algoState.edges[0].u;
                const path = [startNode];
                const visited = new Set([startNode]);
                let curr = startNode;
                while(path.length <= algoState.edges.length) {
                    const neighbors = adj[curr].filter(n => !visited.has(n));
                    if(neighbors.length === 0) break;
                    curr = neighbors[0];
                    visited.add(curr);
                    path.push(curr);
                }
                 if(algoState.isComplete) path.push(path[0]);
                algoPath = path;
            }
        }

        p5_algo.update(graph, algoPath, [255, 165, 0]);

        // Update table
        if (algoState.edges.length > 0) {
            display.tableTitle.textContent = 'Edges in the order selected';
            let tableHTML = '<thead><tr><th>Edge</th><th>Weight</th></tr></thead><tbody>';
            algoState.edges.forEach(edge => {
                tableHTML += `<tr><td>{${edge.u}, ${edge.v}}</td><td>${edge.weight}</td></tr>`;
            });
            tableHTML += '</tbody>';
            display.edgeListTable.innerHTML = tableHTML;
        } else {
            display.tableTitle.textContent = '';
            display.edgeListTable.innerHTML = '';
        }

        // Update weight text
        if (algoState.isComplete) {
            display.totalWeightText.textContent = `The ${currentAlgorithm.replace('-', ' ')} algorithm found a Hamilton circuit of weight ${algoState.totalWeight}.`;
        } else if (algoState.totalWeight > 0) {
            display.totalWeightText.textContent = `The total weight of the edges selected so far is ${algoState.totalWeight}.`;
        } else {
            display.totalWeightText.textContent = '';
        }

        // Update example type text
        display.exampleTypeAlgo.textContent = `${exampleType} example`;
    }

    function loadGraph(newGraph, newExampleType) {
        graph = JSON.parse(JSON.stringify(newGraph)); // Deep copy
        exampleType = newExampleType;
        controls.optimalCheckbox.checked = false;
        display.optimalContainer.style.display = 'none';
        if(p5_optimal) {
            p5_optimal.remove();
            p5_optimal = null;
        }
        resetSimulation();
    }

    function selectStartVertex(e) {
        const startVertex = e.target.dataset.vertex;
        algoState.startVertex = startVertex;
        algoState.currentVertex = startVertex;
        algoState.path = [startVertex];
        algoState.visited.add(startVertex);

        controls.vertexBtns.forEach(btn => {
            btn.disabled = true;
            if (btn.dataset.vertex === startVertex) {
                btn.classList.add('selected');
            }
        });

        controls.nextStepBtn.disabled = false;
        display.titleAlgo.textContent = `The nearest neighbor algorithm starting at vertex ${startVertex}`;
        updateUI();
    }

    // --- HELPER FUNCTIONS ---
    function getWeight(v1, v2) {
        const i1 = vertexLabels.indexOf(v1);
        const i2 = vertexLabels.indexOf(v2);
        return graph.weights[i1][i2];
    }

    function generateRandomGraph() {
        const newGraph = {
            vertices: { ...fixedGraphData.vertices }, // Use same positions for simplicity
            weights: [],
            optimal: { path: [], weight: Infinity }
        };
        // Generate random weights
        for (let i = 0; i < vertexLabels.length; i++) {
            newGraph.weights[i] = [];
            for (let j = 0; j < vertexLabels.length; j++) {
                if (i === j) newGraph.weights[i][j] = -1;
                else if (j < i) newGraph.weights[i][j] = newGraph.weights[j][i];
                else newGraph.weights[i][j] = Math.floor(Math.random() * 41) + 10;
            }
        }

        // Brute-force find optimal path (for N=7)
        const otherVertices = vertexLabels.slice(1);
        const permutations = getPermutations(otherVertices);

        permutations.forEach(perm => {
            const path = ['A', ...perm, 'A'];
            let currentWeight = 0;
            for(let i=0; i < path.length - 1; i++) {
                currentWeight += getWeightForGraph(path[i], path[i+1], newGraph);
            }
            if (currentWeight < newGraph.optimal.weight) {
                newGraph.optimal.weight = currentWeight;
                newGraph.optimal.path = path;
            }
        });

        loadGraph(newGraph, 'random');
    }

    function getWeightForGraph(v1, v2, g) {
        const i1 = vertexLabels.indexOf(v1);
        const i2 = vertexLabels.indexOf(v2);
        return g.weights[i1][i2];
    }

    function getPermutations(arr) {
        if (arr.length === 0) return [[]];
        const firstEl = arr[0];
        const rest = arr.slice(1);
        const permsWithoutFirst = getPermutations(rest);
        const allPermutations = [];
        permsWithoutFirst.forEach(perm => {
            for (let i = 0; i <= perm.length; i++) {
                const permWithFirst = [...perm.slice(0, i), firstEl, ...perm.slice(i)];
                allPermutations.push(permWithFirst);
            }
        });
        return allPermutations;
    }

    // --- EVENT LISTENERS ---
    controls.cheapestLinkRadio.addEventListener('change', () => {
        currentAlgorithm = 'cheapest-link';
        resetSimulation();
    });

    controls.nearestNeighborRadio.addEventListener('change', () => {
        currentAlgorithm = 'nearest-neighbor';
        resetSimulation();
    });

    controls.startOverBtn.addEventListener('click', resetSimulation);
    controls.fixedExampleBtn.addEventListener('click', () => loadGraph(fixedGraphData, 'fixed'));
    controls.randomExampleBtn.addEventListener('click', generateRandomGraph);
    controls.vertexBtns.forEach(btn => btn.addEventListener('click', selectStartVertex));
    controls.nextStepBtn.addEventListener('click', executeNextStep);

    controls.optimalCheckbox.addEventListener('change', (e) => {
        if (e.target.checked) {
            display.optimalContainer.style.display = 'flex';
            display.exampleTypeOptimal.textContent = `${exampleType} example`;
            display.optimalWeightText.textContent = `An optimal solution has a total weight of ${graph.optimal.weight}.`;
            if (!p5_optimal) {
                p5_optimal = new p5(sketch, display.canvasOptimalWrapper);
            }
            p5_optimal.update(graph, graph.optimal.path, [0, 100, 255]);
        } else {
            display.optimalContainer.style.display = 'none';
        }
    });

    // --- INITIALIZATION ---
    function init() {
        p5_algo = new p5(sketch, display.canvasAlgoWrapper);
        loadGraph(fixedGraphData, 'fixed');
    }

    init();
});
</script>

