    <script>
        // --- GLOBAL VARIABLES ---
        let u, v, w, A, B; // Calculated vectors {x, y}
        let calculationOk = true;
        let errorMsg = "";

        const dotProducts = {};
        const scales = {};

        // --- P5.JS SKETCH ---
        const sketch = (p) => {

            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                canvas.parent('canvas-container');
                p.noLoop(); // We will manually redraw

                // Link sliders to update function
                const sliderIds = ['au', 'bu', 'av', 'bv', 'aw', 'bw', 'x-scale', 'y-scale'];
                sliderIds.forEach(id => {
                    const slider = document.getElementById(`slider-${id}`);
                    slider.addEventListener('input', () => {
                        updateAndRedraw();
                    });
                });

                // Initial calculation and draw
                updateAndRedraw();
            };

            p.draw = () => {
                p.background('#F0F0F0');

                // Set up coordinate system
                p.translate(p.width / 2, p.height / 2);
                p.scale(scales.x, -scales.y);

                if (!calculationOk) {
                    // Display error message if calculation failed
                    p.push();
                    p.resetMatrix(); // Use screen coordinates for text
                    p.fill(255, 0, 0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(16);
                    p.text(`Calculation Error: ${errorMsg}`, p.width / 2, p.height / 2);
                    p.pop();
                    return;
                }

                // If calculation is ok, draw the scene
                drawScene();
            };

            p.windowResized = () => {
                const canvasContainer = document.getElementById('canvas-container');
                p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                p.redraw();
            };

            const drawScene = () => {
                if (!A || !B || !u || !v || !w) return;

                // Draw Camera Axes
                drawAxis(u, 'u', '#0000CD');
                drawAxis(v, 'v', '#6495ED');
                drawAxis(w, 'w', '#0000CD');

                // Draw projection lines first so they are in the background
                drawProjectionLines(A, u, v, w);
                drawProjectionLines(B, u, v, w);

                // Draw projection points
                drawProjectionPoints(A, u, v, w);
                drawProjectionPoints(B, u, v, w);

                // Draw main points and origin
                drawPoint(A, 'A', '#FF0000', 12);
                drawPoint(B, 'B', '#FFA500', 12);
                drawPoint({x: 0, y: 0}, 'O', '#FF0000', 8);
            };

            // --- DRAWING HELPER FUNCTIONS ---

            const drawAxis = (vec, label, color) => {
                p.push();
                p.stroke(color);
                p.strokeWeight(3);
                // Draw a very long line to simulate an infinite axis
                const lineLength = Math.max(p.width, p.height) * 2;
                p.line(-vec.x * lineLength, -vec.y * lineLength, vec.x * lineLength, vec.y * lineLength);

                // Draw label
                p.noStroke();
                p.fill(color);
                p.textSize(20 / scales.y); // Scale text size inversely to zoom
                p.text(label, vec.x * 2.5, vec.y * 2.5);
                p.pop();
            };

            const drawPoint = (pt, label, color, size) => {
                p.push();
                p.noStroke();
                p.fill(color);
                p.ellipse(pt.x, pt.y, size / scales.x, size / scales.y);
                p.fill(0);
                p.textSize(20 / scales.y);
                p.text(label, pt.x + 0.1, pt.y + 0.1);
                p.pop();
            };

            const drawProjectionLines = (point, u, v, w) => {
                const dp_u_val = (point === A) ? dotProducts.au : dotProducts.bu;
                const dp_v_val = (point === A) ? dotProducts.av : dotProducts.bv;
                const dp_w_val = (point === A) ? dotProducts.aw : dotProducts.bw;

                const proj_u = { x: dp_u_val * u.x, y: dp_u_val * u.y };
                const proj_v = { x: dp_v_val * v.x, y: dp_v_val * v.y };
                const proj_w = { x: dp_w_val * w.x, y: dp_w_val * w.y };

                p.push();
                p.stroke(128, 128, 128); // Gray
                p.strokeWeight(1);
                p.line(point.x, point.y, proj_u.x, proj_u.y);
                p.line(point.x, point.y, proj_v.x, proj_v.y);
                p.line(point.x, point.y, proj_w.x, proj_w.y);
