            };

            const drawProjectionPoints = (point, u, v, w) => {
                 const dp_u_val = (point === A) ? dotProducts.au : dotProducts.bu;
                const dp_v_val = (point === A) ? dotProducts.av : dotProducts.bv;
                const dp_w_val = (point === A) ? dotProducts.aw : dotProducts.bw;

                const proj_u = { x: dp_u_val * u.x, y: dp_u_val * u.y };
                const proj_v = { x: dp_v_val * v.x, y: dp_v_val * v.y };
                const proj_w = { x: dp_w_val * w.x, y: dp_w_val * w.y };

                p.push();
                p.noStroke();
                p.fill('#00FF00'); // Green
                const pointSize = 8;
                p.ellipse(proj_u.x, proj_u.y, pointSize / scales.x, pointSize / scales.y);
                p.ellipse(proj_v.x, proj_v.y, pointSize / scales.x, pointSize / scales.y);
                p.ellipse(proj_w.x, proj_w.y, pointSize / scales.x, pointSize / scales.y);
                p.pop();
            };

            // --- INTERACTION LOGIC ---

            const updateAndRedraw = () => {
                readSliders();
                performCalculations();
                p.redraw();
            };

            const readSliders = () => {
                const dpIds = ['au', 'bu', 'av', 'bv', 'aw', 'bw'];
                const scaleIds = ['x-scale', 'y-scale'];

                dpIds.forEach(id => {
                    const slider = document.getElementById(`slider-${id}`);
                    const valueSpan = document.getElementById(`value-${id}`);
                    dotProducts[id] = parseFloat(slider.value);
                    valueSpan.textContent = dotProducts[id].toFixed(2);
                });

                scaleIds.forEach(id => {
                    const slider = document.getElementById(`slider-${id}`);
                    const valueSpan = document.getElementById(`value-${id}`);
                    const key = id.replace('-scale', '');
                    scales[key] = parseFloat(slider.value);
                    valueSpan.textContent = scales[key];
                });
            };

            const performCalculations = () => {
                try {
                    // 1. Construct measurement matrix M
                    const M = math.matrix([
                        [dotProducts.au, dotProducts.bu],
                        [dotProducts.av, dotProducts.bv],
                        [dotProducts.aw, dotProducts.bw]
                    ]);

                    // 2. SVD on M: M = U * D * V_transpose
                    const svdResult = math.svd(M);
                    const U = svdResult.U;
                    const s = svdResult.s; // Vector of singular values
                    const V = svdResult.V;
                    const S_hat = U;

                    // 3. Solve for L (l_vec)
                    const s1 = U.subset(math.index(0, [0, 1])).toArray()[0];
                    const s2 = U.subset(math.index(1, [0, 1])).toArray()[0];
                    const s3 = U.subset(math.index(2, [0, 1])).toArray()[0];

                    const K = math.matrix([
                        [s1[0] * s1[0], 2 * s1[0] * s1[1], s1[1] * s1[1]],
                        [s2[0] * s2[0], 2 * s2[0] * s2[1], s2[1] * s2[1]],
                        [s3[0] * s3[0], 2 * s3[0] * s3[1], s3[1] * s3[1]]
                    ]);

                    const ones = math.matrix([1, 1, 1]);
                    const l_vec = math.lusolve(K, ones);

                    const l11 = l_vec.get([0, 0]);
                    const l12 = l_vec.get([1, 0]);
                    const l22 = l_vec.get([2, 0]);

                    const L = math.matrix([[l11, l12], [l12, l22]]);

                    // 4. Check if L is positive definite
                    if (l11 <= 0 || math.det(L) <= 0) {
                        throw new Error("Matrix L is not positive definite.");
                    }

                    // 5. Cholesky decomposition: L = Q * Q_transpose
                    const Q = math.cholesky(L);

                    // 6. Calculate S (Motion) and P (Shape)
                    const S = math.multiply(U, Q);

                    const D = math.diag(s);
                    const P = math.multiply(math.inv(Q), D, math.transpose(V));

                    // 7. Extract 2D vectors
                    u = { x: S.get([0, 0]), y: S.get([0, 1]) };
                    v = { x: S.get([1, 0]), y: S.get([1, 1]) };
                    w = { x: S.get([2, 0]), y: S.get([2, 1]) };

                    A = { x: P.get([0, 0]), y: P.get([1, 0]) };
                    B = { x: P.get([0, 1]), y: P.get([1, 1]) };

                    calculationOk = true;
                } catch (e) {
                    calculationOk = false;
                    errorMsg = e.message || "Matrix factorization failed.";
                    console.error(errorMsg);
                }
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
