    <script>
    document.addEventListener('DOMContentLoaded', () => {
        // --- STATE ---
        let currentType = '2A2';
        let currentDimension = '2D';
        let t1 = 0.0;
        let t2 = 0.0;
        let isDragging = false;

        // --- THREE.JS SETUP ---
        let scene, camera, renderer, controls, visualizationGroup;
        const vizContainer = document.getElementById('visualization-container');

        // --- DOM ELEMENTS ---
        const buttons = document.querySelectorAll('#control-panel button');
        const timePad = document.getElementById('time-pad');
        const timeHandle = document.getElementById('time-handle');

        // --- PARAMETRIC FUNCTIONS ---
        const parametricFunctions = {
            '2A1': (u, v, target) => {
                const U = -2 + 4 * u;
                target.set(U + t1 * 1.5, U * U + t2 * 1.5, 0);
            },
            '2A2': (u, v, target) => {
                const U = -1.5 + 3 * u;
                target.set(2 * U**3 + t1 * 1.5, 3 * U**2 + t2 * 1.5, 0);
            },
            '2B2': (u, v, target) => {
                const U = -2 + 4 * u;
                target.set(U * U + t1 * 1.5, U**4 + t2 * 1.5, 0);
            },
            '3A1p': (u, v, target) => {
                const U = -2 + 4 * u; const V = -2 + 4 * v;
                const T1 = t1 * 1.5; const T2 = t2 * 1.5;
                target.set(U, V, 0.3 * (U**2 + V**2) + T1 * U + T2 * V);
            },
            '3A1m': (u, v, target) => {
                const U = -2 + 4 * u; const V = -2 + 4 * v;
                const T1 = t1 * 1.5; const T2 = t2 * 1.5;
                target.set(U, V, -0.3 * (U**2 + V**2) + T1 * U + T2 * V);
            },
            '3A2': (u, v, target) => {
                const U = -1.5 + 3 * u; const V = -2 + 4 * v;
                const T1 = t1 * 1.5; const T2 = t2 * 1.5;
                target.set(2 * U**3 + T1, 3 * U**2 + T2, V);
            },
            '3A3': (u, v, target) => { // Swallowtail
                const U = -1.5 + 3 * u; const V = -4 + 8 * v;
                const T1 = t1 * 1.5; const T2 = t2 * 1.5;
                target.set(3 * U**4 + U**2 * V, -4 * U**3 - 2 * U * V + T1 * 2, V + T2 * 2).multiplyScalar(0.4);
            },
            '3B2p': (u, v, target) => {
                const U = -2 + 4 * u; const V = -2 + 4 * v;
                target.set(U, V, (U**2 + t1)**2 + V**2 + t2);
            },
            '3B2m': (u, v, target) => {
                const U = -2 + 4 * u; const V = -2 + 4 * v;
                target.set(U, V, -(U**4 + t1 * 2 * U**2 + V**2 - t2)).multiplyScalar(0.5);
            },
            '3B3': (u, v, target) => {
                const U = -2 + 4 * u; const V = -2 + 4 * v;
                target.set(U, V, U**3 + t1 * U + V**2 + t2).multiplyScalar(0.5);
            },
            '3C3p': (u, v, target) => { // Elliptic Umbilic
                const U = -2 + 4 * u; const V = -2 + 4 * v;
                target.set(U, V, U**3 - 3 * U * V**2 + t1 * 2 * (U**2 + V**2) + t2 * 4 * U).multiplyScalar(0.2);
            },
            '3C3m': (u, v, target) => { // Hyperbolic Umbilic
                const U = -2 + 4 * u; const V = -2 + 4 * v;
                target.set(U, V, U**3 + V**3 + t1 * 4 * U + t2 * 4 * V).multiplyScalar(0.2);
            }
        };

        // --- INITIALIZATION ---
        function init() {
            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(vizContainer.clientWidth, vizContainer.clientHeight);
            vizContainer.appendChild(renderer.domElement);

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Lighting
            scene.add(new THREE.AmbientLight(0xffffff, 0.5));
            const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight.position.set(5, 10, 7.5);
            scene.add(dirLight);

            // Visualization Group
            visualizationGroup = new THREE.Group();
            scene.add(visualizationGroup);

            // Event Listeners
            window.addEventListener('resize', onWindowResize, false);
            setupControls();

            // Initial render
            updateActiveButton();
            updateHandlePosition();
            generateVisualization();
            animate();
        }

        // --- CORE LOGIC ---
        function generateVisualization() {
            // Clear previous visualization
            while (visualizationGroup.children.length > 0) {
                const child = visualizationGroup.children[0];
                visualizationGroup.remove(child);
                if (child.geometry) child.geometry.dispose();
                if (child.material) child.material.dispose();
            }

            // Setup camera and controls based on dimension
            setupCameraAndControls();

            const func = parametricFunctions[currentType];
            if (!func) return;

            if (currentDimension === '2D') {
                const points = [];
                const divisions = 200;
                for (let i = 0; i <= divisions; i++) {
                    const v = new THREE.Vector3();
                    func(i / divisions, 0, v);
                    points.push(v);
                }
                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: 0x4a69bd, linewidth: 2 });
                const line = new THREE.Line(geometry, material);
                visualizationGroup.add(line);
                camera.position.set(0, 0, 10);
                camera.lookAt(0, 0, 0);

            } else { // 3D
                const geometry = new THREE.ParametricGeometry(func, 50, 50);
                const material = new THREE.MeshPhongMaterial({
                    color: 0xffc34d,
                    side: THREE.DoubleSide,
                    shininess: 50
                });
                const surface = new THREE.Mesh(geometry, material);
                visualizationGroup.add(surface);

                // Add wireframe for specific types to match screenshots
                const wireframeTypes = ['3A3', '3B3', '3C3p', '3C3m'];
                if (wireframeTypes.includes(currentType)) {
                    const wireframeMaterial = new THREE.LineBasicMaterial({
                        color: 0x000000,
                        linewidth: 1,
                        opacity: 0.25,
                        transparent: true
                    });
                    const wireframe = new THREE.LineSegments(new THREE.WireframeGeometry(geometry), wireframeMaterial);
                    visualizationGroup.add(wireframe);
                }

                if (camera.isPerspectiveCamera) {
                    camera.position.set(3, 3, 5);
                    camera.lookAt(0, 0, 0);
                    if(controls) controls.target.set(0,0,0);
                }
            }
        }

        function setupCameraAndControls() {
            const aspect = vizContainer.clientWidth / vizContainer.clientHeight;
            const frustumSize = 6;

            if (currentDimension === '2D') {
                 if (controls) {
                    controls.dispose();
                    controls = null;
                }
                if (!camera || !camera.isOrthographicCamera) {
                    camera = new THREE.OrthographicCamera(frustumSize * aspect / -2, frustumSize * aspect / 2, frustumSize / 2, frustumSize / -2, 0.1, 100);
                }
                camera.left = frustumSize * aspect / -2;
                camera.right = frustumSize * aspect / 2;
                camera.top = frustumSize / 2;
                camera.bottom = frustumSize / -2;
                camera.updateProjectionMatrix();

            } else { // 3D
                if (!camera || !camera.isPerspectiveCamera) {
                     camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
                }
                camera.aspect = aspect;
                camera.updateProjectionMatrix();

                if (!controls) {
                    controls = new THREE.OrbitControls(camera, renderer.domElement);
                    controls.enableDamping = true;
                    controls.dampingFactor = 0.1;
                }
            }
        }

        function onWindowResize() {
            if (!camera || !renderer) return;
            const width = vizContainer.clientWidth;
            const height = vizContainer.clientHeight;
            renderer.setSize(width, height);

            if(camera.isPerspectiveCamera){
                camera.aspect = width / height;
            } else { // Orthographic
                const aspect = width / height;
                const frustumSize = 6;
                camera.left = frustumSize * aspect / -2;
                camera.right = frustumSize * aspect / 2;
                camera.top = frustumSize / 2;
                camera.bottom = frustumSize / -2;
            }
            camera.updateProjectionMatrix();
        }

        function animate() {
            requestAnimationFrame(animate);
            if (controls) controls.update();
            renderer.render(scene, camera);
        }

        // --- UI & CONTROLS ---
        function setupControls() {
            // Button clicks
            buttons.forEach(button => {
                button.addEventListener('click', (e) => {
                    const id = e.target.id;
                    const type = id.replace('btn-', '');
                    const dimension = type.startsWith('2') ? '2D' : '3D';

                    currentType = type;
                    currentDimension = dimension;

                    updateActiveButton();
                    generateVisualization();
                });
            });

            // Time pad interaction
            function handleTimePadEvent(e) {
                if (e.type === 'mousedown') isDragging = true;
                else if (e.type === 'mouseup' || e.type === 'mouseleave') isDragging = false;

                if (isDragging || e.type === 'mousedown') {
                    const rect = timePad.getBoundingClientRect();
                    let x = e.clientX - rect.left;
                    let y = e.clientY - rect.top;

                    // Clamp to pad bounds
                    x = Math.max(0, Math.min(rect.width, x));
                    y = Math.max(0, Math.min(rect.height, y));

                    t1 = (x / rect.width) * 2 - 1;
                    t2 = -((y / rect.height) * 2 - 1); // Y is inverted

                    updateHandlePosition();
                    generateVisualization();
                }
            }

            timePad.addEventListener('mousedown', handleTimePadEvent);
            window.addEventListener('mousemove', handleTimePadEvent); // Use window to catch drags outside pad
            window.addEventListener('mouseup', handleTimePadEvent);
        }

        function updateHandlePosition() {
            const rect = timePad.getBoundingClientRect();
            const x = (t1 + 1) / 2 * rect.width;
            const y = (-t2 + 1) / 2 * rect.height;
            timeHandle.style.left = `${x}px`;
            timeHandle.style.top = `${y}px`;
        }

        function updateActiveButton() {
            buttons.forEach(button => {
                const buttonType = button.id.replace('btn-', '');
                if (buttonType === currentType) {
                    button.classList.add('active');
                } else {
                    button.classList.remove('active');
                }
            });
        }

        // --- RUN ---
        init();
    });
    </script>
</body>
