                    <label for="slider-feed-rate">feed rate</label>
                    <input type="range" id="slider-feed-rate" min="10" max="200" step="1" value="10">
                    <input type="number" id="input-feed-rate" min="10" max="200" step="1" value="10">
                </div>
            </div>
            <div class="control-group-right">
                <div class="slider-container">
                    <label for="slider-ua">UA</label>
                    <input type="range" id="slider-ua" min="5000" max="30000" step="100" value="15000">
                    <input type="number" id="input-ua" min="5000" max="30000" step="100" value="15000">
                </div>
                <div class="slider-container">
                    <label for="slider-dh1">\(\Delta H_1\)</label>
                    <input type="range" id="slider-dh1" min="500" max="5000" step="50" value="1000">
                    <input type="number" id="input-dh1" min="500" max="5000" step="50" value="1000">
                </div>
                <div class="slider-container">
                    <label for="slider-dh2">\(\Delta H_2\)</label>
                    <input type="range" id="slider-dh2" min="500" max="5000" step="50" value="3000">
                    <input type="number" id="input-dh2" min="500" max="5000" step="50" value="3000">
                </div>
            </div>
        </div>
        <div id="plot-area">
             <div id="legend-container"></div>
             <!-- Canvas will be created here by p5.js -->
        </div>
    </div>

<script>
const sketch = (p) => {
    // --- Control and State Variables ---
    let controls = {};
    let state = {};
    let data = {};

    const feedOptions = ["feed A to B", "feed B to A"];
    let currentFeedIndex = 0;

    // --- Simulation Constants ---
    const V = 100; // L
    const T_initial = 330; // K
    const T_coolant = 330; // K
    const T_feed = 330; // K
    const R = 8.314; // J/mol/K
    const Cp_total = 150000; // J/K
    const k1_0 = 3e4; // L^2/(mol^2*h)
    const E1 = 30000; // J/mol
    const k2_0 = 1e5; // L/(mol*h)
    const E2 = 40000; // J/mol
    const total_moles_reactant = 100; // mol

    // --- Plotting Constants ---
    const plotMargin = { top: 60, right: 30, bottom: 50, left: 60 };
    let plotWidth, plotHeight;

    p.setup = function() {
        const plotArea = document.getElementById('plot-area');
        const canvas = p.createCanvas(plotArea.offsetWidth, plotArea.offsetHeight);
        canvas.parent(plotArea);
        canvas.id('plot-canvas');

        plotWidth = p.width - plotMargin.left - plotMargin.right;
        plotHeight = p.height - plotMargin.top - plotMargin.bottom;

        // --- Get Control Elements ---
        controls.selectPlot = p.select('#select-plot');
        controls.cyclerLabel = p.select('#cycler-feed-label');
        controls.btnFeedDown = p.select('#btn-feed-down');
        controls.btnFeedUp = p.select('#btn-feed-up');

        controls.sliderFeedRate = p.select('#slider-feed-rate');
        controls.inputFeedRate = p.select('#input-feed-rate');
        controls.sliderUA = p.select('#slider-ua');
        controls.inputUA = p.select('#input-ua');
        controls.sliderDH1 = p.select('#slider-dh1');
        controls.inputDH1 = p.select('#input-dh1');
        controls.sliderDH2 = p.select('#slider-dh2');
        controls.inputDH2 = p.select('#input-dh2');

        controls.legendContainer = p.select('#legend-container');

        // --- Attach Event Listeners ---
        controls.selectPlot.changed(updateAndRedraw);
        controls.btnFeedUp.mousePressed(cycleFeedUp);
        controls.btnFeedDown.mousePressed(cycleFeedDown);

        const allSliders = [
            controls.sliderFeedRate, controls.inputFeedRate,
            controls.sliderUA, controls.inputUA,
            controls.sliderDH1, controls.inputDH1,
            controls.sliderDH2, controls.inputDH2
        ];
        allSliders.forEach(el => el.input(updateAndRedraw));

        p.noLoop();
        updateAndRedraw();
    };

    function syncSlidersAndInputs() {
        controls.inputFeedRate.value(controls.sliderFeedRate.value());
        controls.sliderFeedRate.value(controls.inputFeedRate.value());

        controls.inputUA.value(controls.sliderUA.value());
        controls.sliderUA.value(controls.inputUA.value());

        controls.inputDH1.value(controls.sliderDH1.value());
        controls.sliderDH1.value(controls.inputDH1.value());

        controls.inputDH2.value(controls.sliderDH2.value());
        controls.sliderDH2.value(controls.inputDH2.value());
    }

    function cycleFeedUp() {
        currentFeedIndex = (currentFeedIndex + 1) % feedOptions.length;
        updateAndRedraw();
    }

    function cycleFeedDown() {
        currentFeedIndex = (currentFeedIndex - 1 + feedOptions.length) % feedOptions.length;
        updateAndRedraw();
    }

    function updateAndRedraw() {
        syncSlidersAndInputs();

        // --- Read all values from controls into state object ---
        state.plotType = controls.selectPlot.value();
        state.feedConfig = feedOptions[currentFeedIndex];
        controls.cyclerLabel.html(state.feedConfig);

        state.feedRate = parseFloat(controls.sliderFeedRate.value());
        state.UA = parseFloat(controls.sliderUA.value());
        state.dH1 = parseFloat(controls.sliderDH1.value());
        state.dH2 = parseFloat(controls.sliderDH2.value());

        // --- Run Simulation ---
        runSimulations();

        // --- Redraw Plot ---
        p.redraw();
    }

    function runSimulations() {
        if (state.plotType === 'selectivity') {
            const batchResults = solveODE('batch', state.feedConfig);
            const semiResults = solveODE('semibatch', state.feedConfig);

            data.sRatio = [];
            data.time = batchResults.time;

            let minRatio = Infinity;
            let maxRatio = -Infinity;

            for (let i = 1; i < batchResults.time.length; i++) {
                const sBatch = batchResults.Nu[i] > 1e-6 ? batchResults.Nd[i] / batchResults.Nu[i] : 0;
                const sSemi = semiResults.Nu[i] > 1e-6 ? semiResults.Nd[i] / semiResults.Nu[i] : 0;
                const ratio = sBatch > 1e-6 ? sSemi / sBatch : 0;

                if (isFinite(ratio)) {
                    data.sRatio.push(ratio);
                    if (ratio < minRatio) minRatio = ratio;
                    if (ratio > maxRatio) maxRatio = ratio;
                } else {
                    data.sRatio.push(0);
                }
            }
            // Add padding to y-axis range
            const range = maxRatio - minRatio;
            state.yMin = minRatio - range * 0.1;
            state.yMax = maxRatio + range * 0.1;
             if (state.yMin === state.yMax) { // handle flat line
                state.yMin -= 1;
                state.yMax += 1;
            }

        } else {
            const results = solveODE(state.plotType, state.feedConfig);
            data = results;
            state.yMin = 0;
            state.yMax = 100;
        }
    }

    function getDerivatives(Y, t, params) {
        const [Na, Nb, Nd, Nu, T] = Y;
        const { simType, feedConfig, feedRate, UA, dH1, dH2 } = params;

        const tf = total_moles_reactant / feedRate;

        // Rate constants
        const k1 = k1_0 * Math.exp(-E1 / (R * T));
        const k2 = k2_0 * Math.exp(-E2 / (R * T));

        // Concentrations
        const Ca = Na / V;
        const Cb = Nb / V;

        // Reaction rates (mol/L/h)
        const r1 = k1 * Ca * Cb * Cb;
        const r2 = k2 * Ca * Cb;

        // Derivatives
        let dNadt = -(r1 + r2) * V;
        let dNbdt = -(r1 + r2) * V;
        const dNddt = r1 * V;
        const dNudt = r2 * V;

        // Handle semibatch feed
        if (simType === 'semibatch') {
            if (feedConfig === 'feed A to B' && t < tf) {
                dNadt += feedRate;
            } else if (feedConfig === 'feed B to A' && t < tf) {
                dNbdt += feedRate;
            }
        }

        // Energy balance
        const dTdt = (UA * (T_coolant - T) + (-dH1 * r1 - dH2 * r2) * V) / Cp_total;

        return [dNadt, dNbdt, dNddt, dNudt, dTdt];
    }

    function rk4Step(Y, t, dt, params) {
        const k1 = getDerivatives(Y, t, params);

        const Y2 = Y.map((y, i) => y + 0.5 * dt * k1[i]);
        const k2 = getDerivatives(Y2, t + 0.5 * dt, params);

        const Y3 = Y.map((y, i) => y + 0.5 * dt * k2[i]);
        const k3 = getDerivatives(Y3, t + 0.5 * dt, params);

        const Y4 = Y.map((y, i) => y + dt * k3[i]);
        const k4 = getDerivatives(Y4, t + dt, params);

        return Y.map((y, i) => y + (dt / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
    }

    function solveODE(simType, feedConfig) {
        const t_end = 2.0;
        const dt = 0.01;
        const n_steps = Math.floor(t_end / dt);

        let Y;
        if (simType === 'batch') {
            Y = [total_moles_reactant, total_moles_reactant, 0, 0, T_initial];
        } else { // semibatch
            if (feedConfig === 'feed A to B') {
                Y = [0, total_moles_reactant, 0, 0, T_initial];
            } else { // feed B to A
                Y = [total_moles_reactant, 0, 0, 0, T_initial];
            }
        }

        const simParams = { ...state, simType, feedConfig };

        const results = { time: [], Na: [], Nb: [], Nd: [], Nu: [], T: [] };

        for (let i = 0; i <= n_steps; i++) {
            const t = i * dt;

            results.time.push(t);
            results.Na.push(Y[0]);
            results.Nb.push(Y[1]);
            results.Nd.push(Y[2]);
            results.Nu.push(Y[3]);
            results.T.push(Y[4]);

            Y = rk4Step(Y, t, dt, simParams);
        }
        return results;
    }

    p.draw = function() {
        if (!data.time) return;
        p.background('#F0F0F0');
        drawAxesAndGrid();
        drawDataCurves();
        updateLegend();
    };

    function drawAxesAndGrid() {
        p.push();
        p.translate(plotMargin.left, plotMargin.top);
        p.stroke(200);
        p.strokeWeight(1);
        p.fill(100);
        p.textSize(12);
        p.textAlign(p.CENTER, p.CENTER);

        // --- X-axis ---
        p.line(0, plotHeight, plotWidth, plotHeight);
        p.text('time (h)', plotWidth / 2, plotHeight + 35);
        for (let t = 0; t <= 2.0; t += 0.5) {
            const x = p.map(t, 0, 2.0, 0, plotWidth);
            p.line(x, plotHeight, x, plotHeight + 5); // Tick mark
            p.line(x, 0, x, plotHeight); // Grid line
            p.text(t.toFixed(1), x, plotHeight + 15);
        }

        // --- Y-axis ---
        const yLabel = state.plotType === 'selectivity' ? 'selectivity ratio' : 'moles (mol)';
        p.push();
        p.translate(-45, plotHeight / 2);
        p.rotate(-p.HALF_PI);
        p.text(yLabel, 0, 0);
        p.pop();

        p.line(0, 0, 0, plotHeight);
        p.textAlign(p.RIGHT, p.CENTER);
        const yTicks = 5;
        for (let i = 0; i <= yTicks; i++) {
            const val = p.lerp(state.yMin, state.yMax, i / yTicks);
            const y = p.map(val, state.yMin, state.yMax, plotHeight, 0);
            p.line(0, y, -5, y); // Tick mark
            p.line(0, y, plotWidth, y); // Grid line
            p.text(val.toFixed(state.plotType === 'selectivity' ? 1 : 0), -10, y);
        }

        // Draw plot border
        p.noFill();
        p.stroke(0);
        p.rect(0, 0, plotWidth, plotHeight);

        p.pop();
    }

    function drawDashedLine(points, dash, gap) {
        p.beginShape();
        let drawing = true;
        let drawn = 0;
        for (let i = 0; i < points.length - 1; i++) {
            const p1 = points[i];
            const p2 = points[i+1];
            const d = p.dist(p1.x, p1.y, p2.x, p2.y);
            const segmentVec = p5.Vector.sub(p2, p1);
            segmentVec.normalize();

            let traveled = 0;
            while(traveled < d){
                if(drawing){
                    const drawLen = p.min(dash - drawn, d - traveled);
                    const startPoint = p5.Vector.add(p1, p5.Vector.mult(segmentVec, traveled));
                    const endPoint = p5.Vector.add(startPoint, p5.Vector.mult(segmentVec, drawLen));
                    p.vertex(startPoint.x, startPoint.y);
                    p.vertex(endPoint.x, endPoint.y);
                    drawn += drawLen;
                    if(drawn >= dash){
                        drawn = 0;
                        drawing = false;
                        p.endShape();
                        p.beginShape();
                    }
                } else {
                    const gapLen = p.min(gap - drawn, d - traveled);
                    drawn += gapLen;
                    if(drawn >= gap){
                        drawn = 0;
                        drawing = true;
                    }
                }
                traveled += drawing ? (p.min(dash-drawn, d-traveled)) : (p.min(gap-drawn, d-traveled));
            }
        }
        p.endShape();
    }

    function drawDataCurves() {
        p.push();
        p.translate(plotMargin.left, plotMargin.top);
        p.noFill();
        p.strokeWeight(2);

        const mapToScreen = (t, val) => {
            const x = p.map(t, 0, 2.0, 0, plotWidth);
            const y = p.map(val, state.yMin, state.yMax, plotHeight, 0);
            return p.createVector(x, y);
        };

        if (state.plotType === 'selectivity') {
            p.stroke('#800080'); // Purple
            p.beginShape();
            for (let i = 0; i < data.sRatio.length; i++) {
                const pt = mapToScreen(data.time[i+1], data.sRatio[i]);
                p.vertex(pt.x, pt.y);
            }
            p.endShape();

        } else {
            // Na: Green, dashed
            p.stroke('#2ca02c');
            const naPoints = data.time.map((t, i) => mapToScreen(t, data.Na[i]));
            drawDashedLine(naPoints, 5, 5);

            // Nb: Black, dashed
            p.stroke('#000000');
            const nbPoints = data.time.map((t, i) => mapToScreen(t, data.Nb[i]));
            drawDashedLine(nbPoints, 5, 5);

            // Nd: Blue, solid
            p.stroke('#1f77b4');
            p.beginShape();
            data.time.forEach((t, i) => {
                const pt = mapToScreen(t, data.Nd[i]);
                p.vertex(pt.x, pt.y);
            });
            p.endShape();

            // Nu: Red, solid
            p.stroke('#d62728');
            p.beginShape();
            data.time.forEach((t, i) => {
                const pt = mapToScreen(t, data.Nu[i]);
                p.vertex(pt.x, pt.y);
            });
            p.endShape();
        }

        p.pop();
    }

    function updateLegend() {
        let legendHTML = '';
        if (state.plotType === 'selectivity') {
            legendHTML = `
                <div class="legend-item">
                    <div class="legend-line" style="background-color: #800080;"></div>
                    <span>semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)</span>
                </div>
            `;
        } else {
            legendHTML = `
                <div class="legend-item">
                    <div class="legend-line" style="background: repeating-linear-gradient(90deg, #2ca02c, #2ca02c 5px, transparent 5px, transparent 10px);"></div>
                    <span>\\(N_a\\)</span>
                </div>
                <div class="legend-item">
                    <div class="legend-line" style="background: repeating-linear-gradient(90deg, #000000, #000000 5px, transparent 5px, transparent 10px);"></div>
                    <span>\\(N_b\\)</span>
                </div>
                <div class="legend-item">
                    <div class="legend-line" style="background-color: #1f77b4;"></div>
                    <span>\\(N_d\\)</span>
                </div>
                <div class="legend-item">
                    <div class="legend-line" style="background-color: #d62728;"></div>
                    <span>\\(N_u\\)</span>
                </div>
            `;
        }
        controls.legendContainer.html(legendHTML);
        if (window.MathJax) {
            MathJax.typesetPromise([controls.legendContainer.elt]);
        }
    }
};

new p5(sketch);
</script>
</body>
</html>
