                p.strokeWeight(1);
                p.line(goalPos.x - 5, goalPos.y, goalPos.x + 5, goalPos.y);
                p.line(goalPos.x, goalPos.y - 5, goalPos.x, goalPos.y + 5);
            };

            const drawStart = () => {
                p.noStroke();
                p.fill('green');
                p.circle(startPos.x, startPos.y, 8);
            };

            const drawTree = () => {
                // Draw edges for RRT*
                if (treeType === 'rrt-star') {
                    p.stroke(0);
                    p.strokeWeight(0.5);
                    for (let i = 1; i < nodes.length; i++) {
                        const node = nodes[i];
                        const parent = nodes[node.parent];
                        p.line(node.x, node.y, parent.x, parent.y);
                    }
                }

                // Draw all nodes
                p.noStroke();
                p.fill('red');
                for (let i = 1; i < nodes.length; i++) {
                    p.circle(nodes[i].x, nodes[i].y, 4);
                }
                 // Draw the single starting node if that's all there is
                if (nodes.length === 1) {
                    p.fill('red');
                    p.circle(nodes[0].x, nodes[0].y, 4);
                }

            };

            const drawPath = () => {
                if (goalReached && path.length > 0) {
                    p.stroke('green');
                    p.strokeWeight(3);
                    p.noFill();
                    p.beginShape();
                    path.forEach(node => p.vertex(node.x, node.y));
                    p.endShape();
                }
            };

            const updateStatusText = () => {
                if (goalReached) {
                    statusTextEl.textContent = `${nodes.length} nodes, path length ${pathLength.toFixed(2)}`;
                } else {
                    statusTextEl.textContent = `${nodes.length} node${nodes.length !== 1 ? 's' : ''}, goal not yet reached`;
                }
            };

            // --- Simulation Logic ---
            const resetSimulation = (fullReset = true) => {
                if (fullReset) {
                    startPos = defaultStartPos.copy();
                    goalPos = defaultGoalPos.copy();
                }
                generateObstacles();
                nodes = [{
                    x: startPos.x,
                    y: startPos.y,
                    parent: null,
                    cost: 0
                }];
                path = [];
                goalReached = false;
                goalNodeIndex = -1;
                pathLength = 0;
                p.redraw();
            };

            const generateObstacles = () => {
                obstacles = [];
                const wallThickness = 8;
                // Add a border
                obstacles.push({ x: 0, y: 0, w: canvasW - wallThickness/2, h: wallThickness/2 });
                obstacles.push({ x: 0, y: canvasH - wallThickness/2, w: canvasW - wallThickness/2, h: wallThickness/2 });
                obstacles.push({ x: 0, y: 0, w: wallThickness/2, h: canvasH - wallThickness/2 });
                obstacles.push({ x: canvasW - wallThickness/2, y: 0, w: wallThickness/2, h: canvasH - wallThickness/2 });

                if (obstacleType === 'narrow-passage') {
                    obstacles.push({ x: canvasW * 0.3, y: 0, w: wallThickness/2, h: canvasH * 0.4 });
                    obstacles.push({ x: canvasW * 0.3, y: canvasH * 0.6, w: wallThickness/2, h: canvasH * 0.4 });
                    obstacles.push({ x: canvasW * 0.65, y: 0, w: wallThickness/2, h: canvasH * 0.25 });
                    obstacles.push({ x: canvasW * 0.65, y: canvasH * 0.45, w: wallThickness/2, h: canvasH * 0.55 });
                } else if (obstacleType === 'random-rects') {
                    for (let i = 0; i < 15; i++) {
                        obstacles.push({
                            x: p.random(canvasW),
                            y: p.random(canvasH),
                            w: p.random(50, 150),
                            h: p.random(50, 150)
                        });
                    }
                }
            };

            const handleAddNodes = (count) => {
                for (let i = 0; i < count; i++) {
                    if (!goalReached || treeType === 'rrt-star') {
                        addOneNode();
                    } else {
                        break;
                    }
                }
                p.redraw();
            };

            const addOneNode = () => {
                const randomPoint = samplePoint();
                const nearestNodeIndex = findNearestNode(randomPoint);
                const nearestNode = nodes[nearestNodeIndex];

                const steeredPoint = steer(nearestNode, randomPoint);
                if (isCollisionFree(nearestNode, steeredPoint)) {
                    if (treeType === 'rrt-star') {
                        addNodeRRTStar(steeredPoint, nearestNodeIndex);
                    } else {
                        addNodeGeneric(steeredPoint, nearestNodeIndex);
                    }
                }
            };

            const addNodeGeneric = (point, parentIndex) => {
                const newNode = {
                    x: point.x,
                    y: point.y,
                    parent: parentIndex,
                    cost: 0 // Not used in RT/RRT
                };
                nodes.push(newNode);
                const newNodeIndex = nodes.length - 1;
                checkGoalReached(newNodeIndex);
            };

            const addNodeRRTStar = (point, nearestNodeIndex) => {
                const neighbors = findNeighbors(point, rrtStarNeighborRadius);
                if (neighbors.length === 0) { // Default to nearest if no neighbors found
                    neighbors.push(nearestNodeIndex);
                }

                let minCost = Infinity;
                let bestParentIndex = -1;

                // Find best parent
                for (const neighborIndex of neighbors) {
                    const neighborNode = nodes[neighborIndex];
                    if (isCollisionFree(neighborNode, point)) {
                        const cost = neighborNode.cost + p.dist(neighborNode.x, neighborNode.y, point.x, point.y);
                        if (cost < minCost) {
                            minCost = cost;
                            bestParentIndex = neighborIndex;
                        }
                    }
                }

                if (bestParentIndex === -1) return; // No path found

                const newNode = {
                    x: point.x,
                    y: point.y,
                    parent: bestParentIndex,
                    cost: minCost
                };
                nodes.push(newNode);
                const newNodeIndex = nodes.length - 1;

                // Rewire
                for (const neighborIndex of neighbors) {
                    const neighborNode = nodes[neighborIndex];
                    const costViaNewNode = newNode.cost + p.dist(newNode.x, newNode.y, neighborNode.x, neighborNode.y);
                    if (costViaNewNode < neighborNode.cost && isCollisionFree(newNode, neighborNode)) {
                        neighborNode.parent = newNodeIndex;
                        neighborNode.cost = costViaNewNode;
                    }
                }

                checkGoalReached(newNodeIndex);
            };

            // --- Algorithm Helpers ---
            const samplePoint = () => {
                if ((treeType === 'rrt' || treeType === 'rrt-star') && p.random() < explorationBias) {
                    return goalPos;
                }
                return { x: p.random(canvasW), y: p.random(canvasH) };
            };

            const findNearestNode = (point) => {
                let nearestIndex = -1;
                let minDistSq = Infinity;
                for (let i = 0; i < nodes.length; i++) {
                    const dSq = (nodes[i].x - point.x)**2 + (nodes[i].y - point.y)**2;
                    if (dSq < minDistSq) {
                        minDistSq = dSq;
                        nearestIndex = i;
                    }
                }
                return nearestIndex;
            };

            const findNeighbors = (point, radius) => {
                const neighbors = [];
                const radiusSq = radius ** 2;
                for (let i = 0; i < nodes.length; i++) {
                    const dSq = (nodes[i].x - point.x)**2 + (nodes[i].y - point.y)**2;
                    if (dSq < radiusSq) {
                        neighbors.push(i);
                    }
                }
                return neighbors;
            };

            const steer = (from, to) => {
                const d = p.dist(from.x, from.y, to.x, to.y);
                if (d < stepSize) {
                    return { x: to.x, y: to.y };
                }
                return {
                    x: from.x + (to.x - from.x) * stepSize / d,
                    y: from.y + (to.y - from.y) * stepSize / d
                };
            };

            const isCollisionFree = (nodeA, nodeB) => {
                const p1 = p.createVector(nodeA.x, nodeA.y);
                const p2 = p.createVector(nodeB.x, nodeB.y);
                for (const o of obstacles) {
                    // Simple sampling-based collision check
                    const dist = p1.dist(p2);
                    const samples = Math.ceil(dist / 5);
                    for (let i = 0; i <= samples; i++) {
                        const t = i / samples;
                        const pt = p5.Vector.lerp(p1, p2, t);
                        if (pt.x >= o.x && pt.x <= o.x + o.w && pt.y >= o.y && pt.y <= o.y + o.h) {
                            return false;
                        }
                    }
                }
                return true;
            };

            const checkGoalReached = (newNodeIndex) => {
                const newNode = nodes[newNodeIndex];
                const d = p.dist(newNode.x, newNode.y, goalPos.x, goalPos.y);

                if (d < goalRadius) {
                    const newPath = tracePath(newNodeIndex);
                    const newLength = calculatePathLength(newPath);

                    if (!goalReached || newLength < pathLength) {
                        goalReached = true;
                        goalNodeIndex = newNodeIndex;
                        path = newPath;
                        pathLength = newLength;
                    }
                }
            };

            const reCheckGoal = () => {
                if (goalReached) return;
                for(let i = 0; i < nodes.length; i++) {
                    checkGoalReached(i);
                    if(goalReached) break;
                }
                p.redraw();
            }

            const tracePath = (endIndex) => {
                const tracedPath = [];
                let currIndex = endIndex;
                while (currIndex !== null) {
                    tracedPath.push(nodes[currIndex]);
                    currIndex = nodes[currIndex].parent;
                }
                return tracedPath.reverse();
            };

            const calculatePathLength = (pathArr) => {
                let length = 0;
                for (let i = 0; i < pathArr.length - 1; i++) {
                    length += p.dist(pathArr[i].x, pathArr[i].y, pathArr[i+1].x, pathArr[i+1].y);
                }
                return length;
            };

            // --- Event Handlers ---
            const attachEventListeners = () => {
                // Tree Type
                [btnRt, btnRrt, btnRrtStar].forEach(btn => {
                    btn.addEventListener('click', (e) => {
                        treeType = e.target.id.replace('btn-', '');
                        [btnRt, btnRrt, btnRrtStar].forEach(b => b.classList.remove('active'));
                        e.target.classList.add('active');
                        sliderBias.disabled = (treeType === 'rt');
                        resetSimulation(false);
                    });
                });

                // Obstacle Type
                selectObstacle.addEventListener('change', (e) => {
                    obstacleType = e.target.value;
                    resetSimulation(false);
                });

                // Add Nodes
                for (const count in btnAddNodes) {
                    btnAddNodes[count].addEventListener('click', () => handleAddNodes(parseInt(count)));
                }

                // Sliders
                sliderBias.addEventListener('input', (e) => {
                    explorationBias = parseFloat(e.target.value);
                    biasValueLabel.textContent = explorationBias.toFixed(2);
                });

                sliderRadius.addEventListener('input', (e) => {
                    const val = parseFloat(e.target.value);
                    goalRadius = val * 10; // Scale for visualization
                    radiusValueLabel.textContent = val.toFixed(1);
                    reCheckGoal();
                    p.redraw();
                });

                // Reset Button
                btnReset.addEventListener('click', () => resetSimulation(true));
            };
        };

        new p5(sketch, 'canvas-container');
    });
</script>

</body>
</html>
