    <script>
        let scene, camera, renderer, controls;
        let ionPathMesh = null;

        // --- DOM Elements ---
        const sliders = {
            t: document.getElementById('slider-t'),
            rho0: document.getElementById('slider-rho0'),
            zeta0: document.getElementById('slider-zeta0'),
            e0: document.getElementById('slider-e0'),
            v: document.getElementById('slider-v'),
