        const inputs = {
            t: document.getElementById('input-t'),
            rho0: document.getElementById('input-rho0'),
            zeta0: document.getElementById('input-zeta0'),
            e0: document.getElementById('input-e0'),
            v: document.getElementById('input-v'),
            lambda: document.getElementById('input-lambda')
        };

        // --- Initialization ---
        function init() {
            // Scene Setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            const container = document.getElementById('visualization-container');
            camera = new THREE.PerspectiveCamera(60, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.set(4, 4, 4);
            camera.lookAt(0, 1, 0);

            // Renderer
            const canvas = document.getElementById('three-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(container.clientWidth, container.clientHeight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 1, 0);
            controls.update();

            // Lighting
            scene.add(new THREE.AmbientLight(0x666666));
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Axes Helper
            const axesHelper = new THREE.AxesHelper(4);
            scene.add(axesHelper);

            // --- Static Geometry ---
            // Potential Surface
            const surfaceFunc = (u, v, target) => {
                const rho = 0.4 + u * 2.1;
                const theta = v * 2 * Math.PI;
                const x = rho * Math.cos(theta);
                const y_surf = rho * rho + 1 / (rho * rho);
                const z_surf = -rho * Math.sin(theta);
                target.set(x, y_surf, z_surf);
            };
            const surfaceGeo = new THREE.ParametricGeometry(surfaceFunc, 64, 64);
            const surfaceMat = new THREE.MeshStandardMaterial({
                color: 0xE69943,
                metalness: 0.4,
                roughness: 0.5,
                side: THREE.DoubleSide
            });
            const surfaceMesh = new THREE.Mesh(surfaceGeo, surfaceMat);
            scene.add(surfaceMesh);

            // Central Cylinder
            const cylinderGeo = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
            const cylinderMat = new THREE.MeshStandardMaterial({
                color: 0xcccccc,
                metalness: 0.1,
                roughness: 0.9
            });
            const cylinderMesh = new THREE.Mesh(cylinderGeo, cylinderMat);
            cylinderMesh.position.set(0, 1.25, 0);
            scene.add(cylinderMesh);

            // Handle window resize
            window.addEventListener('resize', onWindowResize, false);

            // Setup control listeners
            setupControls();

            // Initial visualization
            updateVisualization();

            // Start animation loop
            animate();
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        // --- Animation Loop ---
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // --- Control Logic ---
        function setupControls() {
            for (const key in sliders) {
                // Link slider to number input
                sliders[key].addEventListener('input', () => {
                    inputs[key].value = sliders[key].value;
                    updateVisualization();
                });

                // Link number input to slider
                inputs[key].addEventListener('input', () => {
                    sliders[key].value = inputs[key].value;
                    updateVisualization();
                });
            }
        }

        // --- Simulation and Visualization Update ---
        function updateVisualization() {
            // Read parameters from controls
            const params = {
                t: parseFloat(inputs.t.value),
                rho0: parseFloat(inputs.rho0.value),
                zeta0: parseFloat(inputs.zeta0.value),
                e0: parseFloat(inputs.e0.value),
                nu: parseFloat(inputs.v.value), // 'v' slider is 'nu' in formulas
                lambda: parseFloat(inputs.lambda.value)
            };

            // Calculate the ion path
            const points = calculatePath(params);

            // Remove old path if it exists
            if (ionPathMesh) {
                scene.remove(ionPathMesh);
                ionPathMesh.geometry.dispose();
                ionPathMesh.material.dispose();
            }

            if (points.length < 2) return;

            // Create new path geometry
            const curve = new THREE.CatmullRomCurve3(points);
            const tubeGeo = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
            const tubeMat = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
            ionPathMesh = new THREE.Mesh(tubeGeo, tubeMat);
            ionPathMesh.name = 'ionPath'; // Assign a name for easy removal

            // Add new path to the scene
            scene.add(ionPathMesh);
        }

        // --- Physics Calculation ---
        function calculatePath(params) {
            const { t, rho0, zeta0, e0, nu, lambda } = params;

            // ODE system: Y = [ρ, dρ/ds, φ]
            const derivatives = (s, Y) => {
                const rho = Y[0];
                const d_rho_ds = Y[1];

                const dY_ds = [];
                dY_ds[0] = d_rho_ds;
                // d²ρ/ds²
                dY_ds[1] = -(2 * rho - 2 / (rho**3)) - 4 * (lambda**2) * rho + 4 * e0 / (rho**3);
                // dφ/ds
                dY_ds[2] = 2 * (e0 / (rho**2) - lambda);

                return dY_ds;
            };

            // RK4 solver step
            const rk4Step = (f, s, Y, h) => {
                const k1 = f(s, Y);
                const k2 = f(s + h / 2, Y.map((y, i) => y + h * k1[i] / 2));
                const k3 = f(s + h / 2, Y.map((y, i) => y + h * k2[i] / 2));
                const k4 = f(s + h, Y.map((y, i) => y + h * k3[i]));

                return Y.map((y, i) => y + h / 6 * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
            };

            const points = [];
            let Y = [rho0, 0, 0]; // Initial conditions: [ρ₀, dρ/ds=0, φ=0]
            const ds = 0.05; // Integration step size

            for (let s = 0; s <= t; s += ds) {
                // Unpack state
                const rho_i = Y[0];
                const phi_i = Y[2];

                // Calculate Cartesian coordinates
                const u_rho = rho_i**2 + 1 / rho_i**2;
                const sqrt_arg = e0 - u_rho;

                // Calculate z (vertical) coordinate
                const z_i = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.sqrt(2) * nu * s + zeta0);

                // Calculate x,y in the rotational plane
                const x_i = rho_i * Math.cos(phi_i);
                const y_i = rho_i * Math.sin(phi_i);

                // Map to THREE.js coordinates (x, z_calc, -y_calc)
                points.push(new THREE.Vector3(x_i, z_i, -y_i));

                // Perform next integration step
                Y = rk4Step(derivatives, s, Y, ds);
            }

            return points;
        }

        // --- Start the application ---
        init();

    </script>
