<body>
    <div id="app-container">
        <div id="control-panel">
            <h4>time</h4>
            <input type="range" id="slider-time" min="0" max="672" step="1" value="0">
            <div class="button-group">
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-back">«</button>
                <button id="btn-reset">⥀</button>
                <button id="btn-step-forward">»</button>
            </div>
            <p id="display-time">0 d 0 h</p>
            <hr>
            <h4>Moon's rotational period related to the current value</h4>
            <div id="rotation-selector">
                <button id="btn-rot-half">half</button>
                <button id="btn-rot-normal">normal</button>
                <button id="btn-rot-double">double</button>
                <button id="btn-rot-none">no rotation</button>
            </div>
            <hr>
            <div class="checkbox-group">
                <input type="checkbox" id="check-day-night">
                <label for="check-day-night">day/night</label>
            </div>
            <div class="checkbox-group">
                <input type="checkbox" id="check-near-side" checked>
                <label for="check-near-side">near side of the Moon</label>
            </div>
            <div class="checkbox-group">
                <input type="checkbox" id="check-earth-moon-line" checked>
                <label for="check-earth-moon-line">Earth-Moon line</label>
            </div>
            <div class="checkbox-group">
                <input type="checkbox" id="check-moon-only">
                <label for="check-moon-only">the Moon only</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script>
        // --- p5.js Sketch ---

        // State variables
        let currentTime = 0;
        let isPlaying = false;
        let rotationMode = 'normal';
        let showDayNight = false;
        let showNearSide = true;
        let showEarthMoonLine = true;
        let showMoonOnly = false;

        // DOM elements
        let sliderTime, btnPlayPause, btnStepBack, btnReset, btnStepForward, displayTime;
        let rotationButtons = {};
        let checkDayNight, checkNearSide, checkEarthMoonLine, checkMoonOnly;

        // Visualization constants
        const EARTH_RADIUS = 80;
        const MOON_RADIUS = 25;
        const ORBIT_RADIUS = 250;
        const MAX_TIME = 672; // 28 days * 24 hours
        const EARTH_PERIOD = 24;
        const MOON_PERIOD = 672;


        function setup() {
            const visArea = document.getElementById('visualization-area');
            const canvas = createCanvas(visArea.offsetWidth, visArea.offsetHeight);
            canvas.parent('visualization-area');
            canvas.id('main-canvas');

            angleMode(RADIANS);

            // Get DOM elements
            sliderTime = select('#slider-time');
            btnPlayPause = select('#btn-play-pause');
            btnStepBack = select('#btn-step-back');
            btnReset = select('#btn-reset');
            btnStepForward = select('#btn-step-forward');
            displayTime = select('#display-time');

            rotationButtons = {
                half: select('#btn-rot-half'),
                normal: select('#btn-rot-normal'),
                double: select('#btn-rot-double'),
                none: select('#btn-rot-none'),
            };

            checkDayNight = select('#check-day-night');
            checkNearSide = select('#check-near-side');
            checkEarthMoonLine = select('#check-earth-moon-line');
            checkMoonOnly = select('#check-moon-only');

            // --- Initialize UI state and add event listeners ---

            // Time controls
            sliderTime.input(() => {
                currentTime = sliderTime.value();
                updateTimeDisplay();
                redraw();
            });

            btnPlayPause.mousePressed(() => {
                isPlaying = !isPlaying;
                if (isPlaying) {
                    btnPlayPause.html('❚❚');
                    loop();
                } else {
                    btnPlayPause.html('▶');
                    noLoop();
                }
            });

            btnReset.mousePressed(() => {
                currentTime = 0;
                updateAllTimeUI();
                redraw();
            });

            btnStepForward.mousePressed(() => {
                currentTime = min(Number(currentTime) + 1, MAX_TIME);
                updateAllTimeUI();
                redraw();
            });

            btnStepBack.mousePressed(() => {
                currentTime = max(Number(currentTime) - 1, 0);
                updateAllTimeUI();
                redraw();
            });

            // Rotation controls
            for (const [mode, button] of Object.entries(rotationButtons)) {
                button.mousePressed(() => {
                    rotationMode = mode;
                    updateRotationButtons();
                    redraw();
                });
            }

            // Checkbox controls
            checkDayNight.changed(() => { showDayNight = checkDayNight.checked(); redraw(); });
            checkNearSide.changed(() => { showNearSide = checkNearSide.checked(); redraw(); });
            checkEarthMoonLine.changed(() => { showEarthMoonLine = checkEarthMoonLine.checked(); redraw(); });
            checkMoonOnly.changed(() => { showMoonOnly = checkMoonOnly.checked(); redraw(); });

            // Initial UI setup
            updateAllTimeUI();
            updateRotationButtons();

            noLoop(); // Start paused
            redraw(); // Draw initial state
        }

        function draw() {
            if (isPlaying) {
                currentTime = (Number(currentTime) + 0.5) % (MAX_TIME + 1);
                updateAllTimeUI();
            }

            background(255);
            translate(width / 2, height / 2);

            // --- Calculations ---
            const moonOrbitAngle = map(currentTime, 0, MOON_PERIOD, 0, TWO_PI) - HALF_PI; // Start at top
            const earthRotationAngle = map(currentTime % EARTH_PERIOD, 0, EARTH_PERIOD, 0, TWO_PI);
            let moonRotationAngle = 0;
            switch(rotationMode) {
                case 'normal': moonRotationAngle = map(currentTime, 0, MOON_PERIOD, 0, TWO_PI); break;
                case 'half': moonRotationAngle = map(currentTime, 0, MOON_PERIOD / 2, 0, TWO_PI); break;
                case 'double': moonRotationAngle = map(currentTime, 0, MOON_PERIOD * 2, 0, TWO_PI); break;
                case 'none': moonRotationAngle = 0; break;
            }

            let moonX = ORBIT_RADIUS * cos(moonOrbitAngle);
            let moonY = ORBIT_RADIUS * sin(moonOrbitAngle);

            if (showMoonOnly) {
                moonX = 0;
                moonY = 0;
            } else {
                // --- Draw Earth ---
                push();

                // Landmasses
                fill(34, 139, 34, 200); // Green land
                noStroke();
                // Simplified Americas
                beginShape();
                curveVertex(-60, -20); curveVertex(-50, -50); curveVertex(-20, 0); curveVertex(-35, 40); curveVertex(-45, 60); curveVertex(-60, 20);
                endShape(CLOSE);
                // Simplified Eurasia/Africa
                beginShape();
                curveVertex(20, -60); curveVertex(60, -40); curveVertex(50, 20); curveVertex(30, 60); curveVertex(10, 40); curveVertex(10, -30);
                endShape(CLOSE);

                // Earth Body
                noFill();
                stroke(0);
                strokeWeight(2);
                fill('#3B82F6');
                ellipse(0, 0, EARTH_RADIUS * 2);

                // North Pole
                fill(255);
                noStroke();
                ellipse(0, 0, 25);
                fill(0);
                ellipse(0, 0, 3);

                // Red Stick Figure
                push();
                rotate(earthRotationAngle);
                translate(0, -EARTH_RADIUS);
                drawStickFigure('#E11D48', 10);
                pop();

                // Day/Night Terminator
                if (showDayNight) {
                    fill(0, 128);
                    noStroke();
                    arc(0, 0, EARTH_RADIUS * 2, EARTH_RADIUS * 2, HALF_PI, PI + HALF_PI, CHORD);
                }

                pop(); // End Earth drawing

                // --- Draw Earth-Moon Line ---
                if (showEarthMoonLine) {
                    drawDashedLine(0, 0, moonX, moonY, 10);
                }
            }

            // --- Draw Moon ---
            push();
            translate(moonX, moonY);

            // Moon body
            fill('#808080');
            stroke(0);
            strokeWeight(1.5);
            ellipse(0, 0, MOON_RADIUS * 2);

            // Near Side Marker and Magenta Figure
            if (showNearSide) {
                push();
                // The "near side" always faces Earth. In its own coordinate system, this is "down" (positive y)
                // because the Moon starts at the top of the screen (-y in world space).
                // So the rotation aligns the Moon's local "down" with the direction to Earth.
                rotate(moonRotationAngle - moonOrbitAngle - HALF_PI);

                // Green Arc (near side)
                fill('#22C55E');
                noStroke();
                arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, 0, PI, CHORD);

                // Magenta Stick Figure
                translate(0, -MOON_RADIUS);
                drawStickFigure('#D946EF', 8);
                pop();
            }

            // Day/Night Terminator
            if (showDayNight) {
                fill(0, 128);
                noStroke();
                arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, HALF_PI, PI + HALF_PI, CHORD);
            }

            pop(); // End Moon drawing
        }

        // --- Helper Functions ---
        function updateTimeDisplay() {
            const days = Math.floor(currentTime / 24);
            const hours = Math.floor(currentTime % 24);
            displayTime.html(`${days} d ${hours} h`);
        }

        function updateAllTimeUI() {
            sliderTime.value(currentTime);
            updateTimeDisplay();
        }

        function updateRotationButtons() {
            for (const [mode, button] of Object.entries(rotationButtons)) {
                if (mode === rotationMode) {
                    button.addClass('active');
                } else {
                    button.removeClass('active');
                }
            }
        }

        function drawStickFigure(clr, size) {
            stroke(clr);
            strokeWeight(size / 5);
            noFill();
            // Head
            ellipse(0, -size * 1.2, size * 0.8, size * 0.8);
            // Body
            line(0, -size * 0.8, 0, size * 0.2);
            // Arms
            line(-size * 0.6, -size * 0.4, size * 0.6, -size * 0.4);
            // Legs
            line(0, size * 0.2, -size * 0.5, size);
            line(0, size * 0.2, size * 0.5, size);
        }

        function drawDashedLine(x1, y1, x2, y2, dashLen) {
            drawingContext.setLineDash([dashLen, dashLen]);
            stroke(0);
            strokeWeight(1.5);
            line(x1, y1, x2, y2);
            drawingContext.setLineDash([]); // Reset to solid line
        }

        function windowResized() {
            const visArea = document.getElementById('visualization-area');
            resizeCanvas(visArea.offsetWidth, visArea.offsetHeight);
            redraw();
        }
    </script>
</body>
</html>
