<script>
document.addEventListener('DOMContentLoaded', () => {
    // --- Constants and Parameters ---
    const constants = {
        v0: 1000,      // cm⁻¹
        B_upper: 0.98, // cm⁻¹
        A_upper: 1.95, // cm⁻¹
        B_lower: 1.0,  // cm⁻¹
        A_lower: 2.0,  // cm⁻¹
        zeta: 0.2,
        T: 300,        // K
        k: 0.695,      // cm⁻¹/K (Boltzmann constant)
        J_max: 60,
    };

    // --- DOM Element References ---
    const plotDiv = document.getElementById('plot-div');
    const controls = {
        view: document.querySelectorAll('input[name="view-selector"]'),
        k: document.querySelectorAll('input[name="k-selector"]'),
        subbandType: document.querySelectorAll('input[name="subband-type"]'),
        branch: document.querySelectorAll('input[name="branch-selector"]'),
        kGroup: document.getElementById('k-selector-group'),
        subbandTypeGroup: document.getElementById('subband-type-group'),
        branchGroup: document.getElementById('branch-selector-group'),
        radioSubbandNegative: document.getElementById('radio-subband-negative'),
        sliderXLower: document.getElementById('slider-x-lower'),
        sliderXUpper: document.getElementById('slider-x-upper'),
        spanXLower: document.getElementById('span-x-lower-value'),
        spanXUpper: document.getElementById('span-x-upper-value'),
    };

    // --- Data Calculation ---
    function getEnergy(J, K, A, B) {
        return B * J * (J + 1) + (A - B) * K * K;
    }

    function calculateTransitions(K_lower) {
        const { v0, B_upper, A_upper, B_lower, A_lower, zeta, T, k, J_max } = constants;
        const data = {
            positive: { P: { x: [], y: [] }, Q: { x: [], y: [] }, R: { x: [], y: [] } },
            negative: { P: { x: [], y: [] }, Q: { x: [], y: [] }, R: { x: [], y: [] } },
        };

        // Positive Sub-band (ΔK = +1)
        const K_upper_pos = K_lower + 1;
        const dE_AK_pos = (A_upper - B_upper) * Math.pow(K_upper_pos, 2) - (A_lower - B_lower) * Math.pow(K_lower, 2) - 2 * A_upper * zeta * K_upper_pos;

        // Negative Sub-band (ΔK = -1)
        if (K_lower > 0) {
            const K_upper_neg = K_lower - 1;
            const dE_AK_neg = (A_upper - B_upper) * Math.pow(K_upper_neg, 2) - (A_lower - B_lower) * Math.pow(K_lower, 2) + 2 * A_upper * zeta * K_upper_neg;

            for (let J = 0; J <= J_max; J++) {
                if (J < K_lower) continue;
                const intensity = (2 * J + 1) * Math.exp(-getEnergy(J, K_lower, A_lower, B_lower) / (k * T));

                // P-branch (neg)
                if (J - 1 >= K_upper_neg) {
                    const dE_BJ = B_upper * (J - 1) * J - B_lower * J * (J + 1);
                    data.negative.P.x.push(v0 + dE_AK_neg + dE_BJ);
                    data.negative.P.y.push(intensity);
                }
                // Q-branch (neg)
                if (J >= K_upper_neg) {
                    const dE_BJ = (B_upper - B_lower) * J * (J + 1);
                    data.negative.Q.x.push(v0 + dE_AK_neg + dE_BJ);
                    data.negative.Q.y.push(intensity);
                }
                // R-branch (neg)
                const dE_BJ_R = B_upper * (J + 1) * (J + 2) - B_lower * J * (J + 1);
                data.negative.R.x.push(v0 + dE_AK_neg + dE_BJ_R);
                data.negative.R.y.push(intensity);
            }
        }

        for (let J = 0; J <= J_max; J++) {
            if (J < K_lower) continue;
            const intensity = (2 * J + 1) * Math.exp(-getEnergy(J, K_lower, A_lower, B_lower) / (k * T));

            // P-branch (pos)
            if (J - 1 >= K_upper_pos) {
                const dE_BJ = B_upper * (J - 1) * J - B_lower * J * (J + 1);
                data.positive.P.x.push(v0 + dE_AK_pos + dE_BJ);
                data.positive.P.y.push(intensity);
            }
            // Q-branch (pos)
            if (J >= K_upper_pos) {
                const dE_BJ = (B_upper - B_lower) * J * (J + 1);
                data.positive.Q.x.push(v0 + dE_AK_pos + dE_BJ);
                data.positive.Q.y.push(intensity);
            }
            // R-branch (pos)
            const dE_BJ_R = B_upper * (J + 1) * (J + 2) - B_lower * J * (J + 1);
            data.positive.R.x.push(v0 + dE_AK_pos + dE_BJ_R);
            data.positive.R.y.push(intensity);
        }

        return data;
    }


    // --- Plotting Logic ---
    function updatePlot() {
        const state = {
            view: document.querySelector('input[name="view-selector"]:checked').value,
            k: parseInt(document.querySelector('input[name="k-selector"]:checked').value),
            subbandType: document.querySelector('input[name="subband-type"]:checked').value,
            branch: document.querySelector('input[name="branch-selector"]:checked').value,
        };

        let traces = [];
        let plotTitle = '';

        if (state.view === 'full') {
            let all_x = [], all_y = [];
            for (let kVal = 0; kVal <= 5; kVal++) {
                const subBandData = calculateTransitions(kVal);
                ['positive', 'negative'].forEach(type => {
                    ['P', 'Q', 'R'].forEach(branch => {
                        all_x.push(...subBandData[type][branch].x);
                        all_y.push(...subBandData[type][branch].y);
                    });
                });
            }
            const maxIntensity = Math.max(...all_y);
            const normalized_y = all_y.map(y => y / maxIntensity);

            traces.push({
                x: all_x,
                y: normalized_y,
                type: 'bar',
                width: 0.1,
                marker: { color: 'black' }
            });
            plotTitle = '';

        } else { // 'sub' view
            const subBandData = calculateTransitions(state.k);
            let subBandY = [];
            ['positive', 'negative'].forEach(type => {
                 ['P', 'Q', 'R'].forEach(branch => {
                    subBandY.push(...subBandData[type][branch].y);
                });
            });
            const maxIntensity = Math.max(...subBandY, 0) || 1;

            plotTitle = `K = ${state.k} sub-band`;

            const createTrace = (branchData, color) => ({
                x: branchData.x,
                y: branchData.y.map(y => y / maxIntensity),
                type: 'bar',
                width: 0.1,
                marker: { color: color }
            });

            if (state.subbandType === 'full') {
                traces.push(createTrace({ x: [].concat(...Object.values(subBandData.positive).map(b => b.x)), y: [].concat(...Object.values(subBandData.positive).map(b => b.y)) }, 'red'));
                traces.push(createTrace({ x: [].concat(...Object.values(subBandData.negative).map(b => b.x)), y: [].concat(...Object.values(subBandData.negative).map(b => b.y)) }, 'blue'));
            } else { // positive or negative
                plotTitle += `\n"${state.subbandType}" sub-band`;
                const typeData = subBandData[state.subbandType];
                const color = state.subbandType === 'positive' ? 'red' : 'blue';
                if (state.branch === 'all') {
                    traces.push(createTrace({ x: [].concat(...Object.values(typeData).map(b => b.x)), y: [].concat(...Object.values(typeData).map(b => b.y)) }, color));
                } else {
                    traces.push(createTrace(typeData[state.branch], color));
                }
            }
        }

        const layout = {
            title: plotTitle,
            xaxis: { 
                title: 'wavenumber (cm⁻¹)', 
                range: [controls.sliderXLower.value, controls.sliderXUpper.value]
            },
            yaxis: { title: 'absorbance', range: [0, 1.2] },
            showlegend: false,
            bargap: 0,
            margin: { t: 50, b: 50, l: 50, r: 20 }
        };

        Plotly.newPlot(plotDiv, traces, layout, {responsive: true});
    }

    // --- Control State Logic ---
    function updateControlStates() {
        const view = document.querySelector('input[name="view-selector"]:checked').value;
        const kVal = document.querySelector('input[name="k-selector"]:checked').value;
        const subbandType = document.querySelector('input[name="subband-type"]:checked').value;

        if (view === 'full') {
            controls.kGroup.classList.add('disabled');
            controls.subbandTypeGroup.classList.add('disabled');
            controls.branchGroup.classList.add('hidden');
        } else { // sub-band view
            controls.kGroup.classList.remove('disabled');
            controls.subbandTypeGroup.classList.remove('disabled');

            // K=0 has no negative sub-band
            controls.radioSubbandNegative.disabled = (kVal === '0');
            if (kVal === '0' && document.getElementById('radio-subband-negative').checked) {
                document.getElementById('radio-subband-full').checked = true;
            }

            if (subbandType === 'full') {
                controls.branchGroup.classList.add('hidden');
            } else { // positive or negative
                controls.branchGroup.classList.remove('hidden');
                controls.branchGroup.classList.remove('disabled'); // Just in case
            }
        }
    }

    // --- Event Listeners ---
    document.getElementById('control-panel').addEventListener('change', () => {
        updateControlStates();
        updatePlot();
    });

    controls.sliderXLower.addEventListener('input', (e) => {
        let lowerVal = parseInt(e.target.value);
        let upperVal = parseInt(controls.sliderXUpper.value);
        if (lowerVal >= upperVal) {
            lowerVal = upperVal - 1;
            e.target.value = lowerVal;
        }
        controls.spanXLower.textContent = lowerVal;
        Plotly.relayout(plotDiv, 'xaxis.range[0]', lowerVal);
    });

    controls.sliderXUpper.addEventListener('input', (e) => {
        let upperVal = parseInt(e.target.value);
        let lowerVal = parseInt(controls.sliderXLower.value);
        if (upperVal <= lowerVal) {
            upperVal = lowerVal + 1;
            e.target.value = upperVal;
        }
        controls.spanXUpper.textContent = upperVal;
        Plotly.relayout(plotDiv, 'xaxis.range[1]', upperVal);
    });

    // --- Initialization ---
    controls.spanXLower.textContent = controls.sliderXLower.value;
    controls.spanXUpper.textContent = controls.sliderXUpper.value;
    updateControlStates();
    updatePlot();
});
</script>
</body>
</html>
