<script type="module">
    import * as THREE from 'three';
    import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

    // --- STATE & CONSTANTS ---
    let scene, camera, renderer, controls;
    let patternGroup, patternMesh, boxHelper;
    let currentWaveType = 'P';
    const patternMaterial = new THREE.MeshStandardMaterial({
        color: 0xD2B48C,
        side: THREE.DoubleSide,
        metalness: 0.3,
        roughness: 0.6
    });

    // --- DOM ELEMENTS ---
    const canvasContainer = document.getElementById('canvas-container');
    const sliderStrike = document.getElementById('slider-strike');
    const sliderDip = document.getElementById('slider-dip');
    const sliderRake = document.getElementById('slider-rake');
    const strikeValue = document.getElementById('strike-value');
    const dipValue = document.getElementById('dip-value');
    const rakeValue = document.getElementById('rake-value');
    const btnP = document.getElementById('btn-p');
    const btnSV = document.getElementById('btn-sv');
    const btnSH = document.getElementById('btn-sh');
    const checkboxBox = document.getElementById('checkbox-box');

    // --- INITIALIZATION ---
    function init() {
        // Scene setup
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        // Camera setup
        const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
        camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
        camera.position.set(0, 0, 8);
        camera.lookAt(0, 0, 0);

        // Renderer setup
        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        canvasContainer.appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
        scene.add(ambientLight);
        const dirLight1 = new THREE.DirectionalLight(0xffffff, 1.5);
        dirLight1.position.set(5, 5, 5);
        scene.add(dirLight1);
        const dirLight2 = new THREE.DirectionalLight(0xffffff, 1.0);
        dirLight2.position.set(-5, -5, 2);
        scene.add(dirLight2);

        // Controls
        controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;
        controls.addEventListener('change', render);

        // Helpers and Overlays
        createCompass();
        createUpArrow();

        // Main object group for rotation
        patternGroup = new THREE.Object3D();
        scene.add(patternGroup);

        // Bounding box
        boxHelper = new THREE.BoxHelper(patternGroup, 0x333333);
        boxHelper.visible = false;
        scene.add(boxHelper);

        // Initial mesh creation and orientation
        updatePatternMesh();

        // Setup event listeners
        setupEventListeners();

        // Initial render
        render();

        // Start animation loop
        animate();
    }

    // --- VISUALIZATION LOGIC ---

    function getPatternGeometry(waveType) {
        const parametricFunction = (u, v, target) => {
            const phi = u * 2 * Math.PI; // Azimuthal angle (0 to 2pi)
            const theta = v * Math.PI;   // Polar angle (0 to pi)
            let r = 0;

            switch (waveType) {
                case 'P':
                    r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                    break;
                case 'SV':
                    r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                    break;
                case 'SH':
                    r = Math.abs(Math.cos(theta) * Math.cos(phi));
                    break;
            }

            const x = r * Math.sin(theta) * Math.cos(phi);
            const y = r * Math.sin(theta) * Math.sin(phi);
            const z = r * Math.cos(theta);

            target.set(x, y, z);
        };

        return new THREE.ParametricGeometry(parametricFunction, 64, 64);
    }

    function updatePatternMesh() {
        if (patternMesh) {
            patternGroup.remove(patternMesh);
            patternMesh.geometry.dispose();
        }

        const geometry = getPatternGeometry(currentWaveType);
        patternMesh = new THREE.Mesh(geometry, patternMaterial);
        patternGroup.add(patternMesh);

        // The box helper needs to be updated to the new object
        boxHelper.setFromObject(patternGroup);

        updateRotation();
    }

    function updateRotation() {
        const strike = parseFloat(sliderStrike.value);
        const dip = parseFloat(sliderDip.value);
        const rake = parseFloat(sliderRake.value);

        const strikeRad = THREE.MathUtils.degToRad(strike);
        const dipRad = THREE.MathUtils.degToRad(dip);
        const rakeRad = THREE.MathUtils.degToRad(rake);

        // Reset rotation before applying new ones
        patternGroup.quaternion.identity();

        // 1. Strike: Rotate around world's Z-axis (Up). Clockwise from North (Y) is a negative rotation.
        patternGroup.rotateOnWorldAxis(new THREE.Vector3(0, 0, 1), -strikeRad);

        // 2. Dip: Rotate around the object's local X-axis.
        patternGroup.rotateOnAxis(new THREE.Vector3(1, 0, 0), dipRad);

        // 3. Rake/Slip: Rotate around the object's local Y-axis.
        patternGroup.rotateOnAxis(new THREE.Vector3(0, 1, 0), rakeRad);

        boxHelper.update();
        render();
    }

    // --- HELPERS ---

    function createTextSprite(message, parameters = {}) {
        const fontface = parameters.fontface || 'Arial';
        const fontsize = parameters.fontsize || 18;
        const borderThickness = parameters.borderThickness || 4;
        const borderColor = parameters.borderColor || { r: 0, g: 0, b: 0, a: 1.0 };
        const backgroundColor = parameters.backgroundColor || { r: 255, g: 255, b: 255, a: 0.0 };
        const textColor = parameters.textColor || { r: 0, g: 0, b: 0, a: 1.0 };

        const canvas = document.createElement('canvas');
        const context = canvas.getContext('2d');
        context.font = `Bold ${fontsize}px ${fontface}`;
        const metrics = context.measureText(message);
        const textWidth = metrics.width;

        context.fillStyle = `rgba(${backgroundColor.r}, ${backgroundColor.g}, ${backgroundColor.b}, ${backgroundColor.a})`;
        context.strokeStyle = `rgba(${borderColor.r}, ${borderColor.g}, ${borderColor.b}, ${borderColor.a})`;
        context.lineWidth = borderThickness;

        context.fillStyle = `rgba(${textColor.r}, ${textColor.g}, ${textColor.b}, ${textColor.a})`;
        context.fillText(message, borderThickness, fontsize + borderThickness);

        const texture = new THREE.Texture(canvas);
        texture.needsUpdate = true;

        const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
        const sprite = new THREE.Sprite(spriteMaterial);
        sprite.scale.set(0.5 * fontsize, 0.25 * fontsize, 0.75 * fontsize);
        return sprite;
    }

    function createCompass() {
        const compassGroup = new THREE.Group();
        const radius = 3.5;

        // Dashed circle
        const circleGeom = new THREE.BufferGeometry().setFromPoints(
            new THREE.Path().absarc(0, 0, radius, 0, Math.PI * 2, false).getSpacedPoints(100)
        );
        const dashedMaterial = new THREE.LineDashedMaterial({ color: 0x888888, dashSize: 0.1, gapSize: 0.1 });
        const circle = new THREE.Line(circleGeom, dashedMaterial);
        circle.computeLineDistances();
        compassGroup.add(circle);

        // Cardinal directions
        const directions = { 'N': [0, 1], 'E': [1, 0], 'S': [0, -1], 'W': [-1, 0] };
        for (const [label, pos] of Object.entries(directions)) {
            const sprite = createTextSprite(label, { fontsize: 48, textColor: {r:0,g:0,b:0,a:1} });
            sprite.position.set(pos[0] * (radius + 0.5), pos[1] * (radius + 0.5), 0);
            compassGroup.add(sprite);
        }

        // Degree markings
        for (let i = 0; i < 360; i += 30) {
            if (i % 90 === 0) continue; // Skip cardinal points
            const angleRad = THREE.MathUtils.degToRad(i);
            const label = `${i}°`;
            const sprite = createTextSprite(label, { fontsize: 24, textColor: {r:80,g:80,b:80,a:1} });
            // Position based on angle from North (Y-axis), clockwise
            const x = (radius + 0.4) * Math.sin(angleRad);
            const y = (radius + 0.4) * Math.cos(angleRad);
            sprite.position.set(x, y, 0);
            compassGroup.add(sprite);
        }

        scene.add(compassGroup);
    }

    function createUpArrow() {
        const dir = new THREE.Vector3(0, 0, 1);
        const origin = new THREE.Vector3(0, 0, 0);
        const length = 1.5;
        const hex = 0x000000;
        const arrowHelper = new THREE.ArrowHelper(dir, origin, length, hex, 0.4, 0.2);
        scene.add(arrowHelper);

        const uSprite = createTextSprite('U', { fontsize: 48 });
        uSprite.position.set(0, 0, length + 0.3);
        scene.add(uSprite);
    }

    // --- EVENT LISTENERS ---
    function setupEventListeners() {
        // Sliders
        sliderStrike.addEventListener('input', (e) => {
            strikeValue.textContent = e.target.value;
            updateRotation();
        });
        sliderDip.addEventListener('input', (e) => {
            dipValue.textContent = e.target.value;
            updateRotation();
        });
        sliderRake.addEventListener('input', (e) => {
            rakeValue.textContent = e.target.value;
            updateRotation();
        });

        // Buttons
        const buttons = [btnP, btnSV, btnSH];
        buttons.forEach(button => {
            button.addEventListener('click', () => {
                buttons.forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                currentWaveType = button.id.split('-')[1].toUpperCase();
                updatePatternMesh();
            });
        });

        // Checkbox
        checkboxBox.addEventListener('change', (e) => {
            boxHelper.visible = e.target.checked;
            render();
        });

        // Resize
        window.addEventListener('resize', () => {
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
            render();
        });
    }

    // --- RENDER & ANIMATION LOOP ---
    function render() {
        renderer.render(scene, camera);
    }

    function animate() {
        requestAnimationFrame(animate);
        controls.update(); // only required if controls.enableDamping = true
        // No need to call render() here because controls.update() fires a 'change' event
    }

    // --- RUN ---
    init();

    // Render MathJax
    MathJax.typeset();
</script>

</body>
</html>
