                <div>
                    <input type="radio" name="cpr" value="4" id="cpr-4">
                    <label for="cpr-4">4</label>
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                    <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                    <label for="cpr-8">8</label>
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>
            <div id="inverse-control-group" class="control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        // State variables
        let locators = [];
        let draggedLocator = -1;

        // UI Element References
        let inverseCheckbox;

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            canvas.parent('canvas-container');
            // This prevents the page from scrolling on touch devices when interacting with the canvas
            canvas.elt.style.touchAction = 'none';

            angleMode(RADIANS);

            // Get a reference to the checkbox
            inverseCheckbox = select('#checkbox-inverse');

            // Initialize locators
            locators.push(createVector(-50, 0));
            locators.push(createVector(50, 0));

            // Add event listeners to all inputs to trigger a redraw
            document.querySelectorAll('input[name="rings"], input[name="cpr"], #checkbox-inverse').forEach(input => {
                input.addEventListener('change', () => redraw());
            });

            // Redraw initially
            redraw();
            noLoop(); // We only need to redraw on user input
        }

        function draw() {
            // Read values from controls
            const numRings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            const numCirclesPerRing = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            const isInverse = inverseCheckbox.checked();

            // Setup drawing environment
            background('#C1D4E6');
            translate(width / 2, height / 2);

            // Generate the base set of circles based on controls
            const originalCircles = generateOriginalCircles(numRings, numCirclesPerRing);

            if (isInverse) {
                drawInverseMode(originalCircles);
            } else {
                drawNormalMode(originalCircles);
            }
        }

        function generateOriginalCircles(numRings, N) {
            const circles = [];
            if (N < 2) return circles;

            const R_max = width / 2 * 0.9;
            const angle_s = PI / N;

            if (sin(angle_s) === 0) return circles; // Avoid division by zero

            const k = (1 - sin(angle_s)) / (1 + sin(angle_s));
            const R_c_outer = R_max / (1 + sin(angle_s));
            const r_outer = R_c_outer * sin(angle_s);

            for (let i = 0; i < numRings; i++) {
                const scale_factor = pow(k, i);
                const R_c = R_c_outer * scale_factor;
                const r = r_outer * scale_factor;

                for (let j = 0; j < N; j++) {
                    const angle = j * 2 * PI / N;
                    const cx = R_c * cos(angle);
                    const cy = R_c * sin(angle);
                    circles.push({ center: createVector(cx, cy), radius: r });
                }
            }
            return circles;
        }

        function drawNormalMode(circles) {
            // Draw guide lines for the outermost ring
            stroke('#A0A0A0');
            strokeWeight(1);
            const N = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            for (let i = 0; i < N && i < circles.length; i++) {
                line(0, 0, circles[i].center.x, circles[i].center.y);
            }

            // Draw the circles
            noStroke();
            fill('#8C929D');
            stroke(0);
            strokeWeight(1);
            for (const c of circles) {
                circle(c.center.x, c.center.y, c.radius * 2);
            }
        }

        function drawInverseMode(originalCircles) {
            // Calculate inversion circle properties
            const C_inv = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            const R_inv = locators[0].dist(locators[1]) / 2;

            // Draw inverted circles first
            noStroke();
            fill(220, 100, 220, 128); // rgba(220, 100, 220, 0.5)
            stroke(0);
            strokeWeight(1);
            for (const c of originalCircles) {
                invertAndDrawShape(c, C_inv, R_inv);
            }

            // Draw the inversion circle
            noStroke();
            fill(255, 0, 0, 76); // rgba(255, 0, 0, 0.3)
            stroke(0);
            strokeWeight(1);
            if(R_inv > 0) circle(C_inv.x, C_inv.y, R_inv * 2);

            // Draw locators and connecting line
            strokeWeight(1);
            stroke('#FF0000');
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);
            noStroke();
            fill('#FF0000');
            circle(locators[0].x, locators[0].y, 8);
            circle(locators[1].x, locators[1].y, 8);
        }

        function invertAndDrawShape(origCircle, C_inv, R_inv) {
            if (R_inv <= 0) return;

            const d_sq = C_inv.dist(origCircle.center) ** 2;
            const r_sq = origCircle.radius ** 2;
            const d = sqrt(d_sq);

            // Case 1: Inversion of a circle not passing through the center of inversion
            // This also covers the case where the center of inversion is inside the circle.
            const denominator = d_sq - r_sq;
            if (abs(denominator) > 1e-5) { // Check if it's not a line
                const s = (R_inv ** 2) / denominator;
                const C_new = p5.Vector.add(C_inv, p5.Vector.mult(p5.Vector.sub(origCircle.center, C_inv), s));
                const r_new = abs(s) * origCircle.radius;
                circle(C_new.x, C_new.y, r_new * 2);
            } 
            // Case 2: Inversion of a circle passing through the center of inversion -> a line
            else {
                const vec_to_center = p5.Vector.sub(origCircle.center, C_inv);
                if (vec_to_center.magSq() < 1e-5) return; // Cannot determine line direction

                // The line is perpendicular to the vector from C_inv to origCircle.center
                // The point on the line closest to C_inv is P = C_inv + (R_inv^2 / (2*d)) * (C_orig - C_inv).normalize()
                const p_on_line = p5.Vector.add(C_inv, p5.Vector.mult(vec_to_center.copy().normalize(), (R_inv**2) / (2 * d)));

                // The line's direction is perpendicular to vec_to_center
                const line_dir = vec_to_center.copy().rotate(HALF_PI);

                // Draw a very long line segment to simulate an infinite line
                const p1 = p5.Vector.add(p_on_line, p5.Vector.mult(line_dir, 2 * width));
                const p2 = p5.Vector.sub(p_on_line, p5.Vector.mult(line_dir, 2 * width));
                line(p1.x, p1.y, p2.x, p2.y);
            }
        }

        function mousePressed() {
            if (inverseCheckbox.checked()) {
                const mx = mouseX - width / 2;
                const my = mouseY - height / 2;
                const mousePos = createVector(mx, my);

                let dist0 = mousePos.dist(locators[0]);
                let dist1 = mousePos.dist(locators[1]);

                if (dist0 < 10) {
                    draggedLocator = 0;
                    loop(); // Start loop for smooth dragging
                } else if (dist1 < 10) {
                    draggedLocator = 1;
                    loop();
                }
            }
        }

        function mouseDragged() {
            if (draggedLocator !== -1) {
                const mx = mouseX - width / 2;
                const my = mouseY - height / 2;
                locators[draggedLocator].set(mx, my);
            }
            // The draw loop is already running if we are dragging
            return false; // prevent default browser behavior
        }

        function mouseReleased() {
            if (draggedLocator !== -1) {
                draggedLocator = -1;
                noLoop(); // Stop continuous redraw when not dragging
            }
        }
    </script>
</body>
</html>
