<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-logCa">\(C_a\)</label>
                <input type="range" id="slider-logCa" min="-4" max="0" value="-1" step="0.01">
            </div>
            <div class="slider-group">
                <label for="slider-pka">\(pK_a\)</label>
                <input type="range" id="slider-pka" min="-4" max="7" value="1.2" step="0.1">
            </div>
        </div>
        <div id="visualization-container">
            <!-- p5.js canvas will be inserted here -->
            <div id="formula-strong" class="formula">
                \( \color{maroon}{pH = - \log C_a} \)
            </div>
            <div id="formula-weak" class="formula">
                \( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)
            </div>
        </div>
    </div>

    <script>
        const sketch = (p) => {
            let sliderLogCa, sliderPka;
            let logCa, pKa;

            const pKaMin = -4, pKaMax = 7;
            const pHMin = 0, pHMax = 7;
            let margin;
            let plotWidth, plotHeight;

            p.setup = () => {
                const canvas = p.createCanvas(600, 500);
                canvas.parent('visualization-container');
                canvas.id('p5-canvas');

                margin = { top: 40, right: 40, bottom: 40, left: 50 };
                plotWidth = p.width - margin.left - margin.right;
                plotHeight = p.height - margin.top - margin.bottom;

                sliderLogCa = p.select('#slider-logCa');
                sliderPka = p.select('#slider-pka');

                sliderLogCa.input(p.redraw);
                sliderPka.input(p.redraw);

                p.noLoop();
                p.redraw();
            };

            p.draw = () => {
                logCa = parseFloat(sliderLogCa.value());
                pKa = parseFloat(sliderPka.value());

                p.background(250);

                // --- Draw Axes and Grid ---
                p.push();
                p.translate(margin.left, margin.top);

                // Draw axes
                p.stroke(50);
                p.strokeWeight(1);
                // X-axis
                p.line(0, plotHeight, plotWidth, plotHeight);
                // Y-axis
                p.line(mapX(0), 0, mapX(0), plotHeight);

                // Axis labels
                p.noStroke();
                p.fill(50);
                p.textAlign(p.CENTER, p.TOP);
                p.text("pKa", plotWidth, plotHeight + 5);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text("pH", mapX(0) - 5, -10);

                // Ticks
                p.stroke(150);
                p.strokeWeight(0.5);
                p.textAlign(p.CENTER, p.TOP);
                for (let i = Math.ceil(pKaMin); i <= Math.floor(pKaMax); i++) {
                    if (i % 2 === 0) {
                        let x = mapX(i);
                        p.line(x, plotHeight, x, plotHeight + 5);
                        p.noStroke();
                        p.text(i, x, plotHeight + 8);
                        p.stroke(150);
                    }
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = Math.ceil(pHMin); i <= Math.floor(pHMax); i++) {
                     if (i > 0 && i % 2 === 0) {
                        let y = mapY(i);
                        p.line(mapX(0), y, mapX(0) - 5, y);
                        p.noStroke();
                        p.text(i, mapX(0) - 8, y);
                        p.stroke(150);
                    }
                }

                // --- Draw Curves ---
                // Strong Acid Approximation (Maroon)
                p.stroke(139, 0, 0); // maroon
                p.strokeWeight(2);
                const strongAcidPH = -logCa;
                p.line(mapX(pKaMin), mapY(strongAcidPH), mapX(0), mapY(strongAcidPH));

                // Weak Acid Approximation (Green)
                p.stroke(0, 128, 0); // green
                const weakAcidPH_start = 0.5 * (pKaMin - logCa);
                const weakAcidPH_end = 0.5 * (pKaMax - logCa);
                p.line(mapX(pKaMin), mapY(weakAcidPH_start), mapX(pKaMax), mapY(weakAcidPH_end));

                // Exact pH Curve (Blue)
                p.stroke(0, 0, 205); // blue
                p.strokeWeight(3);
                p.noFill();
                p.beginShape();
                for(let pk = pKaMin; pk <= pKaMax; pk += 0.05) {
                    const exactPH = calculateExactPH(pk, logCa);
                    p.vertex(mapX(pk), mapY(exactPH));
                }
                p.endShape();

                // --- Draw Indicator Point ---
                const currentPH = calculateExactPH(pKa, logCa);
                p.fill(255, 165, 0); // orange
                p.stroke(50);
                p.strokeWeight(1.5);
                p.circle(mapX(pKa), mapY(currentPH), 10);

                p.pop();
            };

            function mapX(val) {
                return p.map(val, pKaMin, pKaMax, 0, plotWidth);
            }

            function mapY(val) {
                return p.map(val, pHMin, pHMax, plotHeight, 0);
            }

            function calculateExactPH(currentPka, currentLogCa) {
                const Ca = Math.pow(10, currentLogCa);
                const Ka = Math.pow(10, -currentPka);

                // Solve quadratic equation for [H+]: [H+]^2 + Ka[H+] - Ka*Ca = 0
                // a=1, b=Ka, c=-Ka*Ca
                const h_plus = (-Ka + Math.sqrt(Ka * Ka + 4 * Ka * Ca)) / 2;

                // pH = -log10([H+])
                return -Math.log10(h_plus);
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
