                    <label for="radio-a-star">A*</label>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-step">algorithm step</label>
                <div class="slider-container">
                    <input type="range" id="slider-step" min="1" max="1" value="1">
                    <span id="label-step">+ 1</span>
                </div>
            </div>

            <div class="control-group">
                <label>starting point</label>
                <div id="joystick-start" class="joystick-area">
                    <div id="handle-start" class="joystick-handle"></div>
                </div>
            </div>

            <div class="control-group">
                <label>end point</label>
                <div id="joystick-end" class="joystick-area">
                    <div id="handle-end" class="joystick-handle"></div>
                </div>
            </div>

            <div class="control-group">
                <label for="select-obstacles">obstacles</label>
                <select id="select-obstacles">
                    <option value="1-square">1 square</option>
                    <option value="2-squares">2 squares</option>
                    <option value="l-shaped-wall">L-shaped wall</option>
                </select>
            </div>
        </div>

        <!-- Right column: Visualization Canvas -->
        <div id="canvas-container">
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
    document.addEventListener('DOMContentLoaded', () => {

        // --- GLOBAL STATE & CONFIG ---
        const GRID_SIZE = 50;
        let algorithmHistory = [];
        let p5Instance;

        const state = {
            algorithm: 'dijkstra',
            startPoint: { x: 0.15, y: 0.85 }, // Normalized 0-1
            endPoint: { x: 0.85, y: 0.15 },   // Normalized 0-1
            obstacles: '1-square',
        };

        // --- DOM ELEMENTS ---
        const radioDijkstra = document.getElementById('radio-dijkstra');
        const radioAStar = document.getElementById('radio-a-star');
        const stepSlider = document.getElementById('slider-step');
        const stepLabel = document.getElementById('label-step');
        const obstacleSelect = document.getElementById('select-obstacles');
        const startJoystick = { area: document.getElementById('joystick-start'), handle: document.getElementById('handle-start') };
        const endJoystick = { area: document.getElementById('joystick-end'), handle: document.getElementById('handle-end') };

        // --- UTILITY: PRIORITY QUEUE (MIN-HEAP) ---
        class PriorityQueue {
            constructor() { this.elements = []; }
            enqueue(element, priority) {
                this.elements.push({ element, priority });
                this.elements.sort((a, b) => a.priority - b.priority);
            }
            dequeue() { return this.elements.shift().element; }
            isEmpty() { return this.elements.length === 0; }
        }

        // --- OBSTACLE DEFINITIONS ---
        const obstacleLayouts = {
            '1-square': () => {
                const obstacles = new Set();
                for (let i = 18; i < 32; i++) {
                    for (let j = 18; j < 32; j++) {
                        obstacles.add(`${i},${j}`);
                    }
                }
                return obstacles;
            },
            '2-squares': () => {
                const obstacles = new Set();
                for (let i = 10; i < 20; i++) {
                    for (let j = 20; j < 30; j++) {
                        obstacles.add(`${i},${j}`);
                    }
                }
                for (let i = 30; i < 40; i++) {
                    for (let j = 10; j < 20; j++) {
                        obstacles.add(`${i},${j}`);
                    }
                }
                return obstacles;
            },
            'l-shaped-wall': () => {
                const obstacles = new Set();
                for (let i = 15; i < 35; i++) {
                    for (let j = 25; j < 30; j++) {
                        obstacles.add(`${i},${j}`);
                    }
                }
                for (let i = 30; i < 35; i++) {
                    for (let j = 10; j < 25; j++) {
                        obstacles.add(`${i},${j}`);
                    }
                }
                return obstacles;
            }
        };

        // --- PATHFINDING ALGORITHMS ---
        function heuristic(a, b) { // Manhattan distance
            return Math.abs(a.x - b.x) + Math.abs(a.y - b.y);
        }

        function getNeighbors(node, gridSize) {
            const neighbors = [];
            const directions = [
                { x: 0, y: 1, cost: 1 }, { x: 0, y: -1, cost: 1 },
                { x: 1, y: 0, cost: 1 }, { x: -1, y: 0, cost: 1 },
                { x: 1, y: 1, cost: Math.sqrt(2) }, { x: 1, y: -1, cost: Math.sqrt(2) },
                { x: -1, y: 1, cost: Math.sqrt(2) }, { x: -1, y: -1, cost: Math.sqrt(2) }
            ];
            for (const dir of directions) {
                const neighbor = { x: node.x + dir.x, y: node.y + dir.y, cost: dir.cost };
                if (neighbor.x >= 0 && neighbor.x < gridSize && neighbor.y >= 0 && neighbor.y < gridSize) {
                    neighbors.push(neighbor);
                }
            }
            return neighbors;
        }

        function runPathfinding(start, end, obstacles, algorithm) {
            const history = [];
            const frontier = new PriorityQueue();
            frontier.enqueue(start, 0);

            const cameFrom = {};
            const costSoFar = {};
            const closedSet = new Set();

            const startKey = `${start.x},${start.y}`;
            cameFrom[startKey] = null;
            costSoFar[startKey] = 0;

            let pathFound = false;

            while (!frontier.isEmpty()) {
                const current = frontier.dequeue();
                const currentKey = `${current.x},${current.y}`;

                if (current.x === end.x && current.y === end.y) {
                    pathFound = true;
                    break;
                }

                closedSet.add(currentKey);

                // Add frame to history
                const openSetCoords = frontier.elements.map(item => item.element);
                history.push({
                    openSet: new Set(openSetCoords.map(n => `${n.x},${n.y}`)),
                    closedSet: new Set(closedSet),
                    path: []
                });

                for (const neighbor of getNeighbors(current, GRID_SIZE)) {
                    const neighborKey = `${neighbor.x},${neighbor.y}`;
                    if (obstacles.has(neighborKey) || closedSet.has(neighborKey)) {
                        continue;
                    }

                    const newCost = costSoFar[currentKey] + neighbor.cost;
                    if (!(neighborKey in costSoFar) || newCost < costSoFar[neighborKey]) {
                        costSoFar[neighborKey] = newCost;
                        const priority = (algorithm === 'a-star')
                            ? newCost + heuristic(neighbor, end)
                            : newCost;
                        frontier.enqueue(neighbor, priority);
                        cameFrom[neighborKey] = current;
                    }
                }
            }

            // Reconstruct and add final path frame
            const finalPath = [];
            if (pathFound) {
                let current = end;
                while (current.x !== start.x || current.y !== start.y) {
                    finalPath.unshift(current);
                    const currentKey = `${current.x},${current.y}`;
                    current = cameFrom[currentKey];
                }
                finalPath.unshift(start);
            }

            const openSetCoords = frontier.elements.map(item => item.element);
            history.push({
                openSet: new Set(openSetCoords.map(n => `${n.x},${n.y}`)),
                closedSet: new Set(closedSet),
                path: finalPath
            });

            return history;
        }


        // --- P5.JS SKETCH ---
        const sketch = (p) => {
            p5Instance = p;
            let canvas;

            const colors = {
                bg: p.color(255),
                obstacle: p.color(0),
                startEnd: p.color(52, 168, 83),
                openSet: p.color(189, 224, 254),
                closedSet: p.color(0, 0, 205),
                path: p.color(255, 0, 0)
            };

            p.setup = () => {
                const container = document.getElementById('canvas-container');
                const size = Math.min(container.clientWidth, container.clientHeight) - 20;
                canvas = p.createCanvas(size, size);
                canvas.parent('canvas-container');
                p.noLoop();

                // Initial setup and run
                initControls();
                runSimulation();
            };

            p.draw = () => {
                p.background(colors.bg);
                const step = parseInt(stepSlider.value, 10);
                const frame = algorithmHistory[step - 1];
                if (!frame) return;

                const cellSize = p.width / GRID_SIZE;
                const { openSet, closedSet, path } = frame;

                p.noStroke();
                // Draw sets
                for (let i = 0; i < GRID_SIZE; i++) {
                    for (let j = 0; j < GRID_SIZE; j++) {
                        const key = `${i},${j}`;
                        if (closedSet.has(key)) {
                            p.fill(colors.closedSet);
                            p.rect(i * cellSize, j * cellSize, cellSize, cellSize);
                        } else if (openSet.has(key)) {
                            p.fill(colors.openSet);
                            p.rect(i * cellSize, j * cellSize, cellSize, cellSize);
                        }
                    }
                }

                // Draw obstacles
                const obstacles = obstacleLayouts[state.obstacles]();
                p.fill(colors.obstacle);
                for (const obs of obstacles) {
                    const [x, y] = obs.split(',').map(Number);
                    p.rect(x * cellSize, y * cellSize, cellSize, cellSize);
                }

                // Draw start and end points
                const startGrid = toGridCoords(state.startPoint);
                const endGrid = toGridCoords(state.endPoint);
                p.fill(colors.startEnd);
                p.rect(startGrid.x * cellSize, startGrid.y * cellSize, cellSize, cellSize);
                p.rect(endGrid.x * cellSize, endGrid.y * cellSize, cellSize, cellSize);

                // Draw path if it's the last step
                if (step === algorithmHistory.length && path.length > 0) {
                    p.stroke(colors.path);
                    p.strokeWeight(3);
                    p.noFill();
                    p.beginShape();
                    for (const node of path) {
                        p.vertex(node.x * cellSize + cellSize / 2, node.y * cellSize + cellSize / 2);
                    }
                    p.endShape();
                }
            };
        };

        // --- CONTROL LOGIC ---
        function runSimulation() {
            const startGrid = toGridCoords(state.startPoint);
            const endGrid = toGridCoords(state.endPoint);
            const obstacles = obstacleLayouts[state.obstacles]();

            algorithmHistory = runPathfinding(startGrid, endGrid, obstacles, state.algorithm);

            if (algorithmHistory.length > 0) {
                stepSlider.max = algorithmHistory.length;
                stepSlider.value = algorithmHistory.length; // Show final state by default
            } else {
                stepSlider.max = 1;
                stepSlider.value = 1;
            }
            updateStepLabel();
            if (p5Instance) p5Instance.redraw();
        }

        function initControls() {
            // Algorithm radio buttons
            radioDijkstra.addEventListener('change', (e) => {
                if (e.target.checked) { state.algorithm = 'dijkstra'; runSimulation(); }
            });
            radioAStar.addEventListener('change', (e) => {
                if (e.target.checked) { state.algorithm = 'a-star'; runSimulation(); }
            });

            // Step slider
            stepSlider.addEventListener('input', () => {
                updateStepLabel();
                if (p5Instance) p5Instance.redraw();
            });

            // Obstacle dropdown
            obstacleSelect.addEventListener('change', (e) => {
                state.obstacles = e.target.value;
                runSimulation();
            });

            // Joysticks
            initJoystick(startJoystick, state.startPoint);
            initJoystick(endJoystick, state.endPoint);

            // Set initial positions
            updateJoystick(startJoystick, state.startPoint);
            updateJoystick(endJoystick, state.endPoint);
        }

        function updateStepLabel() {
            stepLabel.textContent = `+ ${stepSlider.value}`;
        }

        // --- JOYSTICK IMPLEMENTATION ---
        function initJoystick(joystick, pointState) {
            let isDragging = false;

            joystick.area.addEventListener('mousedown', (e) => {
                isDragging = true;
                updatePosition(e);
            });

            document.addEventListener('mousemove', (e) => {
                if (isDragging) {
                    updatePosition(e);
                }
            });

            document.addEventListener('mouseup', () => {
                if (isDragging) {
                    isDragging = false;
                    runSimulation();
                }
            });

            function updatePosition(e) {
                const rect = joystick.area.getBoundingClientRect();
                let x = e.clientX - rect.left;
                let y = e.clientY - rect.top;

                x = Math.max(0, Math.min(rect.width, x));
                y = Math.max(0, Math.min(rect.height, y));

                pointState.x = x / rect.width;
                pointState.y = y / rect.height;

                updateJoystick(joystick, pointState);
            }
        }

        function updateJoystick(joystick, pointState) {
            const areaRect = joystick.area.getBoundingClientRect();
            joystick.handle.style.left = `${pointState.x * areaRect.width}px`;
            joystick.handle.style.top = `${pointState.y * areaRect.height}px`;
        }

        // --- COORDINATE CONVERSION ---
        function toGridCoords(normalizedPoint) {
            return {
                x: Math.floor(normalizedPoint.x * GRID_SIZE),
                y: Math.floor(normalizedPoint.y * GRID_SIZE)
            };
        }

        // --- START THE SKETCH ---
        new p5(sketch);
    });
    </script>
</body>
</html>
