<body>

    <div id="app-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-source-charge">source charge</label>
                <input type="range" id="slider-source-charge" min="-5" max="5" value="2.0" step="0.1">
                <span id="label-source-charge"></span>
            </div>
            <div class="slider-group">
                <label for="slider-test-charge">charge</label>
                <input type="range" id="slider-test-charge" min="-0.2" max="0.2" value="-0.0845" step="0.0005">
                <span id="label-test-charge"></span>
            </div>
        </div>
        <div id="main-content">
            <div id="canvas-container"></div>
            <div id="info-panel">
                <p id="display-E-mag"></p>
                <p id="display-Ex"></p>
                <p id="display-Ey"></p>
                <p id="display-x-pos"></p>
                <p id="display-y-pos"></p>
                <p id="display-force"></p>
            </div>
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // DOM Elements
            let sliderSourceCharge, sliderTestCharge;
            let labelSourceCharge, labelTestCharge;
            let displayEMag, displayEx, displayEy, displayXPos, displayYPos, displayForce;

            // Simulation State
            let sourceChargeQ = 2.0;
            let testChargeQ = -0.0845;
            let testChargePos;
            let isDragging = false;

            // Visualization Constants
            const CANVAS_WIDTH = 600;
            const CANVAS_HEIGHT = 500;
            const PIXELS_PER_METER = 40; // Scale factor
            const VECTOR_SCALE = 400; // Visual scaling for vectors
            const K_CONSTANT = 1; // Coulomb's constant set to 1 for simplicity

            p.setup = () => {
                let canvas = p.createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
                canvas.parent('canvas-container');

                // Get handles to all DOM elements
                sliderSourceCharge = p.select('#slider-source-charge');
                sliderTestCharge = p.select('#slider-test-charge');
                labelSourceCharge = p.select('#label-source-charge');
                labelTestCharge = p.select('#label-test-charge');
                displayEMag = p.select('#display-E-mag');
                displayEx = p.select('#display-Ex');
                displayEy = p.select('#display-Ey');
                displayXPos = p.select('#display-x-pos');
                displayYPos = p.select('#display-y-pos');
                displayForce = p.select('#display-force');

                // Initial state setup
                testChargePos = p.createVector(2, 3);

                // Add event listeners to sliders
                sliderSourceCharge.input(updateFromSliders);
                sliderTestCharge.input(updateFromSliders);

                // Initial update to reflect default values
                updateAll();
            };

            const updateFromSliders = () => {
                sourceChargeQ = parseFloat(sliderSourceCharge.value());
                testChargeQ = parseFloat(sliderTestCharge.value());
                updateAll();
            };

            const updateAll = () => {
                // Physics Calculations
                const rVec = testChargePos;
                const r = rVec.mag();

                let E_vec = p.createVector(0, 0);
                if (r > 0.1) { // Avoid division by zero
                    const E_mag = (K_CONSTANT * sourceChargeQ) / (r * r);
                    E_vec = rVec.copy().normalize().mult(E_mag);
                }

                const F_vec = E_vec.copy().mult(testChargeQ);

                // Update DOM elements
                const sourceChargeText = (sourceChargeQ >= 0 ? '+' : '') + sourceChargeQ.toFixed(1);
                labelSourceCharge.html(sourceChargeText);
                labelTestCharge.html(testChargeQ.toFixed(4));

                displayEMag.html(`|E| = ${E_vec.mag().toFixed(3)} N/C`);
                displayEx.html(`E<sub>x</sub> component = ${E_vec.x.toFixed(3)} N/C`);
                displayEy.html(`E<sub>y</sub> component = ${E_vec.y.toFixed(3)} N/C`);
                displayXPos.html(`x position = ${testChargePos.x.toFixed(2)} m`);
                displayYPos.html(`y position = ${testChargePos.y.toFixed(2)} m`);
                displayForce.html(`Electric force = ${F_vec.mag().toFixed(3)} N`);

                return { E_vec, F_vec };
            };

            p.draw = () => {
                if (isDragging) {
                    const gridMouseX = (p.mouseX - p.width / 2) / PIXELS_PER_METER;
                    const gridMouseY = (p.height / 2 - p.mouseY) / PIXELS_PER_METER;
                    testChargePos.set(gridMouseX, gridMouseY);
                }

                const { E_vec, F_vec } = updateAll();

                // --- Drawing ---
                p.background(255);
                p.translate(p.width / 2, p.height / 2);
                p.scale(1, -1); // Flip y-axis to match standard math coordinates

                drawGrid();

                // Draw source charge (blue) at origin
                p.noStroke();
                p.fill(0, 0, 255);
                p.circle(0, 0, 15);

                // Draw test charge (black) at its position
                p.fill(0);
                p.circle(testChargePos.x * PIXELS_PER_METER, testChargePos.y * PIXELS_PER_METER, 15);

                // Draw vectors
                const vectorBase = testChargePos.copy().mult(PIXELS_PER_METER);

                // Electric Field vector (red)
                const E_vec_scaled = E_vec.copy().mult(VECTOR_SCALE);
                drawArrow(vectorBase, E_vec_scaled, p.color(255, 0, 0));

                // Electric Force vector (blue), only if charge is non-zero
                if (Math.abs(testChargeQ) > 1e-5) {
                    const F_vec_scaled = F_vec.copy().mult(VECTOR_SCALE);
                    drawArrow(vectorBase, F_vec_scaled, p.color(0, 0, 255));
                }
            };

            const drawGrid = () => {
                p.stroke(220);
                p.strokeWeight(1);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(12);

                const maxX = Math.floor(p.width / 2 / PIXELS_PER_METER);
                const maxY = Math.floor(p.height / 2 / PIXELS_PER_METER);

                for (let i = -maxX; i <= maxX; i += 2) {
                    p.line(i * PIXELS_PER_METER, -p.height / 2, i * PIXELS_PER_METER, p.height / 2);
                    if (i !== 0) {
                        p.push();
                        p.scale(1,-1);
                        p.fill(150);
                        p.noStroke();
                        p.text(i, i * PIXELS_PER_METER, -15);
                        p.pop();
                    }
                }
                for (let j = -maxY; j <= maxY; j += 2) {
                    p.line(-p.width / 2, j * PIXELS_PER_METER, p.width / 2, j * PIXELS_PER_METER);
                    if (j !== 0) {
                        p.push();
                        p.scale(1,-1);
                        p.fill(150);
                        p.noStroke();
                        p.text(j, 15, -j * PIXELS_PER_METER);
                        p.pop();
                    }
                }

                // Draw Axes
                p.stroke(0);
                p.strokeWeight(1.5);
                p.line(-p.width / 2, 0, p.width / 2, 0); // X-axis
                p.line(0, -p.height / 2, 0, p.height / 2); // Y-axis
            };

            function drawArrow(base, vec, myColor) {
                p.push();
                p.stroke(myColor);
                p.strokeWeight(3);
                p.fill(myColor);
                p.translate(base.x, base.y);
                p.line(0, 0, vec.x, vec.y);
                p.rotate(vec.heading());
                let arrowSize = 7;
                p.translate(vec.mag() - arrowSize, 0);
                p.triangle(0, arrowSize / 2, 0, -arrowSize / 2, arrowSize, 0);
                p.pop();
            }

            p.mousePressed = () => {
                const pixelDist = p.dist(p.mouseX, p.mouseY, testChargePos.x * PIXELS_PER_METER + p.width/2, -testChargePos.y * PIXELS_PER_METER + p.height/2);
                if (pixelDist < 15) { // 15 pixels is a good clickable radius
                    isDragging = true;
                }
            };

            p.mouseReleased = () => {
                isDragging = false;
            };

        };

        new p5(sketch);
    </script>
</body>
</html>
