                <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
                <span id="span-k-value"></span>
            </div>
        </div>
    </div>

    <p id="hover-info">(Hover over the curves to see math labels.)</p>

    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>

    <script>
        // MathJax configuration
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']]
            },
            svg: {
                fontCache: 'global'
            }
        };

        // DOM Elements
        const checkboxHertz = document.getElementById('checkbox-hertz');
        const checkboxOverlap = document.getElementById('checkbox-overlap');
        const checkboxGaussian = document.getElementById('checkbox-gaussian');

        const btnLinearLinear = document.getElementById('btn-linear-linear');
        const btnLogLinear = document.getElementById('btn-log-linear');
        const btnLogLog = document.getElementById('btn-log-log');
        const scaleButtons = [btnLinearLinear, btnLogLinear, btnLogLog];

        const sliderR = document.getElementById('slider-r');
        const spanRValue = document.getElementById('span-r-value');
        const sliderK = document.getElementById('slider-k');
        const spanKValue = document.getElementById('span-k-value');

        const plotLeftDiv = document.getElementById('plot-left');
        const plotRightDiv = document.getElementById('plot-right');

        // --- Function Definitions ---
        const f_H = r => (r <= 1) ? Math.pow(1 - r * r, 2.5) : 0;
        const f_O = r => (r <= 1) ? Math.pow(1 - r, 2) : 0;
        const f_G = r => Math.exp(-Math.PI * r * r);

        const F_H = k => {
            if (k < 1e-6) return Math.PI; // Limit as k -> 0 is Pi
            const k2 = k * k;
            const k5 = k2 * k2 * k;
            return 15 * Math.PI * ((3 - k2) * Math.sin(k) - 3 * k * Math.cos(k)) / k5;
        };

        const F_O = k => {
            if (k < 1e-6) return 2 * Math.PI; // Limit as k -> 0 is 2*Pi
            const k2 = k * k;
            const k4 = k2 * k2;
            return 8 * Math.PI * (2 - k2 - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / k4;
        };

        const F_G = k => (1 / Math.sqrt(Math.PI)) * Math.exp(-k * k / (4 * Math.PI));

        // Main update function
        function updatePlots() {
            // 1. Read control states
            const showHertz = checkboxHertz.checked;
            const showOverlap = checkboxOverlap.checked;
            const showGaussian = checkboxGaussian.checked;

            const rMax = parseFloat(sliderR.value);
            const kMax = parseFloat(sliderK.value);

            let yScaleType = 'linear';
            if (btnLogLinear.classList.contains('active') || btnLogLog.classList.contains('active')) {
                yScaleType = 'log';
            }

            // 2. Update slider value displays
            spanRValue.textContent = rMax.toFixed(1);
            spanKValue.textContent = kMax.toFixed(2);

            // 3. Generate data points
            const numPoints = 200;
            const r_values = Array.from({ length: numPoints + 1 }, (_, i) => (i / numPoints) * rMax);
            const k_values = Array.from({ length: numPoints + 1 }, (_, i) => (i / numPoints) * kMax);

            // 4. Calculate function values
            const y_fH = showHertz ? r_values.map(f_H) : [];
            const y_fO = showOverlap ? r_values.map(f_O) : [];
            const y_fG = showGaussian ? r_values.map(f_G) : [];

            const y_FH = showHertz ? k_values.map(F_H) : [];
            const y_FO = showOverlap ? k_values.map(F_O) : [];
            const y_FG = showGaussian ? k_values.map(F_G) : [];

            // 5. Define Plotly traces
            const trace_fH = {
                x: showHertz ? r_values : [], y: y_fH, mode: 'lines', name: 'Hertz',
                line: { color: '#ff0000' },
                hovertemplate: `Hertz<br>\\(f_H(r) = (1 - r^2)^{5/2}, r \\le 1\\)<extra></extra>`
            };
            const trace_fO = {
                x: showOverlap ? r_values : [], y: y_fO, mode: 'lines', name: 'Overlap',
                line: { color: '#000000' },
                hovertemplate: `Overlap<br>\\(f_O(r) = (1 - r)^2, r \\le 1\\)<extra></extra>`
            };
            const trace_fG = {
                x: showGaussian ? r_values : [], y: y_fG, mode: 'lines', name: 'Gaussian',
                line: { color: '#0000ff' },
                hovertemplate: `Gaussian<br>\\(f_G(r) = e^{-\\pi r^2}\\)<extra></extra>`
            };

            const trace_FH = {
                x: showHertz ? k_values : [], y: y_FH, mode: 'lines', name: 'Hertz FT',
                line: { color: '#ff0000' },
                hovertemplate: `Hertz<br>\\(F_H(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}\\)<extra></extra>`
            };
            const trace_FO = {
                x: showOverlap ? k_values : [], y: y_FO, mode: 'lines', name: 'Overlap FT',
                line: { color: '#000000' },
                hovertemplate: `Overlap<br>\\(F_O(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}\\)<extra></extra>`
            };
            const trace_FG = {
                x: showGaussian ? k_values : [], y: y_FG, mode: 'lines', name: 'Gaussian FT',
                line: { color: '#0000ff' },
                hovertemplate: `Gaussian<br>\\(F_G(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}\\)<extra></extra>`
            };

            const dataLeft = [trace_fH, trace_fO, trace_fG];
            const dataRight = [trace_FH, trace_FO, trace_FG];

            // 6. Define Plotly layouts
            const layout = {
                margin: { l: 50, r: 20, t: 80, b: 50 },
                showlegend: false,
                xaxis: { zeroline: false },
                yaxis: { zeroline: false },
                hovermode: 'x'
            };

            const layoutLeft = {
                ...layout,
                title: { text: 'function', font: { color: '#aaaaaa' } },
                xaxis: { ...layout.xaxis, title: 'r', range: [0, rMax] },
                yaxis: { ...layout.yaxis, title: 'f(r)', range: [-0.05, 1.05] }
            };

            const layoutRight = {
                ...layout,
                title: { text: '3D Fourier transform (real part)', font: { color: '#aaaaaa' } },
                xaxis: { ...layout.xaxis, title: 'k', range: [0, kMax] },
                yaxis: { ...layout.yaxis, title: 'F(k)', type: yScaleType }
            };

            // 7. Update plots
            Plotly.react(plotLeftDiv, dataLeft, layoutLeft, { responsive: true, displaylogo: false });
            Plotly.react(plotRightDiv, dataRight, layoutRight, { responsive: true, displaylogo: false });
        }

        function init() {
            // Add event listeners
            [checkboxHertz, checkboxOverlap, checkboxGaussian].forEach(cb => {
                cb.addEventListener('change', updatePlots);
            });

            scaleButtons.forEach(button => {
                button.addEventListener('click', (e) => {
                    scaleButtons.forEach(btn => btn.classList.remove('active'));
                    e.currentTarget.classList.add('active');
                    updatePlots();
                });
            });

            sliderR.addEventListener('input', updatePlots);
            sliderK.addEventListener('input', updatePlots);

            // Set initial active button
            btnLinearLinear.classList.add('active');

            // Initial plot render
            updatePlots();

            // Set up MathJax rendering after plots are rendered/updated
            const typesetOnPlot = () => {
                if (window.MathJax && typeof window.MathJax.typesetPromise === 'function') {
                    window.MathJax.typesetPromise();
                }
            };
            plotLeftDiv.on('plotly_afterplot', typesetOnPlot);
            plotRightDiv.on('plotly_afterplot', typesetOnPlot);
        }

        // Run on page load
        window.onload = init;
    </script>

</body>
