<script>
let sketch = function(p) {
    // State variables
    let params = {};
    const controls = {};

    // Plot dimensions
    let canvas;
    const years = Array.from({ length: 31 }, (_, i) => 2020 + i);
    const plotMargin = { top: 20, right: 30, bottom: 120, left: 60 };
    let plotW, plotH, budgetPlotY;

    p.setup = function() {
        const container = document.getElementById('main-plot-container');
        canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
        canvas.parent('main-plot-container');

        plotW = p.width - plotMargin.left - plotMargin.right;
        plotH = (p.height - plotMargin.top - plotMargin.bottom) * 0.7; // Main plot area
        budgetPlotY = plotMargin.top + plotH + 70; // Start of budget circles area

        p.noLoop();
        initializeControls();
        updateAll();
    };

    p.windowResized = function() {
        const container = document.getElementById('main-plot-container');
        p.resizeCanvas(container.offsetWidth, container.offsetHeight);
        plotW = p.width - plotMargin.left - plotMargin.right;
        plotH = (p.height - plotMargin.top - plotMargin.bottom) * 0.7;
        budgetPlotY = plotMargin.top + plotH + 70;
        p.redraw();
    };

    function initializeControls() {
        // Sliders
        const sliderIds = ['population', 'annual-emissions', 'reduction-time', 'annual-budget', 'start-year', 'compensation-cost', 'fractional-compensation', 'constant-compensation'];
        sliderIds.forEach(id => {
            controls[`slider-${id}`] = document.getElementById(`slider-${id}`);
            controls[`value-${id}`] = document.getElementById(`value-${id}`);
            controls[`slider-${id}`].addEventListener('input', () => {
                const val = controls[`slider-${id}`].value;
                const isFloat = controls[`slider-${id}`].step.includes('.');
                controls[`value-${id}`].textContent = isFloat ? parseFloat(val).toFixed(1) : val;
                updateAll();
            });
        });

        // Radio buttons
        controls.scaleSwitch = document.querySelectorAll('input[name="scale"]');
        controls.scaleSwitch.forEach(radio => radio.addEventListener('change', updateAll));
        controls.typeSwitch = document.querySelectorAll('input[name="type"]');
        controls.typeSwitch.forEach(radio => radio.addEventListener('change', updateAll));

        // Climate neutral buttons
        controls.neutralButtons = {
            now: document.getElementById('btn-neutral-now'),
            2030: document.getElementById('btn-neutral-2030'),
            2040: document.getElementById('btn-neutral-2040'),
            2050: document.getElementById('btn-neutral-2050'),
            never: document.getElementById('btn-neutral-never'),
        };
        Object.entries(controls.neutralButtons).forEach(([key, btn]) => {
            btn.addEventListener('click', () => {
                params.neutralYear = (key === 'now') ? 2020 : (key === 'never' ? null : parseInt(key));
                updateAll();
            });
        });

        // Set initial state
        params.neutralYear = 2040;
    }

    function updateAll() {
        // Read all control values into params object
        params.scale = document.querySelector('input[name="scale"]:checked').value;
        params.type = document.querySelector('input[name="type"]:checked').value;
        params.population = parseFloat(controls['slider-population'].value);
        params.annualEmissions = parseFloat(controls['slider-annual-emissions'].value);
        params.reductionTime = parseFloat(controls['slider-reduction-time'].value);
        params.annualBudget = parseFloat(controls['slider-annual-budget'].value);
        params.startYear = parseInt(controls['slider-start-year'].value);
        params.compensationCost = parseFloat(controls['slider-compensation-cost'].value);
        params.fractionalCompensation = parseFloat(controls['slider-fractional-compensation'].value);
        params.constantCompensation = parseFloat(controls['slider-constant-compensation'].value);

        // Update UI based on state
        const popSlider = controls['slider-population'];
        const popLabel = popSlider.parentElement.previousElementSibling;
        if(params.scale === 'individual') {
            popSlider.disabled = true;
            popSlider.parentElement.style.opacity = 0.5;
            popLabel.style.opacity = 0.5;
        } else {
            popSlider.disabled = false;
            popSlider.parentElement.style.opacity = 1;
            popLabel.style.opacity = 1;
        }

        Object.values(controls.neutralButtons).forEach(btn => btn.classList.remove('active'));
        let activeBtnKey = params.neutralYear === 2020 ? 'now' : (params.neutralYear === null ? 'never' : params.neutralYear);
        if (controls.neutralButtons[activeBtnKey]) {
            controls.neutralButtons[activeBtnKey].classList.add('active');
        }

        // Trigger redraw
        p.redraw();
    }

    // --- CALCULATION LOGIC ---
    function calculateData() {
        const initialEmissions = params.annualEmissions;
        const popMultiplier = params.scale === 'population' ? params.population * 1000 : 1;

        let realEmissions = [], compensatedEmissions = [], effectiveEmissions = [], annualCosts = [];
        let cumulativeEffective = 0;
        const cumulativeData = {};

        for (const year of years) {
            // Per person calculations
            let real_person = (year < params.startYear)
                ? initialEmissions
                : initialEmissions * Math.pow(0.5, (year - params.startYear) / params.reductionTime);

            let fractional_comp = real_person * (params.fractionalCompensation / 100);
            let constant_comp = initialEmissions * (params.constantCompensation / 100);
            let compensated_person = fractional_comp + constant_comp;

            let effective_person = real_person - compensated_person;

            cumulativeEffective += effective_person;
            cumulativeData[year] = cumulativeEffective;

            let cost_person = compensated_person * params.compensationCost;

            // Apply population scaling
            realEmissions.push(real_person * popMultiplier);
            compensatedEmissions.push(compensated_person * popMultiplier);
            effectiveEmissions.push(effective_person * popMultiplier);
            annualCosts.push(cost_person * popMultiplier);
        }

        return { realEmissions, compensatedEmissions, effectiveEmissions, annualCosts, cumulativeData };
    }

    // --- DRAWING LOGIC ---
    p.draw = function() {
        p.background(255);
        if (Object.keys(params).length === 0) return; // Don't draw if not initialized

        const data = calculateData();
        const yMaxEmissions = Math.max(...data.realEmissions) * 1.1;
        const yMinEmissions = Math.min(-2, ...data.effectiveEmissions) * 1.2;

        const yMaxCosts = Math.max(...data.annualCosts) * 1.2;

        let yMin, yMax, yAxisLabel;
        if (params.type === 'emissions') {
            yMin = yMinEmissions;
            yMax = yMaxEmissions;
            yAxisLabel = params.scale === 'individual' ? 'annual CO₂ emissions per person (tons)' : 'annual city emission of CO₂ (mil tons)';
        } else {
            yMin = 0;
            yMax = yMaxCosts;
            yAxisLabel = params.scale === 'individual' ? 'annual personal compensation costs ($)' : 'annual city compensation costs (mil $)';
        }

        // --- MAPPING HELPERS ---
        const mapX = (year) => p.map(year, 2020, 2050, plotMargin.left, plotMargin.left + plotW);
        const mapY = (val) => p.map(val, yMin, yMax, plotMargin.top + plotH, plotMargin.top);

        drawAxes(mapX, mapY, yMin, yMax, yAxisLabel);

        if (params.type === 'emissions') {
            drawEmissionsPlot(data, mapX, mapY);
        } else {
            drawCostsPlot(data, mapX, mapY);
        }

        drawBudgetPlot(data, mapX);
    };

    function drawAxes(mapX, mapY, yMin, yMax, yAxisLabel) {
        p.stroke(200);
        p.strokeWeight(1);
        p.fill(0);
        p.textSize(12);
        p.textAlign(p.CENTER, p.TOP);

        // X-axis ticks and grid lines
        for (let year = 2020; year <= 2050; year += 5) {
            let x = mapX(year);
            p.line(x, plotMargin.top, x, plotMargin.top + plotH);
            p.text(year, x, plotMargin.top + plotH + 5);
        }

        // Y-axis ticks and grid lines
        p.textAlign(p.RIGHT, p.CENTER);
        const yTicks = p.nice(yMin, yMax, 5);
        for(const tick of yTicks){
            let y = mapY(tick);
            p.line(plotMargin.left, y, plotMargin.left + plotW, y);
            let label = params.scale === 'population' && params.type === 'emissions' ? (tick/1e6).toFixed(1) : Math.round(tick);
            if (params.scale === 'population' && params.type === 'costs') label = (tick/1e6).toFixed(1);
            p.text(label, plotMargin.left - 8, y);
        }

        // Axes lines
        p.stroke(0);
        p.line(plotMargin.left, plotMargin.top, plotMargin.left, plotMargin.top + plotH);
        p.line(plotMargin.left, plotMargin.top + plotH, plotMargin.left + plotW, plotMargin.top + plotH);

        // Axis labels
        p.push();
        p.translate(plotMargin.left - 45, plotMargin.top + plotH / 2);
        p.rotate(-p.HALF_PI);
        p.textAlign(p.CENTER, p.CENTER);
        p.text(yAxisLabel, 0, 0);
        p.pop();

        p.textAlign(p.CENTER, p.TOP);
        p.text("year", plotMargin.left + plotW / 2, plotMargin.top + plotH + 25);
    }

    function drawEmissionsPlot(data, mapX, mapY) {
        const popDivisor = params.scale === 'population' ? 1e6 : 1;

        // --- DRAW AREAS ---
        // Compensated (Blue)
        p.fill(173, 216, 230, 150); // lightblue with alpha
        p.noStroke();
        p.beginShape();
        p.vertex(mapX(2020), mapY(data.effectiveEmissions[0]));
        for (let i = 0; i < years.length; i++) {
            p.vertex(mapX(years[i]), mapY(data.effectiveEmissions[i]));
        }
        for (let i = years.length - 1; i >= 0; i--) {
            p.vertex(mapX(years[i]), mapY(data.realEmissions[i]));
        }
        p.endShape(p.CLOSE);

        // Effective > 0 (Red)
        p.fill(255, 0, 0, 100);
        p.beginShape();
        p.vertex(mapX(2020), mapY(0));
        for (let i = 0; i < years.length; i++) {
            p.vertex(mapX(years[i]), mapY(Math.max(0, data.effectiveEmissions[i])));
        }
        p.vertex(mapX(2050), mapY(0));
        p.endShape(p.CLOSE);

        // Effective < 0 (Green)
        p.fill(0, 255, 0, 100);
        p.beginShape();
        p.vertex(mapX(2020), mapY(0));
        for (let i = 0; i < years.length; i++) {
            p.vertex(mapX(years[i]), mapY(Math.min(0, data.effectiveEmissions[i])));
        }
        p.vertex(mapX(2050), mapY(0));
        p.endShape(p.CLOSE);

        // --- DRAW LINES ---
        // Real emissions (Red)
        p.stroke(255, 0, 0);
        p.strokeWeight(2);
        p.noFill();
        p.beginShape();
        for (let i = 0; i < years.length; i++) {
            p.vertex(mapX(years[i]), mapY(data.realEmissions[i]));
        }
        p.endShape();

        // Effective emissions (Black)
        p.stroke(0);
        p.strokeWeight(2);
        p.beginShape();
        for (let i = 0; i < years.length; i++) {
            p.vertex(mapX(years[i]), mapY(data.effectiveEmissions[i]));
        }
        p.endShape();

        // --- ANNOTATIONS ---
        p.noStroke(); p.fill(0); p.textAlign(p.CENTER, p.CENTER);
        p.text("real CO₂ emissions", mapX(2040), mapY(data.realEmissions[20] * 1.1));
        p.text("compensated CO₂", mapX(2030), mapY((data.realEmissions[10] + data.effectiveEmissions[10]) / 2));
        p.text("effective\nCO₂ emissions", mapX(2030), mapY(data.effectiveEmissions[10] / 2));

        // Climate neutral marker
        if (params.neutralYear !== null) {
            const nx = mapX(params.neutralYear);
            p.stroke(0, 0, 0, 100); p.strokeWeight(1);
            p.line(nx, plotMargin.top, nx, plotMargin.top + plotH);
            p.noStroke(); p.fill(0);
            p.text(`climate neutral\n${params.neutralYear}`, nx, mapY(data.effectiveEmissions[params.neutralYear-2020]) - 30);
        }

        const cumulativeUntil2050 = data.cumulativeData[2050] / popDivisor;
        const y2050 = mapY(cumulativeUntil2050);
        drawDashedLine(p, plotMargin.left, y2050, plotMargin.left + plotW, y2050, [5, 5]);
        p.noStroke(); p.fill(0); p.textAlign(p.LEFT, p.CENTER);
        p.text(`CO₂ emissions until 2050 = ${cumulativeUntil2050.toFixed(1)} tons CO₂`, mapX(2028), y2050 - 10);

        if (params.neutralYear !== null) {
            const cumulativeUntilNeutral = data.cumulativeData[params.neutralYear] / popDivisor;
            const yNeutral = mapY(cumulativeUntilNeutral);
            p.stroke(255,0,0);
            drawDashedLine(p, plotMargin.left, yNeutral, plotMargin.left + plotW, yNeutral, [5, 5]);
            p.noStroke(); p.fill(200,0,0); p.textAlign(p.LEFT, p.CENTER);
            p.text(`CO₂ emissions until ${params.neutralYear} = ${cumulativeUntilNeutral.toFixed(1)} tons CO₂`, mapX(2028), yNeutral - 10);
        }
    }

    function drawCostsPlot(data, mapX, mapY) {
        const popDivisor = params.scale === 'population' ? 1e6 : 1;
        const totalCosts = data.annualCosts.reduce((a, b) => a + b, 0) / popDivisor;
        const avgCost = totalCosts / 31;

        // Area
        p.fill(173, 216, 230, 150);
        p.noStroke();
        p.beginShape();
        p.vertex(mapX(2020), mapY(0));
        for (let i = 0; i < years.length; i++) {
            p.vertex(mapX(years[i]), mapY(data.annualCosts[i]));
        }
        p.vertex(mapX(2050), mapY(0));
        p.endShape(p.CLOSE);

        // Line
        p.stroke(0, 0, 139); // Dark blue
        p.strokeWeight(2);
        p.noFill();
        p.beginShape();
        for (let i = 0; i < years.length; i++) {
            p.vertex(mapX(years[i]), mapY(data.annualCosts[i]));
        }
        p.endShape();

        // Annotations
        const yAvg = mapY(avgCost);
        p.stroke(0,0,139);
        drawDashedLine(p, plotMargin.left, yAvg, plotMargin.left + plotW, yAvg, [5, 5]);

        p.noStroke(); p.fill(0); p.textAlign(p.CENTER, p.BOTTOM);
        let unit = params.scale === 'population' ? ' mil' : '';
        p.text(`annual personal compensation costs\n(average) = $${(avgCost).toFixed(2)}${unit} per year`, mapX(2035), yAvg - 5);

        p.textAlign(p.CENTER, p.TOP);
        p.text(`total personal compensation costs\n(until 2050) = $${totalCosts.toFixed(1)}${unit}`, mapX(2035), mapY(avgCost / 2));
    }

    function drawBudgetPlot(data, mapX) {
        p.fill(0); p.noStroke();
        p.textAlign(p.CENTER, p.BOTTOM);
        p.textSize(14);
        const title = `${params.scale} climate budget`;
        p.text(title, p.width / 2, budgetPlotY - 20);

        const budgetYears = [2020, 2030, 2040, 2050];
        const totalBudget = 30 * params.annualBudget;
        const popMultiplier = params.scale === 'population' ? params.population * 1000 : 1;
        const unit = params.scale === 'population' ? 'mil tons' : 'tons';
        const popDivisor = params.scale === 'population' ? 1e6 : 1;

        let circleValues = [];
        for (const year of budgetYears) {
             const cumulativeEffective = data.cumulativeData[year] / popMultiplier;
             circleValues.push(totalBudget - cumulativeEffective);
        }
        const maxAbsValue = Math.max(...circleValues.map(v => Math.abs(v)), 1);


        budgetYears.forEach((year, i) => {
            const centerX = mapX(year);
            const centerY = budgetPlotY + 40;
            const value = circleValues[i];
            const radius = 40 * p.sqrt(p.abs(value) / maxAbsValue);

            p.fill(0); p.noStroke(); p.textAlign(p.CENTER, p.BOTTOM); p.textSize(12);
            p.text(year, centerX, budgetPlotY);

            const isSolid = data.effectiveEmissions[year - 2020] <= 0;

            p.fill(144, 238, 144, 200); // lightgreen
            if (isSolid) {
                p.noStroke();
                p.ellipse(centerX, centerY, radius * 2, radius * 2);
            } else {
                p.stroke(0); p.strokeWeight(1.5);
                drawDashedCircle(p, centerX, centerY, radius, [5, 3]);
                p.ellipse(centerX, centerY, radius * 2, radius * 2);
            }

            p.fill(0); p.noStroke(); p.textAlign(p.CENTER, p.CENTER);
            p.text(`${(value / popDivisor).toFixed(1)} ${unit}\nCO₂`, centerX, centerY);
        });
    }

    // --- UTILITY FUNCTIONS ---
    function drawDashedLine(p, x1, y1, x2, y2, dashArray) {
        p.drawingContext.setLineDash(dashArray);
        p.line(x1, y1, x2, y2);
        p.drawingContext.setLineDash([]);
    }

    function drawDashedCircle(p, x, y, radius, dashArray) {
        p.noFill();
        p.stroke(0);
        p.drawingContext.setLineDash(dashArray);
        p.ellipse(x, y, radius * 2, radius * 2);
        p.drawingContext.setLineDash([]);
    }

    p.nice = function(min, max, ticks) {
        let range = max - min;
        if (range === 0) return [min];
        let tickSpacing = range / ticks;
        let magnitude = Math.pow(10, Math.floor(Math.log10(tickSpacing)));
        let residual = tickSpacing / magnitude;
        if (residual > 5) {
            tickSpacing = 10 * magnitude;
        } else if (residual > 2) {
            tickSpacing = 5 * magnitude;
        } else if (residual > 1) {
            tickSpacing = 2 * magnitude;
        } else {
            tickSpacing = magnitude;
        }
        let niceMin = Math.floor(min / tickSpacing) * tickSpacing;
        let niceMax = Math.ceil(max / tickSpacing) * tickSpacing;
        let nice_ticks = [];
        for (let x = niceMin; x <= niceMax; x += tickSpacing) {
            nice_ticks.push(x);
        }
        return nice_ticks;
    };
};

new p5(sketch);
</script>

