    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // --- CONSTANTS ---
        const UNFOLDED_COLOR = new THREE.Color('#F5E0E0');

        // --- GEOMETRY DEFINITIONS ---
        const SOLIDS_DATA = {
            '1': { // Non-convex Hexahedron from screenshot
                vertices: [
                    new THREE.Vector3(-1, 0, 0),    // 0
                    new THREE.Vector3(0, -1.5, 0),  // 1
                    new THREE.Vector3(0, 1.5, 0),   // 2
                    new THREE.Vector3(1, 0, 0),     // 3
                    new THREE.Vector3(-2, 2, 0),    // 4
