                    { indices: [0, 2, 4], color: '#F3C76E', parent: 0, hinge: [0, 2], angle: -Math.PI * 2 / 3 },
                    { indices: [1, 0, 5], color: '#E47A55', parent: 0, hinge: [1, 0], angle: Math.PI * 2 / 3 }, // Swapped 1,0
                    { indices: [3, 1, 5], color: '#F3C76E', parent: 1, hinge: [3, 1], angle: -Math.PI * 2 / 3 },
                    { indices: [2, 3, 4], color: '#E47A55', parent: 1, hinge: [2, 3], angle: Math.PI * 2 / 3 },
                ]
            },
            '2': { // Tetrahedron
                vertices: [
                    new THREE.Vector3(0, 0, 0),                // 0
                    new THREE.Vector3(2, 0, 0),                // 1
                    new THREE.Vector3(1, Math.sqrt(3), 0),     // 2
                    new THREE.Vector3(1, -Math.sqrt(3) / 3, 0) // Centroid for unfolding
                ],
                faces: [
                    { indices: [0, 1, 3], color: '#E47A55', parent: null },
                    { indices: [1, 2, 3], color: '#A4C6E3', parent: 0, hinge: [1, 3], angle: Math.acos(1/3) },
                    { indices: [2, 0, 3], color: '#F3C76E', parent: 0, hinge: [0, 3], angle: Math.acos(1/3) }
                ]
            },
            '3': { // Cube
                vertices: [
                    // Base square
                    new THREE.Vector3(0, 0, 0), new THREE.Vector3(2, 0, 0), new THREE.Vector3(2, 2, 0), new THREE.Vector3(0, 2, 0),
                    // Top
                    new THREE.Vector3(0, 4, 0), new THREE.Vector3(2, 4, 0),
                    // Bottom
                    new THREE.Vector3(0, -2, 0), new THREE.Vector3(2, -2, 0),
                    // Left
                    new THREE.Vector3(-2, 0, 0), new THREE.Vector3(-2, 2, 0),
                    // Right
                    new THREE.Vector3(4, 0, 0), new THREE.Vector3(4, 2, 0),
                ],
                faces: [
                    { indices: [0, 1, 3], color: '#A4C6E3', parent: null }, // Base part 1
                    { indices: [1, 2, 3], color: '#A4C6E3', parent: 0 },    // Base part 2
                    { indices: [3, 2, 4], color: '#F4E0E1', parent: 1, hinge: [3, 2], angle: Math.PI / 2 }, // Top
                    { indices: [2, 5, 4], color: '#F4E0E1', parent: 2},
                    { indices: [0, 6, 1], color: '#F3C76E', parent: 0, hinge: [0, 1], angle: -Math.PI / 2 }, // Bottom
                    { indices: [6, 7, 1], color: '#F3C76E', parent: 4},
                    { indices: [0, 3, 8], color: '#E47A55', parent: 0, hinge: [0, 3], angle: -Math.PI / 2 }, // Left
                    { indices: [3, 9, 8], color: '#E47A55', parent: 6},
                    { indices: [1, 10, 2], color: '#D0B0D0', parent: 1, hinge: [1, 2], angle: Math.PI / 2 }, // Right
                    { indices: [10, 11, 2], color: '#D0B0D0', parent: 8},
                ]
            }
        };

        // --- THREE.js setup ---
        let scene, camera, renderer, controls, currentSolidGroup;
        const canvasContainer = document.getElementById('canvas-container');

        // --- DOM Elements ---
        const solidButtons = document.querySelectorAll('.solid-btn');
        const foldSlider = document.getElementById('slider-fold');
        const resetButton = document.getElementById('btn-reset-view');

        // --- INITIALIZATION ---
        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color('#f0f0f0');

            // Camera
            camera = new THREE.PerspectiveCamera(50, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 100);
            camera.position.set(0, 0, 10);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvasContainer.appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Event Listeners
            window.addEventListener('resize', onWindowResize);
            solidButtons.forEach(btn => btn.addEventListener('click', handleSolidSelect));
            foldSlider.addEventListener('input', handleSliderInput);
            resetButton.addEventListener('click', resetView);

            // Initial Solid
            switchSolid('1');

            // Start animation loop
            animate();
        }

        // --- CORE FUNCTIONS ---

        function createSolid(id) {
            const data = SOLIDS_DATA[id];
            const solidGroup = new THREE.Group();
            solidGroup.userData.pivots = [];
            solidGroup.userData.faces = [];

            const nodes = [];

            // 1. Create all face meshes and groups
            data.faces.forEach((faceData, i) => {
                const geometry = new THREE.BufferGeometry();
                const vertices = faceData.indices.map(index => data.vertices[index]);
                const positions = new Float32Array(vertices.length * 3);
                vertices.forEach((v, i) => v.toArray(positions, i * 3));

                geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
                geometry.computeVertexNormals();

                const finalColor = new THREE.Color(faceData.color);
                const material = new THREE.MeshStandardMaterial({
                    color: UNFOLDED_COLOR.clone(),
                    side: THREE.DoubleSide,
                    flatShading: true,
                    polygonOffset: true, // Prevent z-fighting with edges
                    polygonOffsetFactor: 1,
                    polygonOffsetUnits: 1
                });

                const mesh = new THREE.Mesh(geometry, material);
                const faceGroup = new THREE.Group();
                faceGroup.add(mesh);

                // Add wireframe
                const edges = new THREE.EdgesGeometry(geometry);
                const line = new THREE.LineSegments(edges, new THREE.LineBasicMaterial({ color: 0x333333 }));
                mesh.add(line);

                nodes.push({ group: faceGroup, mesh, data: faceData, finalColor });
                solidGroup.userData.faces.push({ mesh, finalColor });
            });

            // 2. Build hierarchy
            const rootGroup = new THREE.Group();
            solidGroup.add(rootGroup);

            const processed = new Array(nodes.length).fill(false);
            const queue = [];

            // Find root nodes
            nodes.forEach((node, i) => {
                if (node.data.parent === null) {
                    queue.push(i);
                    rootGroup.add(node.group);
                    processed[i] = true;
                }
            });

            // Process hierarchy
            while (queue.length > 0) {
                const parentIndex = queue.shift();
                const parentNode = nodes[parentIndex];

                nodes.forEach((childNode, childIndex) => {
                    if (childNode.data.parent === parentIndex && !processed[childIndex]) {
                        const hingeIndices = childNode.data.hinge;
                        if (!hingeIndices) { // For compound faces like in the cube
                            parentNode.group.add(childNode.group);
                        } else {
                            const hingeV1 = data.vertices[hingeIndices[0]];
                            const hingeV2 = data.vertices[hingeIndices[1]];

                            const pivot = new THREE.Group();
                            pivot.position.copy(hingeV1);

                            const hingeDir = new THREE.Vector3().subVectors(hingeV2, hingeV1).normalize();
                            pivot.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), hingeDir);

                            parentNode.group.add(pivot);

                            childNode.group.position.sub(hingeV1);
                            pivot.add(childNode.group);

                            solidGroup.userData.pivots.push({
                                pivot: pivot,
                                axis: 'y',
                                angle: childNode.data.angle || 0
                            });
                        }

                        queue.push(childIndex);
                        processed[childIndex] = true;
                    }
                });
            }

            return solidGroup;
        }

        function updateFold(t) {
            if (!currentSolidGroup) return;

            // Update pivots
            currentSolidGroup.userData.pivots.forEach(p => {
                p.pivot.rotation[p.axis] = p.angle * t;
            });

            // Update colors
            currentSolidGroup.userData.faces.forEach(f => {
                f.mesh.material.color.lerpColors(UNFOLDED_COLOR, f.finalColor, t);
            });
        }

        function switchSolid(id) {
            // Remove old solid
            if (currentSolidGroup) {
                scene.remove(currentSolidGroup);
                // Dispose geometries and materials if needed for larger apps
            }

            // Create and add new solid
            currentSolidGroup = createSolid(id);
            scene.add(currentSolidGroup);

            // Apply current slider value
            updateFold(foldSlider.value);

            // Reset view
            resetView();
        }

        function resetView() {
            if (!currentSolidGroup) return;

            const box = new THREE.Box3().setFromObject(currentSolidGroup);
            const center = box.getCenter(new THREE.Vector3());
            const size = box.getSize(new THREE.Vector3());
            const maxDim = Math.max(size.x, size.y, size.z);
            const fov = camera.fov * (Math.PI / 180);
            let cameraZ = Math.abs(maxDim / 2 * Math.tan(fov * 2));
            cameraZ *= 2; // zoom out a bit for padding

            camera.position.set(center.x, center.y, center.z + cameraZ);
            controls.target.copy(center);
            controls.update();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // --- EVENT HANDLERS ---
        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        function handleSolidSelect(e) {
            const selectedId = e.target.id.split('-')[2];

            solidButtons.forEach(btn => btn.classList.remove('active'));
            e.target.classList.add('active');

            switchSolid(selectedId);
        }

        function handleSliderInput(e) {
            const t = parseFloat(e.target.value);
            updateFold(t);
        }

        // --- START ---
        init();
    </script>

</body>
