            <!-- p5.js will create the canvas here -->
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // DOM Elements
            let sliderAC, sliderBC, valueAC, valueBC, btnReset;

            // Constants
            const CANVAS_WIDTH = 600;
            const CANVAS_HEIGHT = 550;
            const PADDING = 70;
            const N_DIVS = 10;

            // Triangle vertices (Cartesian coordinates)
            let A, B, C;

            // Color map definition
            const colorStops = [
                { val: 0.0,  color: '#2c7bb6' },
                { val: 0.25, color: '#abd9e9' },
                { val: 0.5,  color: '#ffffbf' },
                { val: 0.75, color: '#fdae61' },
                { val: 1.0,  color: '#d7191c' }
            ];
            let p5ColorStops;

            // Default slider values
            const default_ac = 0.6;
            const default_bc = 3.93;

            p.setup = () => {
                const canvas = p.createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
                canvas.id('ternary-plot-canvas');
                canvas.parent('visualization-container');

                // Get DOM elements
                sliderAC = p.select('#slider-ac');
                sliderBC = p.select('#slider-bc');
                valueAC = p.select('#value-ac');
                valueBC = p.select('#value-bc');
                btnReset = p.select('#btn-reset');

                // Set initial values from plan
                sliderAC.value(default_ac);
                sliderBC.value(default_bc);
                valueAC.html(parseFloat(default_ac).toFixed(2));
                valueBC.html(parseFloat(default_bc).toFixed(2));

                // Add event listeners
                sliderAC.input(() => {
                    valueAC.html(parseFloat(sliderAC.value()).toFixed(2));
                    p.redraw();
                });
                sliderBC.input(() => {
                    valueBC.html(parseFloat(sliderBC.value()).toFixed(2));
                    p.redraw();
                });
                btnReset.mousePressed(() => {
                    sliderAC.value(default_ac);
                    sliderBC.value(default_bc);
                    valueAC.html(parseFloat(default_ac).toFixed(2));
                    valueBC.html(parseFloat(default_bc).toFixed(2));
                    p.redraw();
                });

                // Define triangle vertices (A=bottom-right, B=top, C=bottom-left)
                const h = p.sqrt(3)/2 * (CANVAS_WIDTH - 2 * PADDING);
                const topY = (CANVAS_HEIGHT - h) / 2;
                A = p.createVector(CANVAS_WIDTH - PADDING, topY + h);
                B = p.createVector(CANVAS_WIDTH / 2, topY);
                C = p.createVector(PADDING, topY + h);

                // Convert hex colors to p5.Color objects
                p5ColorStops = colorStops.map(stop => ({ val: stop.val, color: p.color(stop.color) }));

                p.noLoop(); // Redraw only on input
                p.redraw();
            };

            p.draw = () => {
                p.background(255);

                const alpha_ac = parseFloat(sliderAC.value());
                const alpha_bc = parseFloat(sliderBC.value());

                drawGrid(alpha_ac, alpha_bc);
                drawAxesAndLabels();
            };

            function baryToCartesian(x1, x2, x3) {
                // x1 -> A, x2 -> B, x3 -> C
                const px = x1 * A.x + x2 * B.x + x3 * C.x;
                const py = x1 * A.y + x2 * B.y + x3 * C.y;
                return p.createVector(px, py);
            }

            function getColorForValue(value) {
                if (value <= p5ColorStops[0].val) return p5ColorStops[0].color;

                for (let i = 0; i < p5ColorStops.length - 1; i++) {
                    const start = p5ColorStops[i];
                    const end = p5ColorStops[i+1];
                    if (value >= start.val && value <= end.val) {
                        const t = p.map(value, start.val, end.val, 0, 1);
                        return p.lerpColor(start.color, end.color, t);
                    }
                }
                return p5ColorStops[p5ColorStops.length - 1].color;
            }

            function calculateFunctionValue(x1, x2, x3, alpha_13, alpha_23) {
                const alpha_1 = alpha_13;
                const alpha_2 = alpha_23;
                const alpha_3 = 1.0;

                const denominator = alpha_1 * x1 + alpha_2 * x2 + alpha_3 * x3;
                if (denominator === 0) return 0;

                const y1 = (alpha_1 * x1) / denominator;
                const y2 = (alpha_2 * x2) / denominator;
                const y3 = (alpha_3 * x3) / denominator;

                const F = (x1 - y1)**2 + (x2 - y2)**2 + (x3 - y3)**2;
                return F / 2.0; // Normalize by max value of 2
            }

            function drawGrid(alpha_ac, alpha_bc) {
                p.stroke(0);
                p.strokeWeight(1);

                for (let i = 0; i < N_DIVS; i++) {
                    for (let j = 0; j < N_DIVS - i; j++) {
                        // Up-pointing triangle
                        const v1_up = baryToCartesian( (i + 1) / N_DIVS, j / N_DIVS, (N_DIVS - i - j - 1) / N_DIVS );
                        const v2_up = baryToCartesian( i / N_DIVS, (j + 1) / N_DIVS, (N_DIVS - i - j - 1) / N_DIVS );
                        const v3_up = baryToCartesian( i / N_DIVS, j / N_DIVS, (N_DIVS - i - j) / N_DIVS );

                        // Centroid of up-pointing triangle
                        const cx_up = (i + 1/3) / N_DIVS;
                        const cy_up = (j + 1/3) / N_DIVS;
                        const cz_up = 1 - cx_up - cy_up;

                        const val_up = calculateFunctionValue(cx_up, cy_up, cz_up, alpha_ac, alpha_bc);
                        p.fill(getColorForValue(val_up));
                        p.triangle(v1_up.x, v1_up.y, v2_up.x, v2_up.y, v3_up.x, v3_up.y);

                        // Down-pointing triangle
                        if (j < N_DIVS - i - 1) {
                            const v1_down = v1_up;
                            const v2_down = baryToCartesian((i+1)/N_DIVS, (j+1)/N_DIVS, (N_DIVS-i-j-2)/N_DIVS);
                            const v3_down = v2_up;

                            // Centroid of down-pointing triangle
                            const cx_down = (i + 2/3) / N_DIVS;
                            const cy_down = (j + 2/3) / N_DIVS;
                            const cz_down = 1 - cx_down - cy_down;

                            const val_down = calculateFunctionValue(cx_down, cy_down, cz_down, alpha_ac, alpha_bc);
                            p.fill(getColorForValue(val_down));
                            p.triangle(v1_down.x, v1_down.y, v2_down.x, v2_down.y, v3_down.x, v3_down.y);
                        }
                    }
                }
            }

            function drawAxesAndLabels() {
                p.noFill();
                p.stroke(0);
                p.strokeWeight(1.5);
                p.triangle(A.x, A.y, B.x, B.y, C.x, C.y);

                p.fill(0);
                p.noStroke();
                p.textSize(16);

                // Corner Labels
                p.textAlign(p.LEFT, p.TOP);
                p.text('A', A.x + 8, A.y - 8);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text('B', B.x, B.y - 8);
                p.textAlign(p.RIGHT, p.TOP);
                p.text('C', C.x - 8, C.y - 8);

                p.textSize(12);

                // Tick Labels
                for (let i = 0; i <= N_DIVS; i++) {
                    const val = i / N_DIVS;

                    // Bottom axis (C to A) -> increasing x_A
                    p.textAlign(p.CENTER, p.TOP);
                    const tick_CA = baryToCartesian(val, 0, 1 - val);
                    p.text(val.toFixed(1).replace(/^0\./, '.').replace(/^1\.0/, '1.'), tick_CA.x, tick_CA.y + 5);

                    if (i > 0 && i < N_DIVS) {
                        // Left axis (C to B) -> increasing x_B
                        p.textAlign(p.RIGHT, p.CENTER);
                        const tick_CB = baryToCartesian(0, val, 1-val);
                        p.text(val.toFixed(1).replace(/^0\./, '.'), tick_CB.x - 8, tick_CB.y);

                        // Right axis (A to B) -> increasing x_B
                        p.textAlign(p.LEFT, p.CENTER);
                        const tick_AB = baryToCartesian(1-val, val, 0);
                        p.text(val.toFixed(1).replace(/^0\./, '.'), tick_AB.x + 8, tick_AB.y);
                    }
                }
                 // Add 1.0 label for B
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text("1.", B.x, B.y + 20);
            }
        };

        new p5(sketch, 'visualization-container');
    </script>
</body>
</html>
